#main {
    width: 100%;
    display: table;
    color: #821212!important;
    padding-bottom: 10px;
    min-height: 500px;
}


.headline.contentheadline.linetitle {
    position: relative;
    border: 3px double #ff0000!important;
    border-top-color: rgb(255, 0, 0);
    border-top-style: double;
    border-top-width: 3px;
    border-right-color: rgb(255, 0, 0);
    border-right-style: double;
    border-right-width: 3px;
    border-bottom-color: rgb(138, 192, 36);
    border-bottom-style: solid;
    border-bottom-width: 2px;
    border-left-color: rgb(255, 0, 0);
    border-left-style: double;
    border-left-width: 3px;
    height: auto;
    background-color: rgb(0, 0, 0)!important;
    color: #9F0000!important;
    border-bottom: 2px solid #8ac024;
    margin-bottom: 10px;
    padding-bottom: 0;
}



#mainleft.achievements .achievements>div {
    height: 100px;
    line-height: 100px;
    border: 1px solid #ff0000!important;
    margin-bottom: 5px;
    background: -webkit-linear-gradient(top, rgba(107,35,23,1) 4%, rgba(107,35,23,1) 13%, rgba(107,35,23,1) 20%, rgba(128,23,12,1) 47%, rgba(115,17,4,1) 59%, rgba(80,19,10,1) 79%, rgba(71,19,11,1) 84%, rgba(28,13,12,1) 94%)!important;
}

.tabholder .tab {
    background-color: #000!important;

}

#mainleft.achievements .achievements>div .desc .title {
    color: #000000!important;
