css:
@-moz-document regexp("^http(s)://manhua.dmzj.com/.+/[0-9-]+.shtml(#.*)?") {
body {
  background: #171717 !important;
  padding: 0 !important;
}
img {
  border: 0 !important;
  padding: 0 !important;
}
.font14black {
  color: #FFF !important;
  font-weight: bold !important;
}
/*[[comment]]*/
a {
  transition:all 0.3s ease;
  border: 0 !important;
}
.comic_gd_con .comic_gd_li{
  margin: 0;
}
.comic_gd_con .comic_gd_li li {
  transition:all 0.3s ease;
  border: 0 !important;
}
.comic_gd_con .comic_gd_li li a {
  padding: 9px 5px;
  line-height: 14px;
}
.moreli {
  height: 33px;
}
#floatCode,
.box_text,
.logod,
.r1,
.img-70-70,
.right,
.recom_one,
#dmNav,
span.red1_font12,
a.red1_font12:nth-child(12),
span.icon20,
.hotrmbox,
.pop_bbsadmin_top,
.pop_bbsadmin_foot,
.pop_grade_clew-icon,
.display_left,
.display_right,
a#app_manhua,
#bdshare,
.dmNavclick,
.mainNav_in_l,
div#app_manhua,
.book_open .top,
div[style="height:90px;display:block;width:960px;"]{
  display: none !important;
}
.display_graybg{
  background: none !important;
  margin: 0 auto !important;
  width:auto;
}
.user {
  padding: 0 !important;
  height: 50px !important;
}
.header-box {
  height: 30px !important;
  margin: 0 !important;
  background: #222 !important;
  border: 0;
  width: 100% !important;
  box-shadow: 0px 1px 2px #000;
  position: fixed;
  top: 0px !important;
}
.header-box .user{
  width: auto;
  height: 30px !important;
}
.btns {
  position: absolute;
  top: 0;
  right: 36px;
}
.btns a,.btm_chapter_btn.fl,.btm_chapter_btn.fr{
  background: none;
  color: #9e9e9e;
  width: 80px !important;
  height: 30px !important;
  line-height: 30px;
  margin: 0;
  border-radius: 0;
}
.btns a:hover,.btm_chapter_btn.fl:hover,.btm_chapter_btn.fr:hover{
  background: #127352;
  color: #fff;
  text-decoration: none;
}
#page_select{
  margin: 0;
  height: 30px !important;
  border: 0;
  border-radius: 0px !important;
  color: #999;
  background: #000;
}
.border > div {
  margin: 5px auto !important;
}
.funcdiv {
  position: fixed;
  border: 0 !important;
  top: 0px;
  left: 20px;
  width: auto !important;
  background: none;
}
.funcdiv div{
  height: 30px !important;
}
.funcdiv a:last-of-type,div[style="height:60px;display:block;width:960px;"],div[style="width:960px;height:60px;position: absolute;left: 50%;margin-left: -480px;top: 138px;"],div[style="width:980px;margin:15px auto"] {
  display: none !important;
}
.footer{
  border: 0;
  height: 0px !important;
  line-height: 0px !important;
  background: transparent;
}
.pop_bbsadmin{
  width: 260px;
}
.pop_bbsadmin_mend_back {
  border: 0 !important;
  border-radius: 4px;
  background: #171717;
  height: 105px;
}
.pop_bbsadmin_mend {
  background: 0;
}
.pop_bbsadmin_choice2{
  padding: 5px 0;
}
.pop_bbsadmin_choice2 span{
  color: #127352 !important;
}
.part_collection_bnt {
  border: 0 !important;
  border-radius: 3px;
  background: #023522;
  color: #999;
  margin: 0 8px !important;
  transition: all 0.25s ease;
}
.part_collection_bnt:hover {
  background: #127352;
  color: #fff;
}
.btm_chapter_btn {
  width: 80px;
  height: 30px;
  line-height: 30px;
  border-radius: 2px;
}
#page_select {
  width: 80px;
  height: 30px;
  line-height: 30px;
  border-radius: 2px;
}
.inner_img {
  margin: 0 !important;
}
.mainNav{
  background: #222;
  border: none;
  display: block !important;
  position: fixed;
  top: 0px;
  height: 30px;
  right: 0px;
  z-index: 1;
  width: 20px;
  overflow: hidden;
  transition: all 0.25s ease;
  padding: 0 8px;
}
.mainNav:hover{
  width: 505px;
}
.mainNav_inner{
  width: 100%;
  height: 30px;
}
.mainNav_in_r{
  float: left;
  text-align: left;
}
.mainNav_in_r ul li{
  color: transparent;
  height: 30px;
}
.btmBtnBox{
  position: fixed;
  top: 0px;
  right: 276px;
  width: auto !important;
  height: auto;
  margin: 0;
}
.curr_page{
  height: 30px;
  line-height: 30px;
}
.comic_gd_con{
  padding: 0px 20px;
}
.comic_gd_con p,
.book_open .book_con_li li a.book_title,
.book_open .book_con_li li a.book_num,
.book_open .book_con_li li span.book_clo,
.book_con_bottom{
  color: #999;
}
.comic_gd_fb{
  color: #999;
  width: auto;
  text-align: center;
}
.comic_gd_fb .gd_input{
  color: #999;
  background: #000;
  border: 3px solid #127352;
  height: 22px;
  border-radius: 3px 0 0 3px;
  padding: 0px 4px;
}
.comic_gd_fb a.subtn{
  color: #ccc;
  margin: 0;
  background: #127352;
  height: 28px;
  width: 40px;
  line-height: 28px;
  border-radius: 0 3px 3px 0;
}
.comic_gd_fb a.subtn:hover{
  color: #fff;
  text-decoration: none;
}
a.redhotl, a.redhotl, .display_middle, .red1_font12{
  color: #127352 !important;
}
.display_middle{
  width: 100%;
  margin: 30px 0 0 0;
}
.book_open{
  background: #222;
  position: fixed;
  right: 80px;
  top: 30px;
  box-shadow: 1px 1px 2px #000;
}
.book_open .book_con_li li.li_bg,
.book_open .book_con_li li:hover{
  background: none;
}
.book_open .book_open_tabs{
  border-bottom-color:#444;
}
}

@-moz-document domain("manhua.dmzj.com") {
.online_anim_debate {
  margin-top: 200px !important;
}
}

@-moz-document url-prefix("http://www.dmzj.com/view/"), url-prefix("https://www.dmzj.com/view/") {
body {
  background-color: #222 !important;
}
a, .head_wz, .myPoint {
  color: #FFF !important;
}
a:hover {
  color: #4A97CD !important;
}
.wrap_last_mid, .allN, .comic_gd_fb {
  color: #FFF !important;
}
.mainNav a:hover, .lz_btn:hover, .next_btn:hover {
  color: unset !important;
}
.page_o a {
  color: #000 !important;
  background-color: #FFF !important;
}
.head_title h2, .head_title a {
  color: #FF6600 !important;
}
.foot-detail a {
  color: #666 !important;
}
.dy_success, .manag a {
  color: #333 !important;
}
.qx {
  color: #000 !important;
}
}

@-moz-document url-prefix("http://user.dmzj.com/cache/oviewpoint/"), url-prefix("https://user.dmzj.com/cache/oviewpoint/") {
.comic_gd_con p, .comic_gd_fb {
  color: #FFF !important;
}
}

style_setting_options.value:
div#hd {
  border: 0px !important;
  margin: 0 auto !important;
  padding: 0 !important;
  max-width: 960px;
  width: auto !important;
}
