css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("opensubtitles.org") {

head, body, .ui-autocomplete li a:hover,
.content .msg, #lang_footer, .footer, 
#lang_footer a, #lang_footer a:hover,
#alphabet strong, #alphabet a, .footer a
{
background: #111111 !important;
}
fieldset, td, legend, input, select,
.ui-autocomplete
{
background: #000000 !important;
}
th, .search_field, #network, .bt-dwl
{
background: #3AFF54 !important;
}
.info
{
background: #CE0000 !important;
}
.agile_carousel, .slide_inner
{
background-color: #000000 !important;
}
fieldset, legend, .search_field, 
.bt-dwl, a.bt-dwl
{
border: 1px #3AFF54 solid !important;
}
.msg, #alphabet strong, #alphabet a
{
border: 1px #FFFFFF solid !important;
}
a, abbr, td, .timeago, .msg, input, select, legend, 
.p, .count
{
color: #FFFFFF !important;
text-decoration: none !important;
}
a:hover
{
color: #3AFF54 !important;
text-decoration: underline !important;
}
.bt-dwl
{
color: #000000 !important;
}
.bt-dwl:hover
{
color: #000000 !important;
}
}
