@-moz-document domain("golem.de") {
#recently {
    background: #000 !important;
    text-shadow: 1px 1px 0 #000;
}

div.giv {
    border: none;
}

.list-articles,
.iqadlinetop,
.givc {
    /* li*/
    
    border-top: 1px solid #868686;
}
/* Header */

.dh3,
.dh2,
.dh1,
.head1,
.head2,
.head3,
.head4,
.head5 {
    color: #969696 !important;
    /* grau */
}
/* URL */

a {
    color: #B30404 !important;
    /* red */
}

article,
#comments,
.g,
.g6,
.g-ie6,
.promos,
body,
#footer,
#screen,
#header,
#forum-main,
#thread-detail,
.information {
    background: #000 !important;
    /* black */
    
    color: #969696 !important;
    /* grey-ish */
}

#header {
    border-left: 10px solid #000;
    border-right: 10px solid #000;
}

#superbanner,
#job-market,
#pricehits,
#newsletter,
#followrss {
    display: none;
}
}
