css:
@-moz-document url-prefix("https://community.glowforge.com/")
{
html {
    background-color: white;
}
body input[type="text"],
body input[type="password"],
body input[type="datetime"],
body input[type="datetime-local"],
body input[type="date"],
body input[type="month"],
body input[type="time"],
body input[type="week"],
body input[type="number"],
body input[type="email"],
body input[type="url"],
body input[type="search"],
body input[type="tel"],
body input[type="color"] {
    background-color: white;
}
body{
	color: #333;
    font:14px/19px Helvetica,Arial,sans-serif !important;
}
.d-header {
    background-color: #f9f9f9;
}
.title {
    margin-top: 5px;
}
.user-main .user-stream .item {
    background-color: white;
}
.menu-panel a:hover {
    background-color: #67bfef !important;
    color: #919191 !important;
}
.menu-panel a:hover .fa {
    color: #919191 !important;
}
.user-menu .notifications li:hover,
.select2-results .select2-highlighted {
    background-color: #67bfef;
}
.nav-pills>li.active>a, .nav-pills>li>a.active{
	background-color: white;
}
.nav-pills>li>a.active:hover, .nav-pills>li.active>a:hover{
	background-color: #009eaf !important;
}
.topic-list th{
    padding: 12px 5px !important;
}
.topic-body{
	border-top: 1px solid rgba(0,0,0,.075);
}
.topic-list.categories{
	border-collapse: collapse;
}
.reply-to-tab{
    width: 37px;
    overflow-x:hidden;
    white-space: nowrap;
    animation: width .5s;
    text-align: right;
}
.reply-to-tab:hover{
    width: 150px;
	animation: width .5s;
}
.composer-popup {
    background-color: #DAE3FF;
}
.select2-container,
.select2-drop-active {
    background-color: white;
}
.select2-choice,
.select2-choices {
    background-color: transparent !important;
}
#reply-control,
.grippie {
    background-color: #eee !important;
}
#reply-control input,
#reply-control textarea,
#reply-control .d-editor-preview,
#reply-control .select2-chosen {
    background-color: white !important;
}
#reply-control .d-editor-textarea-wrapper,
#reply-control .d-editor-button-bar {
    border: 1px solid rgba(0, 0, 0, .075) !important;
}
}
