css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#TabsToolbar {background: darkslategrey !important; margin-bottom: 0 !important;}
.arrowscrollbox-scrollbox {padding: 0 !important;}
.tabbrowser-tabs {min-height: 23px !important; margin-left: 0px !important;}
.tabbrowser-tab {-moz-border-left-colors: none !important; -moz-border-right-colors: none !important; border-style: solid !important; border-width: 0px 1px 0px 0px !important; background: darkgrey !important; border-color: black !important;}
.tabs-newtab-button {background: darkgrey !important; margin: 0 !important; width: auto !important;}
.tabbrowser-tab[selected] {background: white !important; background-clip: padding-box !important;}
.tabbrowser-tab:not([pinned]) {min-width: 60px !important;}
.tab-content {padding: 0px 3px 0px 3px !important; font-family: Calibri !important;}
.tab-label {color: black !important;}
.tabs-newtab-button:hover, .tabbrowser-tab:hover:not([selected]) {background-color: lightgrey !important;}
.tab-background {margin: 0 !important; background: transparent !important;}
.tabbrowser-tab:after, .tabbrowser-tab:before {display: none !important;}
.tab-background-start, .tab-background-end {display: none !important;}
.tabs-alltabs-button {display: none !important;}
.tab-background-middle {margin: 0px !important; background: transparent !important;}
#addon-bar, #PersonalToolbar, #nav-bar {box-shadow: none !important; border-color: transparent !important;}
.toolbarbutton-1:not([type=menu-button]), .toolbarbutton-1 > .toolbarbutton-menubutton-button, .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker {padding-top: 0 !important; padding-bottom: 0 !important;}

.tab-icon-image:not([src]) {display: -moz-box !important;}
.tab-throbber[busy] + .tab-icon-image {display: none !important;}
.tab-throbber[progress] {list-style-image: url("chrome://global/skin/icons/loading.png") !important;}
.tab-icon-sound {color: black !important;}
.tab-close-button {list-style-image:url("chrome://global/skin/icons/close.png") !important; -moz-image-region:rect(0,16px,16px,0) !important;}
.tab-close-button:hover {-moz-image-region:rect(0,48px,16px,32px) !important;}
