css:
@-moz-document url-prefix("http://myanimelist.net/profile.php?"), url-prefix("https://myanimelist.net/profile.php?"), regexp(".*myanimelist.net/profile/.*") {
/*
This userscript https://greasyfork.org/en/scripts/13183-myanimelist-mal-move-favorites is required with this Stylish stylesheet! It will break your profile page if you do not download the userscript!
*/
/* More Compact Left Panel */
.page-common .pb12{padding-bottom: 4px !important;}
.page-common .user-favorites-outer.js-truncate-outer .mb8 {margin:1px 0 1px 0;/* text-align: center;*/}
/* Removes Titles & Info */
.user-favorites .favorites-list .list .data, .page-common .content-container .container-right h2[class="mb12"]{
    padding: 0 !important;
    margin: 0 !important;
    display: none !important;
}
/* Fixes Image Sizes and stuff... */
.user-favorites .favorites-list .list .image{
    height: 65px !important;
    margin: 1px 3px 1px 1px !important;
}
.user-favorites .favorites-list .list{
    margin-bottom: 2px !important;
    width: 45px !important;
    display: none;
    float: left;
}
.profile .user-favorites .favorites-list{
    width:225px;
}
}
