css:
@-moz-document url-prefix("https://www.7cups.com/conversation") {
.chatbox {
    font-size: 1.1rem!important;
}
.card-block {
    padding: .5rem;
}
.card {
    font-size: .9rem;
}
div.tab-content {
    /*height:250px;
    width:250px;*/
    
    box-sizing: border-box;
}
.stats .note {
    width: 15em
}
body {
    line-height: 1;
}
.chatBox .card-title {
    padding: .25em 1.2em 0em .8em;
    height: 2em;
}
#actionButtons {
    padding: 0em;
}
.chatBox .headerImageBlock {
    width: 34px;
}
.chatBox .card-title .name {
    font-size: 1em;
    vertical-align: middle;
}
#bottomStatus.heavyLoad {
    background: linear-gradient(to right, #E60000, #E60000 100%);
    background-color: #E60000;
}
#bottomStatus {
    background: linear-gradient(to right, #00FF00, #00FF00 100%);
    background-color: #00FF00;
}
div.hidden-sm-down {
    height: 0px!important;
}
body {
    padding-top: 70px;
}
.navbar {
    font-size: .9rem;
    font-weight: 700;
}
.col-xs-12 {
    line-height: 1!important;
}
.chatBox .youArea {
    margin-bottom: .1em;
}
.chatBox .youWrap {
    font-size: .9em;
}
a.convHolder,
div.convHolder {
    background-color: #FEFF00;
}
.chat-list a.list-group-item.activeConv,
.chat-list a.list-group-item.activeConv:hover {
    background-color: #000000;
}
.chat-list a.list-group-item.newMsg,
.chat-list a.list-group-item.newMsg:hover {
    background-color: #8FCAFF;
}
}

@-moz-document domain("7cups.com") {
.navbar-fixed-top {
    background-color: #029AEF!important;
    background-image: none!important;
}
.alert-warning {
    background-color: #FF0000!important;
}
.alert-caution {
    background-color: #FFFF00!important;
}
.alert-info {
    background-color: #00FF00!important;
}
#bottomStatus.heavyLoad {
    background: linear-gradient(to right, #E60000, #E60000 100%);
    background-color: #E60000;
}
#bottomStatus {
    background: linear-gradient(to right, #00FF00, #00FF00 100%);
    background-color: #00FF00;
}
.badgeColor-bronze>i {
    color: #FBEA05;
}
.badgeColor-gold>i {
    color: #FBB604;
}
.badgeColor-silver>i {
    color: #1D00FF;
}
.navbar-fixed-top {
    background-color: #029AEF;
    background-image: none;
}
.status.userOffline {
    background-color: #CECCCC;
}
.userOffline {
    color: #CECCCC!important;
}
.status.userBusy {
    background-color: #FF8300;
}
.userBusy {
    color: #FF8300!important;
}
.status.userOnline {
    background-color: #0EFF00;
    box-shadow: 0 0 0 .0625em #0EFF00;
}
.userOnline {
    color: #0EFF00!important;
}
.alertCounter {
    background-color: #F53030;
}
.btn-default {
    background-color: #FF0000!important;
    color: #FFFFFF!important;
    border: .0625rem solid #FF0000!important;
}
.btn-warning {
    background-color: #ec971f!important;
    color: #FFFFFF!important;
    border: .0625rem solid #ec971f!important;
}
#listenerGroup .btn-simple {
    background-color: #00FF00!important;
    color: #FFFFFF!important;
    border: .0625rem solid #00FF00!important;
}
}
