css:
@-moz-document domain("owschools.com") {
/*Backgrounds*/
html:not([dir="ltr"]) {
    background-color:#000;
    background:url("https://i.imgur.com/foZt7gK.gif") no-repeat center center fixed;
    background-size: cover;
}

body {
	color:#fff;
    background:transparent;
    margin-bottom:120px;
}

/*Colors*/

h1, h2, h3, h4, h5, h6 {
    color:#28a6e5!important;
}

.page-header {
    color:#fff!important;
}

.standard-icon, .clickable-icon {
    color:#28a6e5;
}

.standard-icon:hover, .clickable-icon:hover {
    color:#fff!important;
}

/*Normals*/

div, b {
	color:#fff;
}

span {
    color:#fff;
}

dd {
    color:#fff;
}

/*Bolds*/

dt {
    color:#28a6e5;
}

/*Italics & Important Terms*/

em, i, p, vocab_definition {
    color:#28a6e5;
}

timeline {
    color:#28a6e5;
    padding-left:2px;
    padding-right:2px;
}

/*Inputs*/

textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"],
input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
    background-color:rgba(0, 0, 0, 0.5)!important;
    color:#fff!important;
    border:none!important;
    box-shadow:none!important;
    -webkit-transition: all .3s ease;
    -moz-transition: all .3s ease;
    -ms-transition: all .3s ease;
    -o-transition: all .3s ease;
    transition: all .3s ease;
}

textarea:focus, input[type="text"]:focus, input[type="password"]:focus, input[type="datetime"]:focus, input[type="datetime-local"]:focus, input[type="date"]:focus, input[type="month"]:focus, input[type="time"]:focus input[type="week"]:focus,
input[type="number"]:focus, input[type="email"]:focus, input[type="url"]:focus, input[type="search"]:focus, input[type="tel"]:focus, input[type="color"]:focus, .uneditable-input:focus {
    box-shadow: 0px 0px 5px 1px #28a6e5!important;
}

/*Dropdown Menu*/

.fuelux .dropdown-menu {
    background: rgba(0, 40, 100, 0.8);
    color:#28a6e5;
    box-shadow: 0px 0px 5px 1px #28a6e5;
}

.fuelux .dropdown-menu > li > a:hover,
.fuelux .dropdown-menu > li > a:focus,
.fuelux .dropdown-submenu:hover > a,
.fuelux .dropdown-submenu:focus > a {
    background-color:rgba(0, 136, 204, 0.35)!important;
    color:#fff!important;
    border-radius:5px 5px 5px 5px;
}

/*Header and User information*/

#nonEulaHeaderContent {
	width:100%;
}

#username {
    float:left;
	display:inline-block;
    left:0px;
}

#username:before {
	display:inline-block;
    content:"Scholar of Learning:";
}

.brand {
    float:left;
    display:inline-block;
}

.brand:before {
	display:inline-block;
    content:"School:";
}

/*Buttons*/

.btn, a.squareButton.bootstrapDefault, a.squareButton, .studentBar .helpButton, .fuelux #msg-send-btn  {
    background-color:rgba(0, 136, 204, 0.35);
    color:#fff;
    background-image: none;
    box-shadow:none;
    border:none;
    text-decoration:none;
    text-shadow:none;
}

.btn:hover, a.squareButton:hover, .studentBar .helpButton:hover, .fuelux #msg-send-btn:hover {
    background-color:rgba(0, 100, 40, 0.5)!important;
    color:#28a6e5;
}

.btn:active, .btn:focus {
    background-color:rgba(0, 136, 204, 0.35);
}

 a.squareButton.bootstrapDefault:hover {
    background-color:rgba(0, 100, 40, 0.5)!important;
    color:#28a6e5;
}

a.squareButton.bootstrapDefault:focus,
a.squareButton.bootstrapDefault:active,
a.squareButton.bootstrapDefault.active,
a.squareButton.bootstrapDefault.disabled,
a.squareButton.bootstrapDefault[disabled] {
    background-image: none;
    color: #fff;
    background-color:rgba(0, 136, 204, 0.35);
}

/*Footer*/

#footer {
    z-index: 1;
    position: fixed;
    bottom: 0px;
    width: 100%;  
}

/*Login Background*/

.login-background {
    background:transparent;
}

/*Login*/

.login-form {
    background: rgba(0, 40, 100, 0.8);
    border-radius:20px 20px 20px 20px;
    box-shadow: 0px 0px 5px 1px #28a6e5;
}

.login-maint {
    height: auto;
    background:  rgba(0, 40, 100, 0.8);
    padding: 10px;
    border-radius:20px 20px 20px 20px;
    box-shadow: 0px 0px 5px 1px #28a6e5;
}

.login-maint-title {
	background:transparent;
}

.login-maint-dates {
    background: rgba(0, 136, 204, 0.35);
    margin-left: -10px;
    margin-right: -10px;
}

.login-maint-body {
	background:transparent;
}

/*Links*/

a:link {
	color:#fff!important;
}

a:visited, a:hover {
    color:#28a6e5!important;
}

/*Tabs*/

.tab-content:not(.main-content) {
    padding:10px;
}

/*Upper Navbar*/

#banner {
    background:  rgba(0, 40, 100, 0.8);
    border-color:#28a6e5;
}

#banner .navbar-inner {
    background:transparent;
}

.bannerContainer {
    background:transparent;
}

/*Upper Navbar Buttons*/

.navbar .nav > li > a {
    background:transparent;
    height:80px!important;
}

.navbar .nav > li > a > i {
    text-shadow:none;
}

.navbar .nav > li > a > div {
    text-shadow:none;
}

.navbar .nav > li > a:hover, .navbar .nav > li > a:focus, .navbar .nav > li.active > a {
    background: rgba(0, 136, 204, 0.35)!important;
    border-radius:0px 0px 0px 0px;
}

#banner .navbar-inner > ul > li {
    background:none!important;
}

/*Upper Navbar Sign Out Button*/

#logout:hover {
    background: rgba(0, 136, 204, 0.35);
}

#logout > i {
    text-shadow:none;
    color:#ffa700;  
}

/*Lower Navbar*/

.lessonJumperTooltip {
    color:#fff!important;
}

div.gl_main_nav_wrapper {
    background:  rgba(0, 40, 100, 0.8);
    border-color:rgba(0, 40, 100, 0.8);
    box-shadow:0px 0px 5px 1px #28a6e5;
}

.gl_main_nav_wrapper  li span, .gl_main_nav_wrapper li a {
    color:#28a6e5!important;
}

 .gl_main_nav_wrapper li.active span, .gl_main_nav_wrapper li.active a, #help-content .gl_main_nav_wrapper li.active a,  #lessonJumperSubTabs > li.active > a {
	  color:#fff!important;
}

.gl_main_nav_wrapper li.active a, .gl_main_nav_wrapper li:hover a   {
    background: rgba(0, 136, 204, 0.35)!important;
}

.gl_main_nav_wrapper li {
    background:transparent!important;
}

/*FAQ and What's New?*/

#whatsNewContent, #faqContent {
    background:  rgba(0, 40, 100, 0.8)!important;
    box-shadow: 0px 0px 5px 1px #28a6e5;
}

/*FAQ Sidebar*/

.nav.helpSideNav {
    padding-left:25px;
}

.helpSideNav > li.active > a {
    background:#28a6e5!important;
    box-shadow:none!important;
}

/*Assignment Page*/

.presentation {
    background:  rgba(0, 40, 100, 0.8);
    box-shadow: 0px 0px 5px 1px #28a6e5;
}

.sectionContainer {
	padding:10px;
}

.presentation table.grid th, .presentation table.grid td, .presentation table.regular th, .presentation table.regular td, .presentation table.regular-left th, .presentation table.regular-left td, .presentation table.grid-left th, .presentation table.grid-left td {
	color:#fff;  
}

h2 img {
   /*content:url("https://i.stack.imgur.com/OZXlX.gif");*/
    content:url("https://3.bp.blogspot.com/-VypHTXQsw8Y/UV1qXckjVxI/AAAAAAAAAWM/GbpDrNbYcgU/s1600/Optical+flare+%252832%2529.png");
    height:50px;
    width:100%;
}

/*Assignment Page Alternating Color Table (for Vocab)*/

.vocabTable {
    background:transparent;
    box-shadow: 0px 0px 5px 1px #28a6e5; 
    border:none;
}

.vocabTable tr:nth-child(odd) {
    background:  rgba(0, 136, 204, 0.35)!important;
}

/*Assignment Page Title Info Window*/

#assignmentDetailTab .popover {
    background:  rgba(0, 40, 100, 0.8);
    box-shadow: 0px 0px 5px 1px #28a6e5; 
}

#assignmentDetailTab .popover p {
	color:#fff;
}

/*Question Page*/

.questions {
    background:  rgba(0, 40, 100, 0.8);
    box-shadow: 0px 0px 5px 1px #28a6e5; 
    padding: 15px;
    padding-top: 1px;
}

.problemBody {
    padding-top:5px!important;
    padding-left:15px!important;
    padding-right:15px!important;
}

.problemBody p {
    padding-left:5px;
    padding-right:5px;
}

.problemBody.alertSuccess {
    background-color:rgba(0, 100, 40, 0.5)!important;
}

.problemBody.alertError {
    background-color:rgba(100, 40, 0, 0.5)!important;
}

.matchingQuestionContainer {
    background:transparent;
    border:none;
}

/*Question Page Writer System*/

html[dir="ltr"] {
    background:transparent;
}

body[contenteditable="true"] {
    background-color:rgba(0, 0, 0, 0.5)!important;
}

.writer {
    background:  rgba(0, 136, 204, 0.35);
}

.writer .writerLogo {
    background:transparent;
}

.cke_wysiwyg_frame, .cke_wysiwyg_div, .cke_inner {
    background:transparent;
}

.ckeditorText {
    border:none;
}

.upload-icon.upload-sprite {
  display:none;  
}

/*Question Page Buttons*/

.studentBar.saveContinue {
	background:transparent;
    padding-top:15px!important;
    padding-bottom:0px!important;
}

.icon-question-sign {
    display:none;
    background-image:none;
}

.studentBar .helpButton {
	padding:10px;
    border-radius:5px 5px 5px 5px;
    margin-top: 7px;
}

/*Question Page "Ask For Help" Window*/

.help .popover {
    background:  rgba(0, 40, 100, 0.8);
    box-shadow: 0px 0px 5px 1px #28a6e5;
    border-radius:0px 0px 0px 0px;
}

.help .popover .popover-title {
    background: rgba(0, 136, 204, 0.35);
    border:none;
    border-radius:0px 0px 0px 0px;
}

/*Assignment/Question Page Toolbar*/

#toolbar ul.nav.nav-tabs {
    background:  rgba(0, 40, 100, 0.8);
    box-shadow: 0px 0px 5px 1px #28a6e5;
    border:none;
}

#toolbar ul.nav.nav-tabs li a {
    background:transparent;
    color:#28a6e5!important;
    border:none;
}

#toolbar ul.nav.nav-tabs li a:hover {
    background: rgba(0, 136, 204, 0.35)!important;
    color:#fff!important;
}

/*Assignment/Question Page Number Navbar*/

.section-bar-bg, .question-bar {
    background:transparent;
    border:none;
    padding-top:0px;
}

.questionBar, .section-bar {
    background-color:  rgba(0, 40, 100, 0.8);
    height:60px;
    overflow:hidden;
    border-radius:0px 0px 20px 20px;
}

.section-bar-bg > div > ul > li > a, .problemNav li {
    background-color:  rgba(0, 40, 100, 0.8)!important;
    background-image:none!important;
    border-radius:0px 0px 20px 20px;
    color:#28a6e5!important;
}

 .problemNav li span {
    color:#28a6e5!important;
}

.section-bar li.active a, .section-bar li:hover a,  .problemNav li.alertCurrent, .problemNav li:hover {
    color:#fff!important;
    background-color:  rgba(0, 136, 204, 0.35)!important;
} 

.problemNav li.alertCurrent span, .problemNav li:hover span {
    color:#fff!important;
}

.problemNavContainer {
    height:60px;
    box-shadow: 0px 0px 5px 1px #28a6e5;
    border:none;
}

/*Assignment/Question Page "Turn It In" Button Window*/

.submitAssignment .popover {
    background:  rgba(0, 40, 100, 0.8);
    box-shadow: 0px 0px 5px 1px #28a6e5;
}

.submitAssignment .popover .popover-title {
    background-color:  rgba(0, 136, 204, 0.35);
    border:none;
    color:#fff;
}

/*Learn Page - Assignment Listing*/


/*Background*/
#plannerTab, #coursesTab {
    background:  rgba(0, 40, 100, 0.8);
    box-shadow: 0px 0px 5px 1px #28a6e5;
}

/*Course Headers*/
#plannerTab .row.course-header, #coursesTab .row.course-header, #plannerTab .row.carousel-title, #coursesTab .row.carousel-title {
    background: rgba(0, 136, 204, 0.35);
    border:none!important;
}

#plannerTab .row.course-header:hover, #coursesTab .row.course-header:hover, #plannerTab .row.carousel-title:hover, #coursesTab .row.carousel-title:hover {
    background: rgba(0, 136, 204, 0.35);
    border:none!important;
}

/*Sorting Headers*/
#plannerTab .row.header:not(.course-header), #coursesTab .row.header:not(.course-header) {
	height:25px;  
}

#plannerTab .row.header:not(.course-header) .plannerHeader, #coursesTab .row.header:not(.course-header) .plannerHeader  {
	padding-top:5px;
}

/*Listing*/
#plannerTab .span1:not(.dueDate), #plannerTab .span2, #plannerTab .span6, #plannerTab .span7, #plannerTab .span12, #coursesTab .span1:not(.dueDate),  #coursesTab .span2, #coursesTab .span6, #coursesTab .span7, #coursesTab .span12  {
  color:#fff!important;
}

#plannerTab .row:not(.header):not(.carousel-title):hover, #coursesTab .row:not(.header):not(.carousel-title):hover {
    background: rgba(0, 136, 204, 0.35);
}

.dueDate {
    margin-top:-7px;
}

/*Listing Progress Bar*/

.progress {
    background-color:rgba(0, 0, 0, 0.5)!important;
    background-image:none;
}

.progress .bar {
    background-color:rgba(0, 100, 40, 1)!important;
    background-image:none;
}

/*Email Navbar*/
#messages-grid > thead {
	background:transparent;  
}

tr.gl_main_nav_wrapper {
	background:transparent;  
    border-color:rgba(0, 40, 100, 0.8);
}

#msg-content {
  	margin-top:-20px;  
}

/*Email Messagebox Listing*/

#messages-grid {
    background:rgba(0, 40, 100, 0.8);
    box-shadow: 0px 0px 5px 1px #28a6e5;
}

.fuelux .table td:first-child, .fuelux .table td:last-child {
	background:transparent;  
}

.fuelux .datagrid tfoot {
    background:transparent;
}

.fuelux .table tr {
	background:transparent;
}

.fuelux .table tr:hover {
    background: rgba(0, 136, 204, 0.35);
}

.fuelux .table tr:hover  td:last-child, .fuelux .table tr:hover  td:first-child, .fuelux .table tr:hover  td {
    background: transparent!important;
}

#pagination-footer > tr:hover {
	background:transparent;  
}

/*Email Message*/

#read-msg {
    margin-top:10px;
    padding:15px;
    background:rgba(0, 40, 100, 0.8);
    box-shadow: 0px 0px 5px 1px #28a6e5;
}

#read-msg .msg-body {
    background: rgba(0, 136, 204, 0.35);
    border:none;
}

.msg-action-btn-group {
	background:transparent;  
}

/*Email Reply*/

#compose-msg {
    width:1024px;
}

#compose-msg .span7{
    float:left;
    background:rgba(0, 40, 100, 0.8);
    box-shadow: 0px 0px 5px 1px #28a6e5;
    padding-left:7px;
    padding-right:7px;
}

/*Email Reply Compose Field*/

#compose-msg #cke_editor1 {
    width:100%!important;
    border: none;
}

.cke_editable {
    background-color:rgba(0, 100, 40, 0.5);
}

/*Email Reply Contact Selector*/

#msg-contact-chooser  {
    float:right;
    background:rgba(0, 40, 100, 0.8);
    box-shadow: 0px 0px 5px 1px #28a6e5;
    padding-left:7px;
    padding-right:7px;
}

div.gridbox_mt {
    border:none;
    box-shadow: 0px 0px 5px 1px #28a6e5;
}

#msg-contact-chooser div {
    background:transparent!important;
}

#msg-contacts-grid .objbox  tr:nth-child(even) {
    background:transparent;
}

#msg-contacts-grid .objbox  tr:nth-child(odd) {
    background: rgba(0, 136, 204, 0.35);
}

#msg-contacts-grid .objbox  tr.rowselected {
    background-color:rgba(0, 100, 40, 0.5);
}

#msg-contacts-grid .objbox  tr td {
	background:transparent!important;
    border:none;
    color:#fff;
}
}
