css:
@-moz-document url-prefix("https://www.tumblr.com"), url-prefix("http://www.tumblr.com") {
/*idk*/

.rapid-recs-container .rapid-recs {
    background-image: linear-gradient(to bottom, rgba(54, 70, 93, 0)0%, #25143d 15%, #25143d 85%, rgba(54, 70, 93, 0)100%);
}
/*---Top bar---*/

.l-header-container--refresh {
    background: #000;
}
/*Compose button*/

.compose-button {
    fill: #8c00ff;
}
/*Notifications*/

.selected .tab_notice {
    background: #8c00ff;
}
.tab-notice--outlined {
    border: 1px solid #000;
}
.tab_notice .tab_notice_value {
    color: #000;
}
/*---Main body---*/

.identity {
    background: url(http://wallpapercave.com/wp/vNwGEbf.jpg)no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
}
/*Center column*/

.l-container.l-container--two-column .l-content,
.l-container.l-container--two-column-dashboard .l-content {
    background: rgba(0, 0, 0, 0.8);
}
/*whatevers within*/

.l-container.l-container--two-column-dashboard .left_column,
.l-container.l-container--two-column .right_column,
.l-container.l-container--two-column-dashboard .right_column,
.identity .right_column:after {
    background: rgba(0, 0, 0, 0);
}
.item,
.sidebar_link.avatar_align {
    display: none;
}
/*Post corner*/

.post_full .post_permalink {
    border-color: #000 #000 transparent transparent;
}
/*Reblog notification*/

.anchor {
    background-color: #000;
    border-color: #000;
}
/*Next page button*/

.chrome.blue {
    border-color: #000;
    background-color: #8c00ff;
}
/*Previous*/

.chrome {
    border-color: #000;
    background: #333;
}
}
