css:
@-moz-document domain("stackoverflow.com"), domain("superuser.com"), domain("serverfault.com"), domain("stackexchange.com"), domain("askubuntu.com") {
body {
    background-color: #D9DEE0;
}
.container {
    background-color: #C4C9CE;
}
#content {
    background-color: #F1F1F1;
    border: none;
}
#questions { margin-left: -9px }

#mainbar,
#mainbar-full,
.mainbar-full,
#logout-page {
    background-color: transparent;
}
.post-tag {
    background: #DEE0E2;
}
pre,
code {
    background-color: #DAE2E0;
}
blockquote pre,
blockquote code {
    background-color: #D5D8CA;
}
.owner {
    background-color: #D0DEE8;
}
.owner a,
a.owner {
    font-weight: bold;
}
.user-info.user-hover .user-gravatar32,
.user-info.user-hover .user-gravatar48 {
    box-shadow: none;
    border: 1px solid gray
}
.started .reputation-score {
    color: rgba(0, 0, 0, 0.5);
    background-color: #EAE8D0;
    padding: 1px 2px;
    border: 1px solid #DAD7B9;
    margin-left: 4px;
}
#footer,
footer {
    background-color: #494F52;
    -webkit-background-blend-mode: multiply;
    -moz-background-blend-mode: multiply;
    background-blend-mode: multiply;
}
#footer .footerwrap,
#footer #footer-menu {
    background: transparent;
    border-top: none;
}
#footer #svnrev {
    margin-bottom: 0
}
footer a,
#footer .footerwrap #footer-menu #footer-sites a,
#footer .footerwrap #footer-menu .top-footer-links a {
    color: #9FC0D8!important;
}
footer a:visited,
#footer .footerwrap #footer-menu #footer-sites a:visited,
#footer .footerwrap #footer-menu .top-footer-links a:visited {
    color: #7697bb!important;
}
#footer .footerwrap #footer-menu #footer-sites th {
    color: #fff
}
#footer .footerwrap #copyright,
#footer .footerwrap #additional-notices,
#footer .footerwrap #svnrev {
    color: #8A8A8A;
}
#footer .footerwrap #copyright a,
#footer .footerwrap #additional-notices a,
#footer .footerwrap #svnrev a {
    color: #8E9AA0;
}
blockquote {
    background-color: #E6E6DE;
    border-left: 2px solid #D8CB91;
}
.supernova,
.mini-counts.supernova {
    color: #B16B36;
}
.answer {
    padding-bottom: 0
}
.question-page #answers-header:after,
.question-page #answers .answer:not(:last-child):after {
    display: block;
    content: " ";
    height: 16px;
    background: linear-gradient(to right, #d9dee0 0, transparent 100%);
    margin: 20px -24px 0;
}
.question-page #answers-header:after {
    margin-top: -15px
}
.post-text img,
.wmd-preview img {
    border: 1px solid #ccc
}
.new-post-activity,
#new-answer-activity {
    background: #E0D9C5;
    margin-left: -15px;
    padding-left: 15px;
}
/**********************************************/

#sidebar .module.community-bulletin {
    background-color: #EFEEE9;
}
div#hireme,
.everyonelovesstackoverflow {
    opacity: 0.2;
    transition: 1s opacity;
}
div#hireme:hover,
.everyonelovesstackoverflow:hover {
    opacity: 1.0;
}
/**********************************************/

.answered {
    background-color: #D6DEC9;
    color: #000;
}
.answered .mini-counts,
.answered strong {
    color: #000;
}
/**********************************************/

.newnav .filtered-tags,
.newnav .welovestackoverflow {
    background-color: rgba(217, 222, 224, 0.45);
}
/**********************************************/

.top-tags .tag-wrapper .tag-container {
    background: #E2E7EA;
}
.top-tags .tag-wrapper .tag-container a {
    background: #D0D9DE;
}
.top-tags .tag-wrapper .col-6 .post-tag {
    max-width: 50%;
}
.badges-content .badges-number .name {
    color: rgba(0, 0, 0, 0.5);
}
.badge1-alternate {
    background: #F5E5B2;
}
.badge1-alternate .badge1 {
    background: #E8C132;
    width: 6px;
    height: 6px;
  	border-radius: 3px;
}
.badge2-alternate,
.badge-how-to {
    background: #E0E4E8;
}
.badge2-alternate .badge2,
.badge-how-to .badge2 {
    background: #BFC6CA;
    width: 6px;
    height: 6px;
  	border-radius: 3px;
}
.badge3-alternate {
    background: #E8DDC8;
}
.badge3-alternate .badge3 {
    background: #CAB17A;
    width: 6px;
    height: 6px;
  	border-radius: 3px;
}
/**********************************************/

.revision td {
    background-color: #D0D7DC;
}
.suggested-edit .body-diffs .full-diff td,
#revisions .markdown-diff .full-diff td {
    background-color: transparent;
    border-color: transparent;
}
.suggested-edit .body-diffs .full-diff .skip,
#revisions .markdown-diff .full-diff .skip {
    background-color: transparent;
    border-color: transparent;
    font-weight: bold;
}
.suggested-edit .body-diffs .full-diff .deleted>div,
#revisions .markdown-diff .full-diff .deleted>div {
    background-color: #ECE0E0;
}
.suggested-edit .body-diffs .full-diff .inserted>div,
#revisions .markdown-diff .full-diff .inserted>div {
    background-color: #E4E8D0;
}
span.diff-add {
    background: #C6D89F;
    color: black;
}
#revisions {
    margin: 20px -15px 0;
}
.revcell1 {
    width: 40px;
}
.revcell5 {
    margin-left: 25px;
}
.owner-revision td {
    background-color: #EAE6D9;
}
.diff-choices {
    margin: -10px 0 10px -25px;
    padding: 10px 10px 10px 30px;
    background-color: rgba(217, 222, 224, 0.5);
}
.diff-choices a,
.diff-choices a.selected {
    border-color: #B7BFC3;
}
.diff-choices a.selected {
    font-weight: bold
}
#revisions .markdown-diff .full-diff {
    width: calc(100% - 5px);
}
.revision-comment {
    font-weight: bold
}
.revision-comment + .post-menu {
    margin-top: -10px;
}
#question-mini-list {
    overflow: visible
}
.question-summary {
    margin-left: -15px;
    padding-left: 15px;
}
.question-summary:nth-child(even) {
    background: linear-gradient(to right, rgba(0, 20, 40, 0.05), rgba(0, 20, 40, 0.02) 75%, transparent);
}
.user-info .user-gravatar32+.user-details {
	width: calc(100% - 42px);
}
}

@-moz-document url-prefix("https://stackoverflow.com/documentation/"), url-prefix("http://stackoverflow.com/documentation/") {
.expanded:hover {
    background-color: unset;
}
.topic-title + .docs-menu:after,
.existing-topic > h2:after,
li.example.expanded.topic-section:after {
    display: block;
    content: " ";
    height: 16px;
    background: linear-gradient(to right, #C4C9CE 0, transparent 100%);
    margin: 20px -15px 0;
}
.container .example.expanded {
    margin-left: -15px !important;
    padding-left: 15px !important;
}
.notifications:after {
    margin-bottom: 24px
}
}

@-moz-document domain("gaming.stackexchange.com") {
body {
    background-color: hsl(221, 47%, 33.7%);
}
.container {
    background-color: transparent
}
#footer {
    background-color: #08162f;
}
}

@-moz-document domain("english.stackexchange.com") {
#footer {
    background-image: none;
}
}

@-moz-document domain("stackoverflow.com"), domain("superuser.com"), domain("serverfault.com") {
#header {
    background-color: #D9DEE0;
    -webkit-background-blend-mode: multiply;
    -moz-background-blend-mode: multiply;
    background-blend-mode: multiply;
}
#hmenus .nav ul li.youarehere a,
#hmenus .nav ul li a:hover {
    background: #CA6312;
}
}

@-moz-document domain("stackoverflow.com"), domain("superuser.com"), domain("serverfault.com") {
.topbar .topbar-links .search-container input[type=text] {
    margin-right: -15px;
}
}

@-moz-document regexp("https?://(\\w+\\.)?stackoverflow\\.com/posts/\\d+/revisions.*"), regexp("https?://(\\w+\\.)?stackoverflow\\.com/review/suggested-edits/\\d+.*") {
#content,
#header {
    width: auto;
    margin-left: 40px;
    margin-right: 40px;
}
.suggested-edit,
.suggested-edit .summary,
.suggested-edit .body-diffs table {
    width: 100%;
}
.suggested-edit .diff-choices {
    background-color: transparent
}
.review-bar-container .review-bar {
    width: auto!important;
}
}

@-moz-document regexp("https?://(\\w+\\.)?stackoverflow\\.com/review/reopen/.*") {
#content,
#header {
    width: auto;
    margin-left: 40px;
    margin-right: 40px;
}
.review-bar-container .review-bar {
    width: auto!important
}
.review-content {
    position: relative;
}
#sidebar,
.sidebar {
    float: none;
    position: absolute;
    right: 2px;
    top: 0;
    background-color: #F1F1F1;
    border: 1px solid #e0e0e0;
}
.sidebar .module {
    margin-bottom: 0;
    padding: 10px;
}
#mainbar,
.mainbar {
    float: none;
    width: 100%;
}
.diffs {
    margin-right: 5px
}
#revisions {
    margin-left: 10px;
    padding-left: 0
}
#revisions .sidebyside-diff > div {
    width: calc(50% - 20px)!important
}
}

@-moz-document regexp("https?://(\\w+\\.)?stackoverflow\\.com/users/\\d+.*") {
.dashboard-item:last-child {
    border-bottom: none
}
.newuser {
    background-color: #E6E5DC;
    padding: 15px;
}
.history-table td {
    vertical-align: top
}
.history-table td br {
    display: none
}
.history-table td b,
.history-table td .comments {
    display: inline-block;
    width: 49%;
    vertical-align: middle;
    line-height: 1.0;
}
.history-table td .comments {
    margin-top: 0;
    padding-bottom: 0
}
}

@-moz-document domain("codereview.stackexchange.com") {
#mainbar {
    padding-left: 15px
}
#sidebar .module:not(.community-bulletin) {
    background: transparent
}
.s-footer .-container {
    background-color: unset
}
.s-footer .-toc th {
    color: #bfc3c5;
}
}

@-moz-document url-prefix("http://stackexchange.com/sites") {
.lv-stats-wrapper,
.lv-info,
.lv-stats-box .number {
    background: none
}
}

@-moz-document domain("stackexchange.com") {
body {
    background-image: none;
    background-color: #bac5d0;
}
footer {
    text-shadow: none;
    color: gray;
}
}

@-moz-document domain("stackoverflow.com") {
html.html__unpinned-leftnav body>.container {
  max-width: none;
}
.container._full #content {
    background-color: #C4C9CE;
}
#content .inner-content {
    background-color: #F1F1F1;
    border: none;
}
.s-footer._short .-copyright {
  color: #818d9a;
}
.s-footer._short .-link._expandable:before {
  opacity: 1;
}
.s-footer._short .-link._expandable:hover:before {
  background-color: rgba(132, 141, 149, 0.82);
}
.s-footer._short .-link._expandable:after,
.s-footer._short .-link._expandable:hover:after {
    border-color: white;
}
}
