css:
@-moz-document domain("se7ensins.com") {
.nodeList .categoryStrip {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 9px 10px;
    margin: 3px auto 0;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #51DA00 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #101010 0%,#8A641E 100%);
    filter: progid-DXImageTransform-Microsoft-gradient( startColorstr='#5a9d32', endColorstr='#527c39',GradientType=0 );
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    -khtml-border-radius: 5px;
    border-radius: 5px;
    min-height: 6px;
}
a.xbCatTrigger {
    color: rgba(255,150,0,1);
    position: absolute;
    right: 10px;
    top: 8px;
    z-index: 1;
}
.nodeList .categoryStrip .nodeTitle a {
    color: rgb(255, 179, 0);
}
.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .XenBase .xengallerySideBarContainer .xengallerySideBar .section h3 {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgba(228,124,24,1);
    background-color: rgb(26, 26, 26);
    padding-top: 7px;
    padding-bottom: 7px;
    padding-left: 10px;
    margin: -3px -3px 5px;
    border-bottom: 3px solid rgb(82, 125, 57);
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -khtml-border-radius: 3px;
    border-radius: 3px;
}
.sidebar .section .primaryContent h3 a, .sidebar .section .secondaryContent h3 a {
    color: rgba(224,124,24,1);
}
.username .style8 {
    color: #FF9B00;
}
.primaryContent a {
    color: rgb(160, 127, 27);
}
img[src*="logo@2x.png"]{
    width:0;
    height:90px;
    padding-right:375px;
    background:transparent url(http://i.imgur.com/N9eFJAI.png) top left no-repeat;
}
.primaryContent a {
    color: rgb(206, 147, 10);
}
.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
    background-image: url('http://i.imgur.com/tWgrQh9.png');
    background-repeat: no-repeat;
    background-position: -36px 0;
}
.navTabs {
    font-size: 13px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    background-color: rgb(26, 26, 26);
    padding: 0 25px;
    border-top: 6px solid rgb(26, 26, 26);
    border-bottom: 6px solid rgba(224,124,24,1);
    height: 40px;
}
a:link, a:visited {
    color: rgb(224, 124, 24);
    text-decoration: none;
}
body {
    -webkit-text-size-adjust: 100%;
    -moz-text-size-adjust: 100%;
    -ms-text-size-adjust: 100%;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(0, 0, 0);
    word-wrap: break-word;
    line-height: 1.28;
}
.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .XenBase .xengallerySideBarContainer .xengallerySideBar .section h3 {
    font-size: 14px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgba(228,124,24,1);
    background-color: rgb(26, 26, 26);
    padding-top: 7px;
    padding-bottom: 7px;
    padding-left: 10px;
    margin: -3px -3px 5px;
    border-bottom: 3px solid rgba(224,124,24,1);
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -khtml-border-radius: 3px;
    border-radius: 3px;
}
html .navTabs .navTab.selected .navLink {
    position: relative;
    font-weight: bold;
    color: rgb(255, 255, 255);
    -webkit-border-top-left-radius: 3px;
    -moz-border-radius-topleft: 3px;
    -khtml-border-top-left-radius: 3px;
    border-top-left-radius: 3px;
    -webkit-border-top-right-radius: 3px;
    -moz-border-radius-topright: 3px;
    -khtml-border-top-right-radius: 3px;
    border-top-right-radius: 3px;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #353329 0%,#F9AC00 100%);
    filter: progid-DXImageTransform-Microsoft-gradient( startColorstr='#5a9d32', endColorstr='#527c39',GradientType=0 );
}
.footer a, .footer a:visited {
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(224, 124, 24);
    padding: 11px 5px;
    display: block;
}
.extraFooter {
    font-size: 12px;
    color: rgb(245, 245, 245);
    background-color: rgb(26, 26, 26);
    padding: 15px;
    border-top: 6px solid rgb(224, 124, 45);
    -webkit-border-top-left-radius: 0px;
    -moz-border-radius-topleft: 0px;
    -khtml-border-top-left-radius: 0px;
    border-top-left-radius: 0px;
    -webkit-border-top-right-radius: 0px;
    -moz-border-radius-topright: 0px;
    -khtml-border-top-right-radius: 0px;
    border-top-right-radius: 0px;
    -webkit-border-bottom-right-radius: 10px;
    -moz-border-radius-bottomright: 10px;
    -khtml-border-bottom-right-radius: 10px;
    border-bottom-right-radius: 10px;
    -webkit-border-bottom-left-radius: 10px;
    -moz-border-radius-bottomleft: 10px;
    -khtml-border-bottom-left-radius: 10px;
    border-bottom-left-radius: 10px;
    display: block;
    overflow: hidden;
}
.footerBlock .button.primary {
    color: #000;
    font-size: 14px;
    padding: 0 6px;
}
.button {
    font-style: normal;
    font-size: 12px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(255, 255, 255);
    padding: 0px 5px;
    border: 1px solid rgb(16, 16, 16);
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    -khtml-border-radius: 5px;
    border-radius: 5px;
    text-align: center;
    outline: none;
    line-height: 26px;
    display: inline-block;
    cursor: pointer;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    -ms-box-sizing: border-box;
    box-sizing: border-box;
    -webkit-box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.06) inset;
    -moz-box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.06) inset;
    -khtml-box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.06) inset;
    box-shadow: 0 -3px 0 rgba(0, 0, 0, 0.06) inset;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #E07C2D 0%,#E07C2D 100%);
    filter: progid-DXImageTransform-Microsoft-gradient( startColorstr='#5a9d32', endColorstr='#527c39',GradientType=0 );
    height: 28px;
}
.extraFooter h3 {
    font-size: 16px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgba(224,124,24,1);
    padding-bottom: 5px;
    margin: 0 auto 7px;
    font-weight: 700;
}
a.callToAction {
    padding: 0px;
    border: 1px solid rgb(0, 0, 0);
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    -khtml-border-radius: 5px;
    border-radius: 5px;
    display: inline-block;
    line-height: 28px;
    -webkit-box-shadow: 1px 1px 5px rgba(0,0,0, 0.15);
    -moz-box-shadow: 1px 1px 5px rgba(0,0,0, 0.15);
    -khtml-box-shadow: 1px 1px 5px rgba(0,0,0, 0.15);
    box-shadow: 1px 1px 5px rgba(0,0,0, 0.15);
    outline: 0 none;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #E07C18 0%,#E07C18 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #E07C18 0%,#E07C18 100%);
    filter: progid-DXImageTransform-Microsoft-gradient( startColorstr='#5a9d32', endColorstr='#527c39',GradientType=0 );
    height: 28px;
}
.XenBase .amsArticleViewItem .main .featured, .XenBase .sidebar .featuredNotice {
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #E07C18 0%,#E07C18 100%);
    filter: progid-DXImageTransform-Microsoft-gradient( startColorstr='#5a9d32', endColorstr='#527c39',GradientType=0 );
    color: #fff;
    border: 1px solid rgb(66,98,14);
    font-size: 12px;
    line-height: 1.4;
}
.messageHeading {
    font-size: 13px;
    color: #FFF;
    font-weight: bold;
    background: #5a9d32;
    background: -moz-linear-gradient(top, #5a9d32 0%, #527c39 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#5a9d32), color-stop(100%,#527c39));
    background: -webkit-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -o-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: -ms-linear-gradient(top, #5a9d32 0%,#527c39 100%);
    background: linear-gradient(to bottom, #000000 0%,#826B17 100%);
    filter: progid-DXImageTransform-Microsoft-gradient( startColorstr='#5a9d32', endColorstr='#527c39',GradientType=0 );
    padding: 9px 10px;
    overflow: hidden;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    -khtml-border-radius: 3px;
    border-radius: 3px;
    margin-bottom: 5px;
}
#logoBlock {
    background: url("http://i.imgur.com/CBmucDd.png") no-repeat scroll 0% 0% rgb(26, 26, 26) !important;
}
.premium {
background: url('http://i.imgur.com/zBObbhU.png') no-repeat;
}

}
