css:
@-moz-document domain("trello.com") {
.list-wrapper {
    width: 300px;
}

html,body,input,select,textarea,button {
	font:"Helvetica Neue",Arial,Helvetica,sans-serif;
}

.list-card a {
	color:#444!important;
}

.quick-card-editor-buttons {
    margin-left: 13px;
}

.list-title .icon-menu {
	right:0!important;
	top:8px!important;
}

.list-gradient-top,.list-gradient-bottom {
	display:none!important;
}

.list-area-wrapper {
	padding-left:10px;
}

.list-area {
	width:3500px!important;
}

.list-header {
	box-shadow:rgba(0,0,0,0.2)!important;
	padding:8px!important;
	background:#ECF0F1!important;
	color:#4d4d4d!important;
	z-index:10!important;
	margin:0!important;
    border-radius:5px;
}

.list-header h2 {
	font-size:15px!important;
	font-weight:600!important;
}

.list-header-name {
    color:#4d4d4d;
}

.list-header-menu-icon {
	top:1px!important;
	right:0!important;
}

.list {
	background:rgba(252,252,252,.9)!important;
	width:300px!important;
	margin:0 5px!important;
	padding:0!important;
}

.list-cards {
	padding:3px !important;   
}

.list.add-list .placeholder {
	display:none;
	padding:10px;
	color:#2980B9!important;
}

.list-card {
	padding:5px 5px 2px!important;
	margin:4px 0!important;
	border-radius:0;
    border-radius:3px;
    background: linear-gradient(#fff, #fff);
}

.list-card:hover {
	background:#fff
}

.quick-card-editor {
    background: rgba(0,0,0,0.2);
}

.quick-card-editor-button-item {
    background: rgba(0,0,0,.8);
}

.board-header-btn {
	color:#fff!important;
	font-weight:400!important;
}

/* Change the Board-Canvas Background to a different color or use url to get image */
.board-canvas {
	background: #2D3E50!important;
	background-size:cover!important;
}

#board {
	padding:15px 0 0!important;
}

#header {
	background:#2d3e50!important;
	padding:4px 0 5px!important;
    height:50px!important;
	box-shadow: 0px 1px 10px rgba(0,0,0,0.3);
}

.header-logo-default {
	margin:10px!important;
}

.header-user {
    margin:10px!important;
}

.board-header {
	height:30px!important;
	background:#2980B9!important;
}

.u-text-underline {
    text-decoration: none;
}

/*add this tag to board header to change padding: padding:3px*/
.header-boards-button {
    margin: 10px;
}

.member-gold-badge {
    display:none;
}

h2 {
	font-size:18px!important;
	line-height:1.2em!important;
	margin:0 0 9px!important;
	font-weight:400!important;
}


.member {
	border-radius:20px!important;
	border:none!important;
}

.member img {
	border-radius:0!important;
	border:none!important;
}


.fancy-scrollbar::-webkit-scrollbar-track-piece {
	background:none!important;
}

.fancy-scrollbar::-webkit-scrollbar-thumb:vertical,.fancy-scrollbar::-webkit-scrollbar-thumb:horizontal {
	background:#555!important;
}

.open-card-composer {
	margin:0!important;
	padding:7px 10px;
    background-color:#ECF0F1;
}

.open-card-composer:hover {
	background:#E5E9EA!important;
	color:#666!important;
	text-decoration:none!important;
	margin:0;
}

.card-detail-window {
    background-color:#edeff0;
}

.card-detail-item-header {
    margin-bottom: 12px
}

.known-service-link {
	transition: background-color .1s ease;
    padding:8px;
   	text-decoration:none!important;
    background-color: #F8F9F9;
}

.known-service-link:hover {
    background-color: #ecf0f1
}

.window-title-text {
    padding-right: 4px
}

.window-wrapper {
	background:#FFF!important;
	border-radius:5px!important;
}

.board-widgets {
	background:rgba(255,255,255,0.98)!important;
}

.sidebar-hide-btn {
	top:5px!important;
}

.atMention {
	background:#A3359F!important;
	border-radius:5px!important;
	color:#fff!important;
	font-size:12px!important;
}

code {
    font: 14px "Helvetica Neue",Helvetica,sans-serif;
	background-color:#F7F7F9!important;
	border:1px solid #E1E1E8!important;
	border-radius:3px!important;
	padding:3px 4px!important;
	color:#D14!important;
}
}
