css:
@-moz-document domain("duckduckgo.com") {  
  /***  Centering *************************************************************/
  div.cw:nth-child(4),
  #header,
  div.zcm:nth-child(4) {
    margin-left: auto !important;
    margin-right: auto !important;
  }

  #header,
  .header__search-wrap {
    width: 100% !important;;
    float: none !important;
  }

  #duckbar {

  }
  
  .results-wrapper {
    padding-right: 0 !important;
  }
  
  .cw, .cw--c {
    max-width: 728px !important;
  }
  
  div.zcm:nth-child(4) {
    width: 570px !important;
  }
  
  [class*="tileview"] {
    padding-left: 0 !important;
  }
  
  #zci-meanings > div:nth-child(1) {
    padding-bottom: 1em !important;
  }
  
  .has-active-zci .header-wrap::before,
  .has-active-zci .header-wrap::after {
    visibility: hidden !important;
  }
  
  #zero_click_wrapper {
    margin-bottom: 1em !important;
  }
  
  #header_wrapper::after {
    height: 2em !important;
  }
  
  /*** Answers, definitions, etc. *********************************************/

  #zci-answer .cw,
  #zci-dictionary_definition .cw,
  #zci-about .cw,
  #zci-thesaurus .cw {
      margin-left: auto !important;
  }
  
  /*** Q&A ********************************************************************/
  
  #zci-qa {
    display: flex !important;
  }  
  
  #zci-qa > div:nth-child(1),
  #zci-qa > div:nth-child(1) > div:nth-child(1) > div:nth-child(1){
    margin-left: auto !important;
    margin-right: auto !important;
  }

  #zci-qa > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) {
    max-width: 640px !important;
    margin-left: auto !important;
    margin-right: auto !important;
  }
  
  .has-tiles .tile:last-child,
  .has-tiles .tile--s:last-child {
    -margin-right: 0 !important;
  }
  
