css:
@-moz-document domain('kanbanflow.com') {
 -webkit-scrollbar-track-piece {
    background-color: #252525 !important;
}
body {
    color: #d4d4d4;
}
h1,
h2 {
    color: silver;
}
textarea,
input[type="text"],
select,
.administrationPopover-button,
.administrationPopover-currentBoardCard,
.administrationPopover-currentBoardTitle,
.administrationPopover-badge,
.taskLabelInput-innerWrapper,
.taskLabelInput-innerWrapper,
.textEditor-preview,
.taskLabelInput-content {
    background: #333;
    color: silver;
}
#board-wrapper {
    background: #333;
}
#administrationPopover {
    background: #595959;
}
#board-table {
    border-color: black;
}
#taskDialog {
    background: #993d00;
}
.administrationPopover-boardTitle {
    color: black;
}
.adminPageContent {
    background: #00264d;
}
.adminBox-header {
    background: #000d1a;
}
.adminBox-menu {
    background: #333;
    color: silver;
}
.adminBox-main,
.adminBox-menuItem--selected {
    background: #595959;
    color: silver;
}
.columnHeader-inner {
    background: #333;
}
.columnHeader {
    color: silver;
}
.column-limitWarning .columnHeader-inner,
.columnHeader--hasOverdueTasks .columnHeader-inner,
.board-overdueIndicator,
.board-taskListCellHeader-inner {
    background: darkred;
    color: #d4d4d4;
}
.task {
    margin: 5px;
    padding: 3px;
}
.task-dueDatePassed {
    color: salmon;
    color: darkred;
}
.context-menu-list {
    background-color: #333;
}
.taskDialog-header {
    background: #4d1f00;
}
/* these are the task colors */

.task.task-white,
.taskColor-white,
.taskBox--white {
    background: darkgrey;
    color: #333;
}
.task.task-green,
.taskColor-green,
.taskBox--green {
    background: #004400;
}
.task.task-blue,
.taskColor-blue,
.taskBox--blue {
    background: #19194d;
}
.task.task-red,
.taskColor-red,
.taskBox--red {
    background: #660000;
}
.task.task-yellow,
.taskColor-yellow,
.taskBox--yellow {
    background: #cccc33;
    color: #333;
}
.task.task-orange,
.taskColor-orange,
.taskBox--orange {
    background: #993d00;
}
.task.task-purple,
.taskColor-purple,
.taskBox--purple {
    background: #660066;
}
.task.task-magenta,
.taskColor-magenta,
.taskBox--magenta {
    background: #9933CC;
}
.task.task-cyan,
.taskColor-cyan,
.taskBox--cyan {
    background: #006652;
}
.task.task-brown,
.taskColor-brown,
.taskBox--brown {
    background: #4d2800;
}}
