div.GFYCPGNBAWB.GFYCPGNBMQ {width: 215px;}
div.GFYCPGNBMQ.GFYCPGNBEQ .GFYCPGNBGR.GFYCPGNBER, div.GFYCPGNBGR {right: 12px;}
div#gwt-debug-Sidebar-notebooksButton {position: absolute; top:108px}
