div#gwt-debug-MetaBarView-root div {border-bottom-width: 0px}
div.GFYCPGNBIVB {padding-top: 14px;}
div#gwt-debug-AccountMenu-avatar {background: url("data:image/png;base64,") no-repeat center center}
div#gwt-debug-sidebar div {border-top-width: 0}
div#gwt-debug-MetaBarView-root {border-bottom: 1px solid #ececec; margin-top: 8px}
div.GFYCPGNBHKB {margin-top: 44px;}
#gwt-debug-stage > div.GFYCPGNBAAC > div.GFYCPGNBL-B.GFYCPGNBN-B {height: initial}
div#gwt-debug-AccountMenuView-root {opacity: 0.6}
div#gwt-debug-sidebar {left: 0px}
div.GFYCPGNBOIB.GFYCPGNBKIB {opacity: 1; visibility: visible}
div#gwt-debug-stage {margin-left: 73px}
div#gwt-debug-NotesView-root {width: 230px; margin-top: 43px; border-top: 1px solid #ececec; left: 0}
#gwt-debug-notesListView {opacity: 1; visibility: visible; display: block}
#gwt-debug-NoteAttributesView-root div {transition: initial}
#gwt-debug-trashHeaderContainer {width: 230px; z-index:9;}

.GFYCPGNBOIB {min-width: 230px}

#gwt-debug-DrawerView-root {width: 231px}
#gwt-debug-stage > div.GFYCPGNBJ5B.GFYCPGNBBAC {opacity: 0; background-color: transparent}
