#gwt-debug-NotebooksDrawer-title {padding: 15px 24px 17px}
#gwt-debug-NotebooksDrawerView-root > div.GFYCPGNBDUB.GFYCPGNBAUB.GFYCPGNBHSB {top: 54px}

div.GFYCPGNBM-B > div:first-of-type {padding:24px 24px 32px;}
.GFYCPGNBB0B {margin: 0}
.GFYCPGNBN0B {margin-left: 24px}
#gwt-debug-NoteView-root.GFYCPGNBKMB.GFYCPGNBOMB.GFYCPGNBPMB {margin-top: 139px}

#gwt-debug-Sidebar-newNoteButton, #gwt-debug-Sidebar-searchButton {top: -66px}
#gwt-debug-NoteAttributes-reminderButton {visibility: visible; margin-left: -255px;}
#gwt-debug-NoteAttributes-shortcutButton,
#gwt-debug-NoteAttributes-infoButton,
#gwt-debug-NoteAttributes-trashButton,
#gwt-debug-NoteAttributes-overflowButton {visibility: visible}
div.focus-editor-SaveAnimation-SaveAnimation-container {top:12px; visibility: visible; position: fixed; right: 20px; top:15px}
div#gwt-debug-NoteAttributesView-root {padding-left:27px; padding-right: 32px; overflow: visible; width: 100%; margin-top: -13px; background: transparent; max-height: 51px; visibility: hidden; position:absolute;}
#gwt-debug-NoteContentView-root > div:first-of-type {margin-top:44px; margin-bottom: 0px}
#gwt-debug-notebooksDrawerSlidingPanel {top: -68px}
#gwt-debug-NoteView-root {margin-left: 230px; min-width: initial; transition: initial}
#gwt-debug-FormattingBar-fontButton {margin-left: -14px; background-color:white; height:19px; margin-bottom:3px;}
iframe {padding-bottom: 148px}

#gwt-debug-allNotes div.qa-noteWidget > div:first-of-type > div {right: 0;}
#gwt-debug-allNotes div.qa-noteWidget > div:first-of-type > div:nth-of-type(5) {right: 12px;}
#gwt-debug-allNotes div.qa-noteWidget div.qa-title {max-width: initial;}

#gwt-debug-notesListView div.focus-NotesView-Subheader {display: none;}
#gwt-debug-notesListView div.focus-NotesView-NotesView {width: 230px}
#gwt-debug-notesListView div.NotesView-ScrollWindow::-webkit-scrollbar {display: none;}
#gwt-debug-notesListView {padding-top: 43px;}
#gwt-debug-notesListView div.focus-NotesView-NotesView {border-top: 1px solid #ECECEC;}
#gwt-debug-notesListView div.focus-NotesView-NotesView > div.focus-NotesView-NotesView {border-top: none;}
#gwt-debug-notesListView div.NotesView-ScrollWindow {padding-bottom: 165px; box-sizing: content-box;}
#gwt-debug-notesListView div.NotesView-ScrollWindow > div {margin-bottom: -54px;}
#gwt-debug-notesListView div.focus-NotesView-Note-snippetContent > div.focus-NotesView-Note-date.qa-date {margin-right: 0;}
#gwt-debug-notesListView div.focus-NotesView-Note-snippetContent > div.focus-NotesView-Note-noteTitle.qa-title {width: 184px;}
#gwt-debug-NotebookSelectMenu-root > div:last-child {display: none;}

#gwt-debug-trashHeaderContainer.GLATSGFCO1B.GLATSGFCH2B {height: 0;}

#gwt-debug-AccountMenuView-root #gwt-debug-AccountMenu-avatar + div {display: none;}

#gwt-debug-Sidebar-notebooksButton-container > div > div > img {box-shadow: inset 0px 0px 0px 1px #2dbe60;}

/* <div style="-webkit-column-width:300px;">  </div> */
hr {border: 0; border-bottom: 1px solid #D9D9D9;}
td hr {margin-left: -10px; margin-right: -10px}
blockquote {font-style: italic;border-left-color: #ECECEC;}
blockquote p::before {content: '"';}
blockquote p::after {content: '"';}
img.en-media {margin-bottom: 0px;}
div + div img {margin-top: 0;}
/*body#tinymce table img.en-media {display: block;}*/
/*span[style="text-decoration: line-through;"] {opacity: 0.7;}*/



@media (max-width: 659px){
    div#gwt-debug-NotesView-root,
    div#gwt-debug-sidebar,
    div#gwt-debug-NoteAttributesView-root,
    div#gwt-debug-NoteTitleView-container,
    div#gwt-debug-notesListView,
    div.GFYCPGNBA2.GFYCPGNBI2.GFYCPGNBK2 div.GFYCPGNBLD.GFYCPGNBA3,
    div.GFYCPGNBF3 ~ div.GFYCPGNBP1.GFYCPGNBB2,
    div.GFYCPGNBL2.GFYCPGNBK2,
    div.GFYCPGNBC3.GFYCPGNBK2,
    div.GFYCPGNBH3.GFYCPGNBK2,
    div.GFYCPGNBI3.GFYCPGNBK2,
    div.GFYCPGNBB3.GFYCPGNBK2,
    div.GFYCPGNBJ1.GFYCPGNBK1,
    div.GFYCPGNBJ1.GFYCPGNBK1 + div.GFYCPGNBP1,
    div.GFYCPGNBD2 + div.GFYCPGNBP1.GFYCPGNBB2,
    div.GFYCPGNBN2.GFYCPGNBK2,
    div.GFYCPGNBOKB,
    div.GFYCPGNBPLB.GFYCPGNBLD,
    div#gwt-debug-NotebookSelectMenu-root,
    div#gwt-debug-MetaBarView-tagIcon,
    div.focus-editor-SaveAnimation-SaveAnimation-container,
    div#gwt-debug-FormattingBar-subscriptButton,
    div#gwt-debug-FormattingBar-superscriptButton,
    div#gwt-debug-FormattingBar-indentButton,
    div#gwt-debug-FormattingBar-outdentButton,
    div#gwt-debug-FormattingBar-linkButton,
    div#gwt-debug-FormattingBar-italicButton,
    div#gwt-debug-FormattingBar-alignButton,
    div#gwt-debug-FormattingBar-underlineButton,
    div.GFYCPGNBC2.GFYCPGNBE2,
    #gwt-debug-MetaBarView-root > div > div > div.GFYCPGNBJHB
    {display: none}

    iframe {padding: 20px 3%; box-sizing: border-box;}
    .GFYCPGNBCKB, .GFYCPGNBDHB {max-width: initial; min-width: initial; padding: 0;}
    .GFYCPGNBGMB {border: 0; min-width: initial;}
    div#gwt-debug-NoteContentView-root > div, .GFYCPGNBBKB, .GFYCPGNBDKB {margin-top: 36px; margin-bottom: 0px;}
    div.GFYCPGNBK2.GFYCPGNBI2 {width: initial;}
    div.GFYCPGNBCHB {padding: 0 3%;}
    div#gwt-debug-NoteView-root {margin-left: 0;}
    div#gwt-debug-NoteContentView-root div {padding-left: 0; padding-right: 0;}
    #gwt-debug-MetaBarView-root > div {height: 32px}
    #gwt-debug-NoteContentView-root > div:first-of-type {margin-top: 36px}
    #gwt-debug-MetaBarView-root {padding-left: 0; padding-right: 0; max-width: initial; min-width: initial;padding: 0 3%;display: block; top: -5px;}
    #gwt-debug-stage {margin-left: 73px; left:-73px; margin-right:-73px;}
    div.GFYCPGNBK2 {height: initial; margin: initial;}
    #gwt-debug-MetaBarView-root > div > div > div {border-left: 0;}
    #gwt-debug-NoteContentEditorView-root {margin-top: -24px}
    #gwt-debug-FormattingBar-fontButton {width: initial;margin-left: 0;}
    #gwt-debug-FormattingBar-root {transition: initial;opacity: 1; visibility:visible;}
    #gwt-debug-MetaBarView-root, #gwt-debug-FormattingBar-fontButton {margin-left: -16px;}
}
