#header #top_logo img
{
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(https://userstyles.org/ui/images/stylish-logo-2-copy-2.svg) no-repeat;
    width: 111px;
    height: 34px;
    background-size: cover;
    padding-left: 111px;
}

#header #search_input
{
    flex-grow: 2;
    height: 32px;
    border-radius: 2px;
    border: 1px solid #ccc;
}
#header #search_input #search_image_div
{
    margin: 0;
    padding: 10px 8px 6px 8px;
    background-color: #aaa;
    border-radius: 2px 0 0 2px;
}
#header #search_input input
{
    color: #333 !important;
}

#header #create_new_style
{
    min-height: 32px;
    border-radius: 2px;
    border: solid 1px #ccc;
}
#header #create_new_style a
{
    color: #888 !important;
}
#header #create_new_style:hover
{
    background-color: #3498db;
}
#header #create_new_style:hover a
{
    color: white !important;
}

#header #fourm_link
{
    margin-left: 16px;
    margin-right: 16px;
}
#header #fourm_link a
{
    color: #888 !important;
}
#header #fourm_link a:hover
{
    color: #444 !important;
}

#header #account
{
    margin-left: 16px;
    margin-right: 30px;
}
#header #account a
{
    color: #888 !important;
}
#header #account a:hover
{
    color: #444 !important;
}
#header #account .loggedIn
{
    margin-left: 0;
}


/*
 * Container
 */

#container
{
    display: block;
}

#nav-view
{
    padding: 16px 0;
    display: block;
}


/*
 * Left panel
 */

#nav-view #navigation
{
    min-width: inherit;
    float: left;
    width: 175px;
    padding: 12px;
    margin-top: -16px;
    background: white;
    border-right: 1px solid #eee;
}

#nav-view #navigation .navigation_link
{
    min-width: inherit;
    padding: 10px 0;
}


/*
 * Content
 */

#nav-view #view
{
    margin: 0 20px;
    margin-left: 220px;
    padding: 12px 26px;
    background-color: white;
    box-shadow: 0 1px 2px rgba(0,0,0,.1);
}

#nav-view #view_inner
{
    width: 100%;
}

#nav-view #view_inner .styles
{
    justify-content: space-around;
}
#nav-view #view_inner .style_card
{
    margin: 10px;
}
#nav-view #view_inner .adLong
{
    display: none;
}


/*
 * Carousel
 */

#nav-view #wrapper-carousel
{
    margin: auto;
}


/*
 * Style page
 */


#nav-view #style_info
{
    margin: 0;
    margin-top: 360px;
}

#nav-view #style_info:before
{
    margin: 0 -27px;
    padding-left: calc(100% + 54px);
    height: 20px;
    background-color: #f5f7fa;
    border-bottom: 1px solid #edeff2;
    content: " ";
}

#nav-view #preview_image_div
{
    position: absolute;
    top: 7px;
    left: 200px;
    margin: 0;
    padding: 0;
    width: calc(100% - 200px);
    background-size: contain !important;
    background-color: #222;
    max-width: inherit;
    height: 400px;
}

