css:
/** 
Theme by BallGaZer
**/


/** cs-manager.com only: **/
@-moz-document domain(cs-manager.com) {

#header {
	margin: 0 auto !important;
}
#container, #footer_container,.post,#content {
	margin: 0 auto !important;
}


body 

{	background: #1A191F url("http://i26.photobucket.com/albums/c112/MC-314o/bgk1-2.jpg")repeat-y fixed center !important;
}
a {
	font-style: normal !important;
}

/** Add remote logo as bg and change colors of header **/
#header {
	background-color: #1A191F !important;
	background-image: url("http://img241.imageshack.us/img241/8092/bgk1copyfh0.jpg") !important;
	background-repeat: no-repeat !important;
	border-right: 0px solid #000000 !important;
	border-bottom: 1px solid #000000 !important;
	border-left: 0px solid #000000 !important;
	border-top: 0px solid #000000 !important;
}


/** Hide original logo: **/
#header img {	visibility: hidden !important;} /** ingame **/
#header a img {	visibility: hidden !important;} /** front page **/


/** box containing it all and a few elements, that shouldnt have any specific colour middle background behind presentation **/
#container, #footer_container,.post,#content {
	color: #000000 !important;
	background: #FFFFFF !important;
	background-repeat: no-repeat !important;
}

/** adding borders to some tables **/ 
table.rank,table.login {
	border: 1px solid #000000 !important; 
}
/** adding background to some tables in the mailarea **/ 
table.rank,table.login,table.mail,table.info {
	background: #B7B7B7 !important;
}
/** All sorts of 'small' elements needing colour **/
td.space,div.space {
	background: #4B4B4B!important;

}
a.player,td.player,td.top,td.tactic,a.tactic,a.lineup {
	background: #D2BEBE !important;
	color: #000000 !important;
}
a.tactic:hover,a.lineup:hover {
	background: #AF8D8D !important;
	color: #000000 !important;
}
td.position {
	background: #D1d1d1 !important;
	color: #000000 !important;
}

/** Adding borders and bg-colour to headers **/
td.head, h3 , h2  {
	color: #636363 !important;
	background-image: url("http://img265.imageshack.us/img265/7826/grad3brs7.gif") !important;
	border-top: 0px solid #000000 !important;
	border-left: 0px solid #000000 !important;
	border-right: 0px solid #000000 !important;
	border-bottom: 0px solid #000000 !important;
}

/** All sorts of links. (tabs, menu etc backgroundcolor) **/
li a {
	background: #636363 !important;
        background-image: url("http://img149.imageshack.us/img149/8546/menubackgroundky4.gif") !important;
	color: #000000 !important;
	font-style: normal !important;
	border-right: 1px solid #000000 !important;
	border-left: 0px solid #000000 !important;
	border-bottom: 1px solid #3F3E3E !important;

}


/** menu specific borders **/
ul#menu li a {
	border-bottom: none;
	border-right: none;
	border-left: none;
	border-top: none;
}

/** tab-specific borders **/
ul#tab li a {
	border: 1px solid #000000 !important;
}

li a:hover {
	background: #6B7F7F !important;
        background-image: url("http://img120.imageshack.us/img120/5647/menuoverlayne6.gif") !important;
	color: #000000 !important;
	font-style: normal !important;
}

/** Selected tabs **/
ul#tab li span {
	background: #A20000 !important;
	color: #A4A4A4 !important;
	font-style: normal !important;
	border: 1px solid #000000 !important;
}

/** other spans (eg online in freinds list) **/
li span {
	color: #482828 !important;
}

/** color of areas surrounding maillist **/
table.mail tr.head td, table.mail td.select, table.mail td.delete {
	background-color: #636363 !important;
	color: #000000 !important;
}

/** dark area below the left and right menus **/
#menu, #footer_left, #footer_right, div#container>div#info {
	background: #4B4B4B !important;

}

/** search area **/
form#search {
	border-bottom: none;

	background: #4B4B4B !important;
}

/**input fields and buttons **/
input,select,textarea,radio {
	background: #6F6F6F !important;
	border: 1px solid #000000 !important;
	color: #000000 !important;
}
.buttonsmall, .button {
	background: #CFD9CE !important;
	color: #000000 !important;
	border-top: 1px solid #000000 !important;
	border-left: 1px solid #000000 !important;
	border-right: 1px solid #000000 !important;
	border-bottom: 1px solid #000000 !important;
}

/** news items (community news) **/
td.value {
	background: #B7B7B7! important;
	color: #000000 !important;
}
td.foot,td.info {
	background: #B7B7B7 !important;
	color: #000000 !important;

}

/** Training room colours **/
#training_map,#training_player,#skill,#trainer,#training_players {
	background: #B7B7B7 !important;
}
div#training_type {
	background: #B7B7B7 !important;
}

/** FORUM **/
tr.even {
	background: #F4F4F4 !important;
}
tr.odd {
	background: #ffffff !important;
}
div.modify {
	background: #ffffff !important;
}


/** Status messages **/
#ok_msg {
	color: #59F559 !important;
	background-color: #005D00 !important;
	border-top: 2px solid #008B00 !important;
	border-left: 2px solid #008B00 !important;
	border-right: 2px solid #008B00 !important;
	border-bottom: 2px solid #008B00 !important;
}
#error_msg {
	color: #F55959 !important;
	background-color: #5D0000 !important;
	border-top: 2px solid #8B0000 !important;
	border-left: 2px solid #8B0000 !important;
	border-right: 2px solid #8B0000 !important;
	border-bottom: 2px solid #8B0000!important;
}

/** HELP TAB **/
#help_tab {
	background: #976666 !important;
	color: #FFFFFF !important;
	font-weight: bolder !important;
}

/** LanCamp Training **/
tr.lan td.info {
	background: #ADBDDE !important;
}
tr.lan td.value {
	background: #E1E7F2 !important;
}

/** League table **/
table.table tr.promotion td, table.table tr.promotion_line td {
	color: #004E00 !important;
}
table.table tr.qualup td, table.table tr.qualup_line td {
	color: #000000 !important;
	color: #008000 !important;
}
table.table tr.qualdown td, table.table tr.qualdown_line td {
	color: #000000 !important;
	color: #BC0000 !important;
}
table.table tr.relegation td, table.table tr.relegation_line td {
	color: #800000 !important;
}

/** PCW Calendar **/
table.calender td.week {
	background: #B9B9B9 !important;
	color: #000000 !important;
}
a.challenge {
	background: #E8E8E8 !important;
}
a.challenge:hover {
	background: #464646 !important;
}
div.challenge {
	background: #D3D3D3 !important;
}
div.disabled {
	background-color: #C8C8C8 !important;
}
}
