css:
@-moz-document domain("play.pokemonshowdown.com") {
body {
    background: #22252A !important;
}
/*.activitymenu,*/
.rightmenu,
.mainmenufooter,
.logo, .news-embed {
    display: none !important;
}
.header {
    position: relative;
    height: 50px;
    background: none;
    background: #22252A;
}
.maintabbarbottom {
    background: #2E3135;
    border: solid 1px #25282C;
}
.userbar .username {
    color: hsl(0, 100%, 100%) !important;
    text-shadow: none;
}
.fa-user {
    color: #22252A !important;
}
.icon {
    background-color: #22252A !important;
    border: none;
}
.select,
.team {
    width: 230px;
    height: 32px;
    padding: 0 5px;
    font-size: 9pt;
    text-align: left;
    font-family: Verdana, Helvetica, Arial, sans-serif;
    border-radius: 4px;
    border: 1px solid #888888;
    box-shadow: none;
    background: #FFF;
    color: black;
    box-sizing: border-box;
}
.ps-popup {
    position: absolute;
    top: auto;
    right: auto;
    left: auto;
    bottom: auto;
    text-align: left;
    background: #2E3135;
    color: white;
    border: 1px solid #2E3135;
    padding: 2px 10px;
    border-radius: 6px;
    box-shadow: inset 1px 1px 0 #2E3135, inset -1px -1px 0 #2E3135, 2px 2px 3px rgba(0, 0, 0, .2);
    z-index: 20;
}
.ps-popup button {
    border: none;
    background: #525252;
    color: #FFF;
    border-radius: 4px;
}
.optlabel select {
    border: none;
    border-radius: 4px;
}
.optlabel input {
    border: none;
}
hr {
    display: none;
}
.select:hover,
.team.team-hover {
    border-color: #FFFFFF;
    background: #FFFFFF;
    background: linear-gradient(to bottom, #FFFFFF, #FFFFFF);
}
.ps-room.ps-room-opaque {
    background: #22252A;
    color: #FFF;
}
.inner {
    background-color: #22252A;
    color: #FFF;
}
.battle-log h2 {
    margin: 0.5em -8px;
    padding: 4px 8px;
    border: #25282C;
    background: #2E3135;
}
.ps-room .battle-controls {
    position: absolute;
    top: 370px;
    left: 0;
    width: 640px;
    background: #22252A;
}
.battle-log {
    position: absolute;
    text-align: left;
    border: 1px solid #22252A;
    background: #22252A;
    color: #22252A;
    top: 9px;
    left: 640px;
    right: -1px;
    height: 360px;
    font-family: Verdana, sans-serif;
    font-size: 10pt;
    overflow: auto;
    overflow-y: scroll;
}
.battle-log-add {
    position: absolute;
    text-align: left;
    border: 1px solid #22252A;
    background: #22252A;
    color: black;
    top: 369px;
    left: 640px;
    right: -1px;
    font-family: Verdana, sans-serif;
    font-size: 10pt;
}
.textbox,
.textbox:hover,
.textbox:focus {
    border: 1px solid #22252A;
    border-radius: 3px;
    padding: 2px 3px;
    font-family: Verdana, Helvetica, Arial, sans-serif;
    font-size: 9pt;
    box-shadow: inset 0px 1px 2px #22252A, 1px 1px 0 #22252A;
    background: #000000;
    color: white;
}
.chatbox label {
    color: hsl(0, 100%, 100%);
}
::-webkit-scrollbar {
    width: 10px !important;
    height: 12px !important;
    background-color: #22252A !important;
}
::-webkit-scrollbar-thumb {
    background-color: #4A4A4A !important;
    border-top-left-radius: 0px !important;
    border-top-right-radius: 0px !important;
    border-bottom-right-radius: 0px !important;
    border-bottom-left-radius: 0px !important;
}
.controls button {
    border: none;
    background-color: #FFF;
    border-radius: 4px !important;
    text-align: center;
}
.message-throttle-notice,
.message-error {
    color: #00A72E;
}
.battle-log .chat strong {
    color: #00A72D;
}
.battle-log .chat em {
    padding: 1px 4px 1px 3px;
    color: #FFFFFF;
    font-style: normal;
}
.tabbar li:last-child a.button {
    border-top-right-radius: 3px;
}
.tabbar li:first-child a.button {
    border-top-left-radius: 3px;
    margin-left: 6px;
}
.button {
    outline: none;
    cursor: pointer;
    text-align: center;
    text-decoration: none;
    text-shadow: 0 1px 0 rgba(255, 255, 255, .4);
    border-radius: 5px;
    -webkit-box-shadow: 0 1px 2px rgba(0, 0, 0, .2), inset 0 -1px 2px rgba(255, 255, 255, 1);
    -moz-box-shadow: 0 1px 2px rgba(0, 0, 0, .2), inset 0 -1px 2px rgba(255, 255, 255, 1);
    box-shadow: 0 1px 2px rgba(0, 0, 0, .2), inset 0 -1px 2px rgba(255, 255, 255, 1);
    border-radius: 5px;
    font-family: Verdana, Helvetica, Arial, sans-serif;
    color: #222222;
    text-shadow: 0 1px 0 white;
    border: solid 1px #22252A;
    background: #2E3135;
    background: linear-gradient(to bottom, #2E3135, #2E3135);
}
.button.cur,
.button.cur:hover {
    color: #000000;
    background: #2E3135;
    box-shadow: none;
    border-color: #2E3135;
    cursor: default;
}
.effect-volume em,
.music-volume em {
    display: block;
    margin-top: -6px;
    font-size: 9pt;
    padding-left: 5px;
    color: #FFFFFF;
}
.jslider .jslider-value {
    font-size: 9px;
    white-space: nowrap;
    padding: 1px 2px 0;
    position: absolute;
    top: -19px;
    left: 20%;
    background: #2E3135;
    line-height: 12px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    -o-border-radius: 2px;
    border-radius: 2px;
}
.jslider .jslider-label {
    font-size: 9px;
    line-height: 12px;
    color: white;
    opacity: 1;
}
.button:hover,
.button:focus {
    background: #2E3135;
    background: linear-gradient(to bottom, #2E3135, #2E3135);
    border-color: #22252A;
}
.button {
    text-shadow: 0 1px 0 #2E3135;
}
.button.cur,
.button.cur:hover {
    color: #FFFFFF;
}
.icon {
    background-color: #22252A !important;
    border: none;
    color: #FFF;
}
.fa-plus,
.fa-home {
    color: #FFF;
}
.mainmenu button {
    border: none !important;
    box-shadow: none !important;
    text-shadow: none !important;
}
.battle-options {
    color: #FFF;
}
.chatbox label {
    color: hsl(0, 100%, 100%) !important;
}
.battle-controls .whatdo small {
    float: right;
    padding: 1px 2px;
    border: 1px solid #FFFFFF;
    border-radius: 4px;
}
.battle-controls .whatdo {
    color: #FFFFFF;
}
.movemenu {
    border-color: #000;
}
.controls button {
    border: none;
    background-color: #FFFFFF !important;
}
.controls {
    text-align: center;
}
.battle-controls p button {
    border: none;
    background-color: #FFF;
    border-radius: 4px;
}
.battle-controls {
    text-align: center;
}
.switchcontrols .switchselect button,
.moveselect button {
    background-color: #22252A !important;
}
.ps-room.ps-room-light {
    background: #22252A;
    color: #fff;
}
.teamlist {
    list-style: none;
    margin: 0;
    padding: 0;
    text-align: center;
}
.pad {
    margin: 0;
    padding: 5px 15px;
    text-align: center;
}
button {
    border: none;
    border-radius: 4px;
    background-color: #FFF;
}
.teamchart {
    text-align: center
}
.button {
    outline: none;
    cursor: pointer;
    text-align: center;
    text-decoration: none;
    text-shadow: 0 1px 0 rgba(255, 255, 255, .4);
    border-radius: 5px;
    -webkit-box-shadow: 0 1px 2px rgba(0, 0, 0, .2), inset 0 -1px 2px rgba(255, 255, 255, 1);
    -moz-box-shadow: 0 1px 2px rgba(0, 0, 0, .2), inset 0 -1px 2px rgba(255, 255, 255, 1);
    box-shadow: none;
    border-radius: 5px;
    font-family: Verdana, Helvetica, Arial, sans-serif;
    color: #FFFFFF;
    text-shadow: none;
    border: solid 1px #22252A;
    background: #2E3135;
    background: linear-gradient(to bottom, #2E3135, #2E3135);
}
.roomlist a.ilink {
    display: block;
    margin: 2px 7px 4px 7px;
    padding: 1px 4px 2px 4px;
    border: 1px solid #2E3135;
    background: #545454;
    border-radius: 4px;
    text-decoration: none;
    color: #FFFFFF;
    text-shadow: rgba(255, 255, 255, 0) 0px -1px 0;
    box-shadow: 1px 1px 1px #2E3135;
    cursor: pointer;
    font-size: 10pt;
    overflow: hidden;
    white-space: nowrap;
}
.roomlist a.ilink:hover {
    border: 1px solid #2E3135;
    background: #908F8F;
    color: #FFFFFF;
    text-decoration: none;
}
.ps-room {
    position: absolute;
    display: block;
    top: 56px;
    left: 0;
    right: 0;
    bottom: 0;
    border-left: 1px solid #22252A;
    margin-left: -1px;
}
.challenge {
    margin-top: -1px;
    background: #545454;
    border: none;
    color: #FFFFFF;
    padding: 1px 5px;
}
.pm-log {
    min-height: 100px;
    max-height: 300px;
    background: #545454;
    color: black;
    word-wrap: break-word;
    border: none;
    border-top: 0;
    border-bottom: 0;
    overflow: auto;
}
.inner {
    background-color: #202328;
    color: #FFF;
}
.pm-log-add {
    border: none;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
    background: #545454;
}
.pm-window h3 {
    background: #545454;
    color: #FFFFFF;
    margin: 0;
    padding: 2px 2px 2px 5px;
    font-size: 11pt;
    border: none;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    cursor: pointer;
}
.textbox, .textbox:hover, .textbox:focus {
    border: none;
    border-radius: 3px;
    padding: 2px 3px;
    font-family: Verdana, Helvetica, Arial, sans-serif;
    font-size: 9pt;
    box-shadow: none;
    background: #000000;
    color: white;
}
}
