css:
@-moz-document regexp('https://(kickass|kat|thekat).*') {
  body {
    color: grey !important;
    background: /*[[bgcolor]]*/ !important;
  }
  a {
    color: grey;
  }
  a:hover, .lightgrey a:hover, h1 a:hover, h1 a:hover span,
  h2 a:hover, h2 a:hover span, h3 a:hover, h3 a:hover span,
  h4 a:hover, h4 a:hover span, h5 a:hover, h5 a:hover span {
    color: /*[[headcolor]]*/;
    text-decoration: underline;
  }
  .tagcloud a:hover {
    border-color: /*[[titlecolor]]*/;
  }
  .tagcloud a:hover, .showmore {
    color: /*[[titlecolor]]*/ !important;
    text-decoration: none !important;
  }
  .tabsSeparator {
    background: /*[[menucolor]]*/ !important;
  }
  .lightgrey, .lightgrey a {
    color: grey !important;
  }
  .lightgrey a:hover {
    color: /*[[headcolor]]*/ !important;
    text-decoration: underline;
  }
  h1, h2, h3, h4, h5, h1 a, h2 a, h3 a, h4 a, h5 a, .tagcloud a {
    color: /*[[titlecolor]]*/ !important;
  }
  header, .bodySearch, #navigation li .dropdown {
    background: /*[[headcolor]]*/ /*rgba(87,76,45,0.9)*/ !important;
  }
  .torrentFileList tr.last .torTree {
    background-color: inherit !important;
  }
  #showHideSearch {
    text-shadow:0px 1px 2px rgba(0, 0, 0, 0.6) !important;
  }
  #navigation li a, #showHideSearch {
    color: /*[[hovcolor]]*/ !important;
  }
  #navigation li .dropdown li a, .data tr.firstr:hover,
  .kaGiantButton.siteButton .ka:before {
    background: none !important;
  }
  #navigation li .dropdown li a:hover, #navigation li a:hover,
  #navigation li a:hover, #navigation > li:hover a {
    background: /*[[menucolor]]*/ !important;
  }
  .darkButton, .circleButton, .siteButton, .icon16,
  .ka16, .kaButton, .kaGiantButton, .kaButton.smallButton,
  #navigation li .dropdown li .ka16 {
    color: /*[[bgcolor]]*/ !important;
    background: linear-gradient(to bottom, /*[[butcolor]]*/ 0%,
    /*[[titlecolor]]*/ 100%) !important;
    text-shadow: 1px 1px 0 /*[[headcolor]]*/;
  }
  .darkButton:hover, /*.sliderbox a:hover,*/
  .circleButton:hover, .siteButton:hover, .icon16:hover,
  .ka16:hover, .kaButton:hover, .kaGiantButton:hover,
  .lightivorybg, .lightivorybg:hover, .pages a.active
  /*#navigation li .dropdown li:hover .ka16*/ {
    background: /*[[titlecolor]]*/ !important;
    text-shadow: 1px 1px 0 /*[[headcolor]]*/;
  }
  #fancybox-outer, .commentbody, .tagcloud,
  .folder, .folderopen, .menuValue,
  .selectedTab, .selectedTab:hover,
  .valueBlock, valueBlock:hover,
  #contentSearch, .selectedTab {
    background: /*[[bgcolor]]*/ !important;
  }
  .selectedTab, .selectedTab:hover {
    color: /*[[headcolor]]*/ !important;
  }
  .downloadButtonGroup .kaGiantButton .ka:before {
    color: /*[[bgcolor]]*/ !important;
  }
  table.data tr.odd{
    background: /*[[oddcolor]]*/ !important;
  }
  table.data tr.odd:hover, table.data tr:hover, .showmore {
    background: /*[[hovcolor]]*/ !important;
  }
  .prevAV {
    background: /*[[titlecolor]]*/ !important;
    border: /*[[titlecolor]]*/ !important;
  }
  .quote, .blockquote, .accentbox, .profileCloud {
    color: /*[[headcolor]]*/ !important;
    background: /*[[hovcolor]]*/ !important;
    border: /*[[titlecolor]]*/ !important;
  }
  .userImage {
    border: 3px solid /*[[hovcolor]]*/ !important;
  }
  .valueBubble {
    border: 2px solid /*[[hovcolor]]*/ !important;
  }
  #navigation li a:hover .userImage,
  #navigation li a:hover .valueBubble {
    border-color: /*[[hovcolor]]*/ !important;
  }
  .tabNavigation .menuValue, #navstyle .menuValue {
    /*border: 1px solid /*[[menucolor]]*/ !important;*/
    border: none !important;
  }
  ul.tabNavigation, ul.tabNavigation li a {
    border-color: /*[[menucolor]]*/ !important;
  }
}
