/* Body Coloring */

body {
    background-color: #2c3e50;
    color: #ffffff;
    font-family: 'Roboto', sans-serif;
    font-weight: 300;
}
.sg-logon {
    background-color: #7b7b7b;
    font-family: 'Roboto', sans-serif;
    font-weight: 300;
}
.sg-header {
    background-color: #2980b9;
    background-image: url(http://ashishbach.com/GetEmbeddedImage2.png);
    color: #fff;
    border-top-right-radius: 10px;
}
.sg-hac-menu {
    background-image: url("https://johnnybui.com/null") !important;
    color: #fff;
}
a:link {
    color: #FFFFFF;
    opacity: 70;
}
a:visited {
    color: #FFFFFF;
    opacity: 55;
}
.sg-banner-left {
    min-height: 0px;
    min-width: 120px;
    max-height: 50px;
}
.sg-hac-menu-option-selected {
    background-color: #7b7b7b;
}
.sg-hac-menu-option:hover {
    background-color: #FBC02D;
}
.sg-asp-table-header-selected {
    background-color: #005A95;
}
.sg-asp-table-header-row td {
    background-image: url("https://johnnybui.com/null") !important;
}
.sg-asp-table-header-row a:link {
    color: #fff;
}
.sg-asp-table-header-row a:visited {
    color: #fff;
}
.sg-hac-submenu-option-selected {
    background-color: #5a5a5a;
    color: #fff;
}
.sg-main-header {
    background-color: #5a5a5a;
}
.sg-menu-element-logoff a:link {
    color: #70B8FF;
}
.sg-menu-element-logoff a:visited {
    color: #70B8FF;
}
.sg-header-square {
    border-radius: 5px;
}
.sg-content-grid {
    border: 0;
}
.sg-header-heading {
    color: #fff;
}
.sg-content-alert-container {
    background-color: #2c3e50;
}
.sg-header-group {
    background-image: url(http://ashishbach.com/GetEmbeddedImage2.png);
    color: #fff;
}
.sg-content-grid-container {
    background-color: #7b7b7b;
}
button.ui-button {
    background-image: url(http://ashishbach.com/HACButtonImage4.png) !important;
}
.sg-button.sg-button-show-hide {
    color: #ffffff !important;
}
.ui-state-default {
    color: #fff;
}
.sg-hac-submenu {
    background-image: url("https://johnnybui.com/null") !important;
    background-repeat: repeat-x;
    display: inline-block;
    width: 100%;
    height: 35px;
    font-size: 1.25em;
    overflow: hidden;
}
.sg-main-footer {
    display: none;
}
.sg-banner-menu-left {
    background: url(http://ashishbach.com/hac-banner-nav-left.png) !important;
}
.sg-banner-menu {
    background: url(http://ashishbach.com/hac-banner-nav-fill.png) !important;
    color: #ffffff !important;
}
.sg-banner-menu-right {
    background: url(http://ashishbach.com/hac-banner-nav-right.png) !important;
}
.sg-banner-menu-element span {
    color: #ffffff !important;
}
.ui-buttonset .ui-button {
    margin-left: 5px !important;
}
