    background-color: #2C3E50;
}
tr:nth-child(even) {
    background: #455A64
}
.ui-accordion .ui-accordion-icons {
    padding-left: 2.2em;
    color: black;
}
.sg-asp-calendar-header {
    /* border: none; */
    /* border-bottom: medium solid #006699; */
    
    font-weight: 700;
    color: #FFFFFF;
    background-color: #2C3E50;
}
a[style="color:Black"] {
    color: white !important;
}
.sg-right {
    float: right !important;
    padding-right: 5px;
    font-family: 'Roboto', sans-serif;
    font-weight: 500;
}
