css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('https://tweetdeck.twitter.com/') { 
section.js-column.column.column-type-interactions.will-animate, 
section.js-column.column.column-type-activity.will-animate, 
section.js-column.column.column-type-mention.will-animate, 
section.js-column.column.column-type-message.will-animate{
    width: 240px;
    font-size: 10px;
    line-height: 1;
}
section.js-column.column.column-type-interactions.will-animate .tweet-body, 
section.js-column.column.column-type-activity.will-animate .tweet-body, 
section.js-column.column.column-type-mention.will-animate .tweet-body, 
section.js-column.column.column-type-message.will-animate .tweet-body{
    font-size: 11px;
}
section.js-column.column.column-type-home.will-animate {
    width: 320px;
}
button.js-user-actions-menu.btn.btn-alt.btn-square {
    width: 10px !important;
    height: 10px !important;
    padding: 0 !important;
    background: none !important;
    border: none !important;
}

i.icon.icon-user-dd {
    font-size: 10px !important;
    height: 10px;
    width: 10px;
    line-height: 1 !important;
}
article.stream-item {
    border: none;
}
article .item-box {
    padding: 5px !important;
}

section.js-app-add-panel.js-trends-column.column.trends-column.is-shrunk {
    width: 265px !important;
    height: 100% !important;
    background: black;
}

section.js-app-add-panel.js-trends-column.column.trends-column.is-shrunk .column-content{
overflow-y: auto;
overflow-x: hidden;
}

}
