css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("iichan.hk") {

html,body {
	background: #1d1f21 none;
	color:#c5c8c6;
	font-family:Segoe UI,arial,sans-serif;
	font-size:10pt;
}
div[de-oppost], .reply {
	background:#282a2e;
	overflow: auto;
    display: block;
    width: 1024px;
	margin-left: auto;
	margin-right: auto;
    word-wrap: break-word;
    padding: inherit;
    border-radius: 5px;
    color: #c5c8c6;
}
a {
	color:#81a2be;
}
a:hover {
	color:#5F89AC;
}
.logo {
    color: #B294BB;
}
.doubledash {
	display:none;
}
.thumbnailmsg {
    font-size: 0.7em;
    color: #B294BB;
}
.filetitle {
    font-size: 1em;
    color:  #B294BB;
}
.reply.hiclass {
	background:#282a2e;
    border: 1px solid #111111 !important;
}
hr {
    border-color: #1d1f21;
}
div[de-thread], div[id*="thread-"] {
    width: 1024px;
	margin-left: auto;
	margin-right: auto;
    margin-bottom: 20px;
}
.de-pview{
    border: 1px solid #111111 !important;
    width: auto !important;
}
.postblock {
    background: #282a2e;
    color: #c5c8c6;
}
.list div a {
    color: #c5c8c6;
}
.category {
	background: #282a2e;
}
.list {
    border-left: 1px solid  #c5c8c6;
    border-right: 1px solid  #c5c8c6;
    border-bottom: 1px solid  #c5c8c6;
}
.header {
    background: #c5c8c6;
    color: #800000;
}
.adminbar {
    text-align: center;
    float: none;
}
.reply blockquote, blockquote :last-child {
    margin-bottom: 1em;
}
.de-entry {
    width: auto;
}
}
