css:
@-webkit-document domain(whatsapp.com) {
	body,
	.app-wrapper:before,
	#startup,
	.btn-more,
	.message-system-body,
	.backdrop,
	.media-content {
		background: #181818 !important;
	}
	.menu-tabs.menu-tabs-emoji.emoji-panel-header {
		background: #1c1c1c !important;
	}
	.intro-image {
		border-radius: 1000px;
	}
	::-webkit-scrollbar-thumb {
		background-color: rgba(255,255,255,.3);
	}
	.chatlist .infinite-list-item {
		overflow: hidden;
		height: 71px !important;
	}

	.chatlist .infinite-list-item .chat {
		background: transparent;
	}

	.chatlist .infinite-list-item .active,
	.chatlist .infinite-list-item .chat:hover,
	.chatlist .infinite-list-item .chat:hover:before {
		background: #1c1c1c !important;
		top: 0px;
	}
	.chatlist .infinite-list-item .chat-body .chat-secondary {
		-webkit-transform: scale(.75);
	    width: 400px;
	    margin-left: -50px;
	    margin-top: -5px;
	    margin-bottom: -5px;
	}
	.chatlist .infinite-list-item .chat-body .chat-main {
		margin-top: -5px;
	}
	.chatlist .infinite-list-item .chat-body .chat-main .chat-meta,
	.message .message-author,
	.message .message-datetime,
	.drawer-section .drawer-value,
	.drawer-section-expand .drawer-value,
	.meta-audio {
	  	color: #FFFFFF !important;
	    opacity: .5;
	}
	.chatlist .infinite-list-item .chat-body .chat-secondary .chat-status {
	  	color: #FFFFFF !important;
	    opacity: .8;
	}
	.pane-header,
	.drawer-header,
	.pane-chat .pane-chat-header .chat-body .chat-secondary,
	.pane-chat .pane-chat-header .chat-body .chat-secondary .chat-status {
		background: #1c1c1c !important;
	    color: #8a8a8a !important;
	}
	.pane-chat .pane-chat-header .chat-body .chat-title,
	.chatlist .infinite-list-item .chat-body .chat-main .chat-title,
	.drawer-section .drawer-section-title,
	.drawer-section-expand .drawer-section-title,
	.pane-subheader.pane-list-subheader.subheader-search .cont-input-search input,
	.emojitext,
	.message a,
	.msg-search-notfound,
	.popup,
	.popup-title,
	.chat-status {
	    color: #FFFFFF !important;
	}
	.media-viewer .media-chat:hover {
		background: transparent !important;
	}
	.pane-subheader.pane-list-subheader.subheader-search,
	.pane-subheader.pane-list-subheader.subheader-search .cont-input-search,
	.pane-subheader.pane-list-subheader.subheader-search .cont-input-search .input-placeholder,
	.pane-subheader.pane-list-subheader.subheader-search .cont-input-search input,
	.infinite-list-item,
	.drawer-body,
	.pane-intro,
	.pane-body.pane-list-body,
	.pane-body.pane-chat-body.lastTabIndex,
	.list-title,
	.chat-secondary .chat-meta,
	.drawer-body, .btn-add-media button,
	.popup,
	.media-panel-header,
	.media-panel-header .media-chat {
		background: #2b2b2b;
		border-color: #181818;
	}
	.pane-subheader.pane-list-subheader.subheader-search .cont-input-search {
		border: 1px solid #555555;
	}
	.message {
		width: 90%;
		border-radius: 0;
		max-width: 100%;
	}
	.message:before {
		height: 0px;
		width: 0px;
	}
	.message.message-in,
	.message.message-out,
	.pane-subheader.pane-list-subheader.subheader-search .cont-input-search:hover,
	.message-in .btn-context,
	.message-out .btn-context,
	.btn-media-prev {
		background: #404040;
	}
	.message:hover > .bubble .message-author,
	.message:hover > .bubble .message-datetime{
		opacity: 1;
	}
	.pane-footer.pane-chat-footer .input-container {
		border-radius: 0;
		background: #595959;
		color: #FFFFFF;
	}
	.drawer-section,
	.drawer-section-expand {
		border-radius: 0;
		color: #FFFFFF;
	}
	.drawer-section .drawer-value:hover,
	.drawer-section-expand .drawer-value:hover {
		opacity: 1;
	}
	.drawer-section-photo {
		background: transparent !important;
	}
	.header-body * {
		color: #c1c1c1 !important;
	}
	.tail.message-in:before, .tail.message-out:before {
		display: none;
	}
	.message-in .btn-context:after,
	.message-out .btn-context:after{
	    background: -webkit-linear-gradient(left,rgba(64,64,64,0),#404040) !important;
	    background: linear-gradient(to right,rgba(64,64,64,0),#404040) !important;	
	    height: 23px;

	}
	.message .btn-context {
		height: 23px;
	}
	.drawer-header-small, .pane-header,
	.chatlist .infinite-list-item:hover .chat-secondary .chat-meta,
	.drawer-header-offset, .media-collection,
	.pane-footer.pane-chat-footer,
	.drawer-section,
	.drawer-section-expand {
		background: #1c1c1c !important;
	}
	.drawer-header {
		height: 58px;
	}
	.app-wrapper::after {
		background: transparent;
	}
	.chat-secondary .chat-meta {
	    position: absolute;
	    left: 324px;
	}
	.chatlist .chat-secondary {
		width:360px !important;
	    overflow: hidden;
	}
	.icon-chat,
	.icon-menu,
	.icon-clip,
	.chat-secondary .chat-meta > span,
	.icon-back, 
	.icon-pencil,
	.icon-smiley,
	.icon-ptt,
	.icon-emoji-recent,
	.icon-emoji-people, 
	.icon-emoji-nature, 
	.icon-emoji-things,
	.icon-emoji-places,
	.icon-emoji-symbols,
	.icon-hide,
	.icon-x,
	.icon-send,
	.chat-status .icon:not(.icon-status-dblcheck-ack),
	.icon-audio-download,
	.icon-audio-play, 
	.icon-audio-pause,
	.icon-search,
	.icon-down::before,
	.icon-x-viewer,
	.icon-download {
	    -webkit-filter: invert(1);
	}
	.icon-audio-download svg {
		fill: #fff;
	}
	.input-search {
		background: transparent !important;
	}
}
