css:
@-moz-document url-prefix("http://runouw.com/") {
/* BACKGROUND RESET */

#wrap {
    width: 85%;
    margin-left: auto;
    margin-right: auto
}
.forabg {
    align-content: center;
    background: #909090;
    border-left: 1px solid rgb(0, 0, 0);
    border-right: 1px solid rgb(0, 0, 0);
    border-bottom: 1px solid rgb(0, 0, 0);
    border-top: 1px solid rgb(0, 0, 0);
}
.forumbg,
.bg1,
.bg2,
ul.forums,
#tabs a {
    background: #E3E3E3;
}
.li.row:hover {
    background: #FFFFFF;
}
.li.row.dl {
    color: black;
}
}

@-moz-document url-prefix("http://runouw.com/") {
/* ＢＡＣＫＧＲＯＵＮＤ ＧＲＡＤＩＥＮＴ */

body {
    background: -webkit-linear-gradient(left, #9bd7fd 0%, #0082e6 51%, #9bd7fd 100%);
    /* For Safari 5.1 to 6.0 */
    
    background: -o-linear-gradient(left, #9bd7fd, #0082e6, #9bd7fd);
    /* For Opera 11.1 to 12.0 */
    
    background: -moz-linear-gradient(left, #9bd7fd 0%, #0082e6 51%, #9bd7fd 100%);
    /* For Firefox 3.6 to 15 */
    
    background: linear-gradient(left, #9bd7fd, #0082e6, #9bd7fd);
    /* Standard syntax */
}
}

@-moz-document url-prefix("http://runouw.com/") {
/* ＳＣＲＯＬＬＢＡＲ */

::-webkit-scrollbar {
    background-color: #9bd7fd;
    width: 12px;
    height: 12px;
}
::-webkit-scrollbar-thumb {
    background-color: #0082e6;
    border-top-left-radius: 99px;
    border-top-right-radius: 99px;
    border-bottom-right-radius: 99px;
    border-bottom-left-radius: 99px;
}
}
