css:
@-moz-document domain("userstyles.org") {
body {
    margin-top: 70px;
    background-color: #7D7DA2;
    background-color: #7F87BF;
    color: #fff;
    max-width: 90%!important;
}
a {
    color: #FFF;
}
a:hover {
    color: #4ED5FF;
}
a:visited {
    color: #F1E9F7;
}
a:visited:hover {
    color: #FFBBFC;
}
h1 {
    font-weight: 100;
    color: #000;
}
h1 a {
    color: #000;
    text-decoration: none;
}
h1 a:visited {
    color: #000;
}
h1 a:visited:hover {
    color: #B23ABF;
}
div#left-sidebar {
    margin-top: -23px;
    padding-top: 13px;
    background-color: #655C86;
    border-radius: 3px;
    color: #ddf;
}
header#main-header {
    margin: 0;
    padding: 10px 60px;
    border-radius: 0;
    position: fixed;
    left: -30px;
    right: -30px;
    top: 0;
    height: 30px;
    z-index: 10;
    box-shadow: 0 0 20px rgba(0, 0, 68, 0.43);
    background-color: #C5C8FF;
    /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#87e0fd+0,53cbf1+40,05abe0+100;Blue+3D+%23+16 */
    
    background: #87e0fd;
    /* Old browsers */
    
    background: -moz-linear-gradient(top, #87e0fd 0%, #53cbf1 40%, #05abe0 100%);
    /* FF3.6+ */
    
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #87e0fd), color-stop(40%, #53cbf1), color-stop(100%, #05abe0));
    /* Chrome,Safari4+ */
    
    background: -webkit-linear-gradient(top, #87e0fd 0%, #53cbf1 40%, #05abe0 100%);
    /* Chrome10+,Safari5.1+ */
    
    background: -o-linear-gradient(top, #87e0fd 0%, #53cbf1 40%, #05abe0 100%);
    /* Opera 11.10+ */
    
    background: -ms-linear-gradient(top, #87e0fd 0%, #53cbf1 40%, #05abe0 100%);
    /* IE10+ */
    
    background: linear-gradient(to bottom, #87e0fd 0%, #53cbf1 40%, #05abe0 100%);
    /* W3C */
    
    /* filter: progid: DXImageTransform.Microsoft.gradient( startColorstr='#87e0fd', endColorstr='#05abe0', GradientType=0); */
    /* IE6-9 */
}
#left-sidebar > * {
    background-color: inherit;
    border-radius: 0;
    margin: 10px;
    padding: 0;
}
input#search-submit {
    background-color: #B4A6EA;
    color: #000;
    cursor: pointer;
    width: 35px;
}
input#search-terms {
    width: 127px;
    position: absolute;
}
div#simple-search {
    position: relative;
    height: 25px;
    margin-bottom: 5px;
    overflow: hidden;
    border-radius: 5px;
}
div#simple-search input {
    height: 25px;
    border: none;
    padding: 5px 10px;
    margin: 0;
    overflow: hidden;
    transition: width 200ms, padding 200ms;
}
.advanced-search-active div#simple-search input#search-terms {
    width: 100%;
}
.advanced-search-active div#simple-search input#search-submit {
    width: 0;
    padding: 5px 0;
    display: initial;
}
div#social,
div.pagination,
article.style-brief {
    background-color: #B4A6EA;
    padding: 5px 10px;
    border-radius: 5px;
    border-bottom: 3px solid #655C86;
}
div.pagination a,
div.pagination em.current,
div.pagination span {
    color: #655C86;
    text-decoration: none;
    font-size: 19px;
    font-weight: 500;
    margin: 0 5px;
    font-family: monospace;
}
article.style-brief {
    color: #000;
    padding: 0;
    overflow-y: hidden;
    overflow-x: auto;
}
article.style-brief a {
    /*color: #3D3950;*/
    
    color: #000;
}
pre {
    color: #000;
}
}
