css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
.l-header-container .types-tabs a,
.tags-and-tumblelogs *,
.l-content div,
.l-content h1,
.l-content h2,
.l-content h3,
.l-content h4,
.l-content h5,
.l-content h6,
.l-content blockquote,
.l-content p,
.l-content ul,
.l-content li,
.l-content span,
.l-content small,
.l-content a {
	color: #828282 !important;
	font-weight: normal;
}
/* background: #fff */

#tabs,
.tab_notice,
.identity .right_column:after,
input,
textarea,
.l-header-container--refresh,
.right_column,
.left_column,
.l-content,
.identity {
	background: #fff !important;
	background-color: #fff !important;
	color: #828282 !important;
}
.post_avatar .post_avatar_link,
.post_avatar {
	background-color: rgba(0, 0, 0, 0) !important;
}
.identity .controls_section.user_list li .follow_list_item_blog:before {
	background: rgba(255, 255, 255, 0) !important;
	background-color: rgba(255, 255, 255, 0) !important;
}
.elevator {
	background: #fff !important;
	background-color: #fff !important;
}
.compose-button {
	fill: #BFBFBF !important;
}
.chrome.blue {
	border-color: #BFBFBF !important;
	background: #BFBFBF !important;
	color: #fff !important;
}
.chrome.clear {
	border-color: #EFEFEF !important;
}
#tabs,
#tabs a {
	border: none !important
}
#tabs .tab {
	border-radius: 0 !important;
}
input,
textarea {
	outline: 1px solid #eee !important;
}
.selected .tab_notice {
	background: #fff !important;
}
.tab-notice--outlined {
	border: 1px solid #DADADA !important;
	font-weight: normal !important;
	line-height: 20px !important;
}
.l-header-container--refresh {
	border-bottom: 1px solid #eee !important;
}
.loader-bar.bottom,
.tab_anchor,
.ui_search .search_form_field:before,
.logo .svg-logo,
.post_buttons > a > i {
	-webkit-filter: grayscale(100%) contrast(0%);
	filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale");
	filter: grayscale(100%);
	transition: all 0.2s ease-in-out;
}
.post_buttons > a:hover > i,
.logo:hover .svg-logo {
	-webkit-filter: grayscale(0%) contrast(100%) !important;
}
.post.new_post .new_post_label {
	margin-top: -16px !important;
}
/* dashboard */

#posts.posts>.post_container {
	outline: 1px solid #eee !important;
}
#posts.posts>.post_container#new_post_buttons {
	outline: none !important;
}
.post.new_post .new_post_label {
	background-image: none !important;
}
.post.post_full {
	background: none !important;
}

.peepr-body .indash_blog .post {
	background: #fff !important;
}

.radar .radar_footer .radar_avatar:before {
	background-image: none !important;
}
.post .audio_player {
	background-color: #fff !important;
}
.audio_player .audio_controls,
.audio_player .audio_visualizer {
	border: none !important;
}
.post_full .post_permalink {
	border-color: #ECECEC #F1F1F1 transparent transparent !important;
}
.reblog-list,
.reblog-trail--original-post {
	border-top: none !important;
}
/* recommended-for-you */

.identity .controls_section.user_list li .follow_list_item_blog:before {
	border-right: 0 !important;
}
.discover .discover-posts .post {
	border: 1px solid #eee !important;
}
/* activity*/

.identity .controls_section li:not(.section_header):not(.selected_blog):hover {
	background-color: #EAEAEA !important;
}
.notifications_page .section.divider {
	background: none !important;
}
/* Inbox */

.identity .send_fan_mail {
	background: #BFBFBF !important;
}
.identity .send_fan_mail div {
	color: #fff !important;
}
/* for you*/

.discover .discover-search-term-item:nth-child(6n+1) .discover-search-term {
	background-color: #CDF3E0 !important;
}
.discover .discover-search-term-item:nth-child(6n+2) .discover-search-term {
	background-color: #F9D7CF !important;
}
.discover .discover-search-term-item:nth-child(6n+3) .discover-search-term {
	background-color: #F9DFC0 !important;
}
.discover .discover-search-term-item:nth-child(6n+4) .discover-search-term {
	background-color: #DDEDF7 !important;
}
.discover .discover-search-term-item:nth-child(6n+5) .discover-search-term {
	background-color: #EAEAEA !important;
}
.discover .discover-search-term-item:nth-child(6n+6) .discover-search-term {
	background-color: #EFDDFB !important;
}
/* following*/

.follower.invite_someone {
	background: rgba(0, 0, 0, 0) !important;
}
input.text_field {
	border: solid 1px #FFFFFF !important;
}
.follower {
	background: rgba(0, 0, 0, 0.01) !important;
}
/*setting*/

.tumblr_settings .settings_heading {
	border-bottom: 1px solid #eee !important;
}
.identity .controls_section li:not(.no_push):not(.section_header).selected,
.identity .controls_section li:not(.no_push):not(.section_header).selected:hover {
	background-color: #ECECEC !important;
}
/* search */

.search_popover {
	box-shadow: none !important;
}
.search_results_container .tumblelog_mask_item {
	background-color: #E4E4E4;
}
.post_brick {
	border: 1px solid #eee !important;
}
/* business */

#cards .card {
	box-shadow: 0 1px 1px rgba(0, 0, 0, 0.1);
}
.footer_tagline_container,
.chrome_nav,
.chrome_nav .chrome.sidebar {
	border: none !important;
}
.active .chrome {
	background: #EFEFEF !important;
}
/* post form */

.popover--post-settings-dropdown .form-group input {
	outline: none !important;
}
/* post tag */

a.post_tag {
	color: #757575 !important;
}
/* source */

.post .post-source-footer a.post-source-link {
	color: #FF99AB !important;
}
/* drawer */

.flag--peepr-redux .peepr-body .indash_blog .post,
.drawer.open .flag--peepr-redux .peepr-body .indash_blog .post {
	background: #fff !important;
}
#body.old_archive {
	background: #FFFFFF !important;
}
/* explore */

.post_brick .expand-post-content,
.post_brick.is_photo .post_media+.expand {
	background: rgba(134, 134, 134, 0.63) !important;
	color: #fff !important;
	font-weight: bold !important;
}
/* explore chats */

.post_brick.is_conversation .conversation_lines .chat_line:nth-child(even) {
	background: #F9F9F9 !important;
	border: 1px solid #eee !important;
	border-right: none !important;
	border-left: none !important;
	padding-bottom: 2% !important;
}
/* posts button */

.flat-button.blue {
	border-color: #ADADAD !important;
	background: #CECECE !important;
}
.post-form .split .options:after,
.pop-menu .split .options:after {
	border-left: 1px solid #ADADAD !important;
	border-left-color: rgba(173, 173, 173, 0) !important;
}
/* post-forms-glass | background  overlay */

.post-forms-glass {
	background-color: rgba(238, 238, 238, 0.51) !important;
}
.ui_dialog_pos,
.ui_dialog_lock {
	background: rgba(238, 238, 238, 0.51) !important;
}
.ui_dialog {
	padding: 3% !important;
	background: #eee !important;
	border-radius: 13px !important;
	border: 1px solid #D2D2D2 !important;
}
.ui_dialog .text {
	color: #7B7B7B !important;
}
/* non login user https://www.tumblr.com/search */

body#search_actions_search_lightbox_post.identity.base.hello_lightbox {
	background: rgba(58, 58, 58, 0.51) !important;
}
body#search_actions_search_lightbox_post.identity.base.hello_lightbox .l-content .post {
	box-shadow: none !important;
}
body#search_actions_search_lightbox_post.identity.base.hello_lightbox .l-content .follow_prompt h2 {
	text-shadow: none !important;
}
body#search_actions_search_lightbox_post.identity.base.hello_lightbox .l-content .lightbox_header .tumblelog_avatar {
	box-shadow: none !important;
}
body#search_actions_search_lightbox_post.identity.base.hello_lightbox .l-content .lightbox_header .tumblelog_avatar {
	border-radius: 0px !important;
}

.logo .png-logo {
filter: invert(100%);
    opacity: 0.3;
}
.tab_bar .tab_anchor:before, .tab_bar .tab_anchor i:before,
.tab_bar a:before {
    color: #a5a5a5 !important;
}
}
