css:
@-moz-document domain("dota2lounge.com") {
/*
Made by Kizaro
To get info on next style created or contact me, Follow https://twitter.com/ViktorSIXth
*/ 

a:link {
    color: #901212 !important;
    font-size: 15px!important;
    font-weight: 700!important;
}
a:visited{
    color:#760000 !important;
}
a:hover {
    color:#DC0000 !important;
	background: #000!important;
}
a:active{color:#FF0000 !important;
}


/*background color*/
body {
    background-color: black!important;
}

/*bottom color*/
body.darktheme .file-list-item .desc, .file-list-item .bottombar {
    color: #AD2020!important;
}

/*box color*/
.box {
    background: black!important;
}

/*border of offers*/
.tradepoll {
    border: 1.5px groove red!important;
}

/*border bottom of offers headline*/
.tradeheader {
    border-bottom: 1px groove red!important;
}

/*text shadow*/
#tradelist a {
    text-shadow: 1px 1px 0 #7A0000!important;
}

/*offers color*/
div.tradecnt {
	background: black!important;
}

/*matches color*/
.match, .winnings {
    float: left;
    width: 100%;
    min-height: 75px;
    background-color: #531414;
    background-repeat: no-repeat;
    background-position: right;
    border-radius: 5px;
    box-shadow: 1px 1px 2px #F00;
}

/*matches teams color + border*/
.teamtext {
    background: rgba(0, 0, 0, 0.9);
    border: 2px solid red;
}

/*offers border*/
.left, .right {
    background: #000;
    border: 1px dotted red;
}

/*buttons outset*/
.simplePagerNav li a {
    color: #555;
    background: #000;
    margin: 2px;
    height: 18px;
    padding: 4px;
    text-align: center;
    min-width: 18px;
    border-radius: 3px;
    border: 1px outset red;
    text-shadow: 1px 1px 0 #000;
    box-shadow: 1px 1px rgba(60, 58, 58, 0);
}

/*text color*/
.whenm, .eventm {
    text-shadow: 1px 1px 0 #000000;
    color: red;
}

tr:nth-child(odd) {
    background: #381313!important;
    color: #2CA300!important;
}


.lost {
    text-shadow: 1px 1px 0px #000000;
    color: #A90000;
}

.won {
    text-shadow: 1px 1px 0px #000000;
    color: #2ECA00;
}

html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, embed, figure, figcaption, footer, header, hgroup, menu, nav, output, ruby, section, summary, time, mark, audio, video {
    border: 0;
    font-size: 100%;
    font: inherit;
    vertical-align: baseline;
    margin: 0;
    padding: 0;
    color: #47E0E5;
}


tr:nth-child(odd) {
    background: #2E0000;
}


.lost {
    text-shadow: 1px 1px 0px #000000;
}


#replist li:nth-child(even) {
    background: #300000;
}

#replist li:nth-child(even) {
    background: #300000;
    border-top: 1px solid red;
    border-bottom: 1px solid red;
}


.standard {
    float: left;
    width: 98%;
    margin: 12px 1%;
    background: black;
    border: 1px solid red;
}

.message {
    word-wrap: break-word;
    width: 98%;
    margin: 1px 1% 20px 1%;
    line-height: 1.2em;
    font-weight: 700;
    color: red;
    float: left;
    border: 1px solid red;
    border-radius: 4px;
    box-shadow: -1px -1px 1px #000000;
    background: #000;
    background: radial-gradient(ellipse at 0 0,#000 0,#000000 50%);
}

#messages #notes {
    color: red;
    font-weight: 700;
    border: 1px solid red;
}

.winsorloses {
    width: 47%;
    background: black;
    border: 1px solid red;
}

}
