@-moz-document domain("hltv.org") {
body {
    background: rgba(51, 51, 51, 0.8) !important;
}
.tab_selected {
    color: #f00 !important;
}

div.centerFade {
    font-size: 12px;
}
[title="Win"] {
    font-weight: bold !important;
}
[title="Loss"] {
    font-weight: bold !important;
}
[style="width:606px;height:22px;background-color:white"] {
    height: 30px !important;
}
[style="width:606px;height:22px;background-color:#E6E5E5"] {
    height: 30px !important;
}
.covSmallHeadline {
    font-size: 12px;
}
.covSmallHeadline[style="width:30%;float:left;font-weight:normal;"] {
    width: 20% !important;
    margin-left: 5%;
}
.covSmallHeadline[style="width:9%;float:left;text-align:center"] {
    padding-left: 3% !important;
}
.covSmallHeadline[style="width:10%;float:left;;font-weight:normal;"] {
    font-size: 10px;
}
div.covSmallHeadline[style="width:30%;float:left;"] {
    width: 20% !important;
    padding-left: 40px;
}
div.covSmallHeadline[style="width:23%;float:left;font-weight:normal;"] {
    width: 25.5% !important;
}
img.flagFix {
    width: 20px !important;
    height: 18px !important;
    position: relative;
    top: 1px;
}
