@-moz-document domain(tumblr.com) {



/* Dashboard - America */



#home_button {



    padding-right: -17px !important;

}





#home_button a {



    height: 0 !important;

    width: 0 !important;

    top: 0px !important;

    padding-left: 36px !important;

    padding-top: 40px !important;

    background: url(http://i.imgur.com/smxS2qU.png) !important;}

/* Explore all of Tumblr - Suit */



#discover_button {



    padding-right: -17px !important;

}





#discover_button a {



    height: 0 !important;

    width: 0 !important;

    top: 0px !important;

    padding-left: 36px !important;

    padding-top: 40px !important;

    background: url(http://i.imgur.com/8plQPso.png) !important;}


/* Inbox - Pen */



#inbox_button {



    padding-right: -17px !important;

}





#inbox_button a {



    height: 0 !important;

    width: 0 !important;

    top: 0px !important;

    padding-left: 36px !important;

    padding-top: 40px !important;

    background: url(http://i.imgur.com/33Dj5zF.png) !important;}




/* activity- LegalScales */



#activity_button a {



    height: 0 !important;

    width: 0 !important;

    top: 0px !important;

    padding-left: 36px !important;

    padding-top: 40px !important;

    background: url(http://i.imgur.com/DYFyl6b.png) !important;}

/*Positioning*/

.tab_bar .iconic a.tab_anchor {background-size: 175px 36px;}



.tab_bar .tab_discover .tab_anchor {background-position: -175px 0}



.tab_bar .tab_log_out .tab_anchor {background-position: -140px 0}



.tab_bar .tab_settings .tab_anchor {background-position: -105px 0}



.tab_bar .tab_help .tab_anchor {background-position: -70px 0}



.tab_bar .tab_inbox .tab_anchor {background-position: -35px 0}



.tab_bar .tab_home .tab_anchor {background-position: 0 0}



.tab_bar .tab.iconic:hover .tab_anchor,.tab_bar .tab.iconic:active .tab_anchor {opacity: .7;}



.tab.iconic > .tab_anchor::before {content: none !important;}



}
