css:
@-moz-document regexp("http?:\\/\\/(?!.*i\\.).*\\.kwick\\.de.*"), regexp("http?:\\/\\/(?!.*i\\.).*\\.kiwibox\\.com.*") {

	#contentcontainer {
		margin-top: 0px;
		margin-right: 20px;
	}

	#wrapper {
		float: left;
		width: auto;
	}

	.container {
		margin: 0px auto!important;
		padding: 0px!important;
		width: auto;
	}

	#socialStream .socialStreamColumn {
		width: auto;
		margin-right: 20px;
		float: left;
	}

	#socialStream .socialStreamColumn:last-child {
		display: none;
	}

	#gwrapper {
		width: auto;
		margin: 0px auto;
		margin-left: 10%;
	}

	#ad-skyscraper {
		width: auto;
		margin: 20px;
	}
	
	.socialObject .storyContainer .storyContent .storyImageBox {
		max-width: 350px;
		overflow: hidden;
	}
	
	.socialObject .storyContainer .storyContent .storyVideoBox {
		max-width: 350px;
		overflow: hidden;
	}
}
