css:
@-moz-document domain("habitica.wikia.com") {
/* Ensure all "Information for Developers" sections are visible and highlighted */

.habitrpg-InfoForDevs, .habitica-InfoForDevs {
    display: block !important;
    background-color: #fee;
    border: 1px dashed #f0c;
    padding: 5px;
}

/* Hide information recommended to be hidden from devs */

.habitrpg-InfoForDevs-hideIfDev, .habitica-InfoForDevs-hideIfDev {
    display: none;
}
}
