@-moz-document domain("mail.google.com") {
/* Add a hover highlight for the active mail item */
tr.aqw {
    background: rgba(255,255,255,0.5);
}
/* Hide the 'Consider including' section */
div.az7 {
    display: none;
}
/* Move CC+BCC links up a bit to compensate for the above */
div.ow {
    position: absolute; bottom: 18px; right: 18px;
}
/* Hide side ads section */
div.nH.HbF0O, .Bu + .Bu {
	display: none !important;
}
/* Hide inline ads */
div.a2q {
    display: none !important;
}
/* Hide bottom links for Terms and Privacy */
div.l2 div.ma {
	visibility: hidden !important;
}
/* Aligns the bottom link for Account Activity */
div.l2 .ae3 .l6 {
	position: relative; top: -12px;
}
/* If also using littleleshy's Compact Hangouts stylesheet, this restores the Reply All icon */
div.aaq > img.mK {
    display: block !important; position: absolute; top: 5px; left: 10px;
}
/* If also using littleleshy's Compact Hangouts stylesheet, this restores the Reply All button alignment - English only */
div.aaq[aria-label='Reply to all'] + div.aap {
    position: relative; bottom: 11px;
}
}

@-moz-document domain("facebook.com") {
/* LHS */
#appsNav h4 { display: none; } /* removes heading for explore */
#appsNav li:nth-child(n+2) { display: none; } /* limits explore list to 1 */

/* Main newsfeed */
div.injectedSearchSuggestion { display: none; } /* removes search suggestions prompt from search box */
div[data-ft='{"tn":"*="}'] { display: none; } /* removes 'People Also Shared' box that gets inserted after you click a link */
div[data-referrer^='hyperfeed_story_id_'] div[data-ownerid*='_jsonp_'] { display: none; } /* removes 'Suggested Videos' that get inserted after you watch one */

/* RHS */
#pagelet_games_rhc { display: none; } /* removes recommended games */
#pagelet_ego_pane { display: none; } /* removes suggested pages, people you may know, etc. */
#pagelet_trending_tags_and_topics { display: none; } /* removes trending topics */
div#pagelet_rhc_footer a._41uf, div#pagelet_rhc_footer a._41uf + span { display: none; } /* removes Ad Choices link */
div#pagelet_rhc_footer a[title="Learn about your privacy and Facebook."], div#pagelet_rhc_footer a[title="Learn about your privacy and Facebook."] + span, div#pagelet_rhc_footer a[title="Review our terms and policies."], div#pagelet_rhc_footer a[title="Review our terms and policies."] + span, div#pagelet_rhc_footer a[title="Cookies"], div#pagelet_rhc_footer a[title="Cookies"] + span { display: none; } /* removes Privacy, Terms, Cookies links - English only */

/* Own profile page */
li.fbTimelineUnit div#profile_intro_card_bio { display: none; } /* removes nag to add bio text */
li.fbTimelineUnit a[data-store*='fav_photos_add_click'] { display: none; } /* removes nag to add favorite photos */

/* Gallery view for photos */
.ego_section { display: none; } /* removes people you may know */
}
