css:
/*change main colors*/

.byui-bs .navbar-default {
    background-color: #0e0038;
}
.byui-bs .panel-custom > .panel-heading {
    background-color: #0e0038;
    border-radius: 10px 10px 0px 0px;
}

.d2l-note{
    background-color: rgba(0,0,0,.8)
}
.byui-bs .panel-custom {
    background-color: rgba(255, 255, 255, 0.2);
    border-radius: 15px 15px 15px 15px;
    overflow: hidden;
}

.page-title-container {
    color: grey;
    background-color: black;
    border-color: #222;
}
.list-item-title {
    color: #eeeeee;
    font-weight: bold;
}
.list-item-title:hover {
    color: gray;
    font-weight: bold;
}

.byui-bs a{
    color: #eeeeee;
}

/*font colors*/
.list-item-subtitle, .list-item-subtitle-right{
    color: #eeeeee;
}
.courseGradeGreen {
    color: lightgreen !important;
    cursor: pointer;
}
/*inside course colors*/
.x-tree-node a span, .x-dd-drag-ghost a span {
    color: #cccccc;
    font: normal 11px "century gothic", tahoma, helvetica, sans-serif;
}
.x-panel-tbar-noheader .x-toolbar, .x-panel-mc .x-panel-tbar .x-toolbar {
    border-color: #222;
}
.x-layout-split, .x-layout-collapsed {
    background-color: #222;
}
.x-panel{
    border-color: #222;
}
.highlight_color, .highlight_color *, .x-tree-node .x-tree-selected, .x-grid3-row-selected {
    background-color: #555!important;
}
.x-tree-node .x-tree-node-over, .selectable-hover{
background-color: #555!important;
}
#ext-gen17{
 overflow:hidden!important;   
}
.x-btn-tl, .x-btn-tr, .x-btn-tc, .x-btn-ml, .x-btn-mr, .x-btn-mc, .x-btn-bl, .x-btn-br, .x-btn-bc {
    background-image: none;
    background-color: #555;
}

.course_player_nav_prev {
    background-image: url(http://benharkerphoto.com/comm310-folder/images/nav_back_normal.gif);
}

.course_player_nav_next {
    background-image: url(http://benharkerphoto.com/comm310-folder/images/nav_next_normal.gif);
}
input{
 background-color: #222; 
    border-color: #555;
    padding: 2px;
    margin: 2px;
}

file-upload-button, button {
    background-color: #222;
}

/*window pop-ups*/
.grade_label {
    color: #cccccc;
    font-family: "century gothic";
}

.x-window-tc, .x-window-tl, .x-window-tr, .x-window-bl, .x-window-br, .x-window-ml, .x-window-mr, .x-window-mc, .x-window-bc {
    background-image:none;
    background-color: #555;
}

body.x-body-masked .x-window-mc, body.x-body-masked .x-window-plain .x-window-mc {
    background-color: #222;
    border-color: #222;
}
.x-window-plain .x-window-body {
    border-color: #555;
}
.x-tool{
    background-image: url(http://benharkerphoto.com/comm310-folder/images/tool-sprites.gif);
}
.ext-el-mask {
    background-color: #222;
}
/*icon and divider colors*/


.color_header, .x-toolbar {
    background: #555 !important;
}

/* change hover to gray*/

.byui-bs .navbar-default .navbar-nav > li > a:hover,
.byui-bs .navbar-default .navbar-nav > li > a:focus {
    background-color: #333;
}
.byui-bs .navbar-default .navbar-toggle:hover,
.byui-bs .navbar-default .navbar-toggle:focus {
    background-color: #333;
}
.byui-bs .navbar-default .navbar-brand:hover,
.byui-bs .navbar-default .navbar-brand:focus {
    background-color: #333;
}
/*hide eye on ilear*/

.eye-on-ilearn-container {
    display: none;
    visibility: hidden;
    width: 0px;
    height: 0px;
    min-width: 0px;
}

scrollbar { 
    background-color: rgba(0,0,0,.2);
}
scrollbar-thumb        {
    background-color: rgba(0,0,0,1);
}

*{
    font-family: "Century Gothic";
    font-size: 1em;
}
/*change background*/
.custom-background .byui-full-page {
    background-color: rgba(0,0,0,.8);
}

.page_color{
    background-color: #111;
}

.color_light {
    background-color: #222;
}

.color_medium {
    background-color: #444;
    border: none;
}

.gbg-c.page_color, .groupsetup-cell.page_color {
    background-color: #333;
}
