css:
@-moz-document domain("brainhoney.com") {
/*change main colors*/

.byui-bs .navbar-default {
    background-color: pink;
}
.byui-bs .panel-custom > .panel-heading {
    background-color: pink;
    /*border-radius: 10px 10px 0px 0px;*/
}
/*
.d2l-note{
    background-color: rgba(0,0,0,.8)
}
.byui-bs .panel-custom {
    background-color: rgba(255, 255, 255, 0.2);
    border-radius: 15px 15px 15px 15px;
    overflow: hidden;
}

.page-title-container {
    color: grey;
    background-color: black;
    border-color: #222;
}
.list-item-title {
    color: white;
    font-weight: bold;
}
.list-item-title:hover {
    color: gray;
    font-weight: bold;
}

.byui-bs a{
    color: white;
}

/*font color*/
.list-item-subtitle, .list-item-subtitle-right{
    color: white;
}

/* change hover to gray*/

.byui-bs .navbar-default .navbar-nav > li > a:hover,
.byui-bs .navbar-default .navbar-nav > li > a:focus {
    background-color: #ff6bd2;
}
.byui-bs .navbar-default .navbar-toggle:hover,
.byui-bs .navbar-default .navbar-toggle:focus {
    background-color: #ff6bd2;
}
.byui-bs .navbar-default .navbar-brand:hover,
.byui-bs .navbar-default .navbar-brand:focus {
    background-color: #ff6bd2;
}
/*hide eye on ilearn*/

.eye-on-ilearn-container {
    display: none;
    visibility: hidden;
    width: 0px;
    height: 0px;
    min-width: 0px;
}
*{
    font-family: "Century Gothic";
    font-size: 1em;
}
/*change background*/
.custom-background .byui-full-page {
    background-color: #ffe5f7;
}

}
