css:
@-moz-document domain("readmanga.me"), domain("grouple.co"), domain("librebook.me"), domain("findanime.me"), domain("doramatv.ru"), domain("selfmanga.ru"), domain("selflib.me"), domain("mintmanga.com") {
body {
    background: url(http://i70.fastpic.ru/big/2016/0622/2f/bd88f75d98770248aa958db145bdd22f.png) repeat!important;
    background-color: rgb(20, 20, 20)!important;
    color: rgb(120, 120, 120)!important;
}

.federation-nav   .container,
.popover-title,
a:focus{
	background-color:rgba(0,0,0,0);
}

#mangaPicture, #nextPicture {
	opacity: /*[[opacity]]*/;
    border-radius: /*[[round]]*/px;
}

a:visited,
.cattitle,
.forum-breadcrumb * {
    color: #445D6F!important;
    transition: 300ms;
}

a,
.js-link {
    color: #176093!important;
    transition: 300ms;
}

.popover.bottom > .arrow:after, .popover-title,
.table > thead > tr > th{
border-bottom-color:#176093;
}

.popover.top > .arrow:after {
    border-top-color:#176093;
}

.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
border-top: 1px solid #0d3654;
}

.nav > li > a:hover, .nav > li > a:focus{
background-color: #0d3654;
}

table {
	border-collapse: separate;
}

a:hover,
.js-link:hover,
#mangaBox > div.leftContent > table > tfoot > tr > th > span > a:hover,
.navbar-default .btn-link:hover,
.navbar-default .btn-link:focus {
    color: #ff5202!important;
    background: none!important;
    transition: 300ms;
}

tr.chapter:hover {
    background: none!important;
}

#mangaBox,
.navbar.navbar-default,
.second-nav,
.federation-nav,
.rightContent,
.expandable.expanded .background,
.expandable.expanded .background:hover .open,
.expandable.expanded .background:hover .open .js-link,
.dropdown-menu,
.dropdown-menu .divider,
.js-link.current,
.selectize-control.multi .selectize-input > div,
#cboxTopCenter,
#cboxMiddleLeft,
#cboxTopLeft,
#cboxTopRight,
#cboxMiddleRight,
#cboxBottomCenter,
#cboxBottomRight,
#cboxBottomLeft {
    background: rgb(20, 20, 20)!important;
    transition: 300ms;
}

h1,
p,
h2,
h3,
h1.names span:first-child,
.manga-description p,
.manga-description,
.manga-description span,
.input-group .form-control,
.chosen-container-single .chosen-search input[type="text"],
.selectize-input > input,
.postinfo,
#\3a 1c3 > ul > li > span,
.selectize-control.multi .selectize-input > div,
.navbar-default .btn-link,
.spoiler {
    color: rgb(120, 120, 120)!important;
    transition: 300ms;
}

#form > div > span > button {
    border-radius: 0!important;
}

#mangaBox > div.leftContent > div.manga-description > p {
    background: none!important;
}

#q,
.chosen-container-single .chosen-single,
.chosen-container-single .chosen-search input[type="text"],
.form-control.selectize-control,
.selectize-input,
#subscribeform > div > input,
#subscribeform > div > span > button,
#chapterSelectorSelect {
    border: none!important;
    border-radius: 0!important;
}
.pageBlock {
    border: none!important;
    box-shadow: none!important;
    background: rgb(20, 20, 20)!important;
    transition: 300ms;
}
.control-hint,
.btn-success,
.btn-primary,
.btn-default,
.my-tooltip,
.chosen-container-single .chosen-single,
.chosen-container .chosen-drop,
.chosen-container .chosen-results {
    background: rgb(50, 50, 50)!important;
    border: none!important;
    color: #176093!important;
    transition: 300ms;
}
.fa,
h1.names .name:hover {
    text-shadow: none!important;
}
.form-control,
.pagination>a,
.pagination>span {
    background: rgb(50, 50, 50)!important;
    border: 1px solid rgb(100, 100, 100)!important;
    color: #176093!important;
    transition: 300ms;
}
.nav-tabs > li.active > a {
    background: rgb(40, 40, 40)!important;
    border: solid 1px #176093!important;
    transition: 300ms;
}
.nav-tabs > li > a:hover {
    border-color: #176093!important;
    transition: 300ms;
}
.form-control:focus,
.chosen-container-active.chosen-with-drop .chosen-single,
.selectize-input.focus {
    box-shadow: none!important;
}
.top-block .chosen-container .chosen-drop {
    bottom: -170px;
}
.pull-leftm,
body > div:nth-child(5) > div.right-brand > div,
body > div:nth-child(5) > div.left-brand,
.pull-left {
    display: none!important;
}
.table-hover > tbody > tr:hover,
.qtip .qtip-tip,
.qtip .qtip-tip .qtip-vml,
.qtip .qtip-tip canvas,
.expandable.expanded .background .open .js-link{
    background: rgb(50, 50, 50)!important;
    transition: 300ms;
}
div.qtip-screenshot .qtip-content,
.qtip-light,
#cboxContent {
    background: rgb(50, 50, 50)!important;
    border: none!important;
    color: rgb(120, 120, 120)!important;
    transition: 300ms;
}
.panel,
.panel-footer,
.autocomplete-suggestions,
.mature-message,
.mature-message-small,
.navbar-default .navbar-nav > .open > a,
.selectize-input {
    background: rgb(40, 40, 40)!important;
    border: none!important;
    transition: 300ms;
}
.filter-selector .active,
.form-control.selectize-control,
#mangaBox > div.leftContent > div.manga-description > p > span,
td.catleft,
td.catright,
th.thcornerr,
th.thright,
th,
#mangaBox > div.leftContent > div.manga-description > span,
#mangaBox > div.leftContent > div.manga-description > div > font > span,
#mangaBox > div.leftContent > div.manga-description > div > span,
#mangaBox > div.leftContent > div.person-description > div > div.manga-description > span,
#mangaBox > div.leftContent > div.person-description > div > div.manga-description > h1 > font > span,
#mangaBox > div.leftContent > div.person-description > div > div.manga-description > font > font > span,
#mangaBox > div.leftContent > div.person-description > div > div.manga-description > div > font > span,
#mangaBox > div.leftContent > div.person-description > div > div.manga-description > div > span,
body > div:nth-child(4) > div.left-brand,
body > div:nth-child(4) > div.right-brand > div {
    background: none!important;
}
.autocomplete-suggestions strong,
.expandable.expanded .background .open {
    background: #176093!important;
    transition: 300ms;
}
.autocomplete-suggestions .autocomplete-selected,
table.calendar td.today {
    background: rgb(80, 80, 80)!important;
    transition: 300ms;
}
.expandable.expanded .open-border {
    border-bottom: 5px solid #176093!important;
    transition: 300ms;
}
.nav-tabs,
.media-body {
    border-bottom: solid 1px #176093!important;
}
.expandable.expanded .background:hover .open-border {
    border-bottom: 5px solid rgb(20, 20, 20)!important;
    transition: 300ms;
}
.chosen-container-single .chosen-search input[type="text"] {
    background-color: #111!important;
    transition: 300ms;
}
.btn {
    line-height: 1.3!important;
}
.forumline>*>tr>td,
.forumline {
    background-color: rgb(20, 20, 20)!important;
    border-bottom: 1px solid #176093;
    transition: 300ms;
}

th.thcornerl,
th.thleft,
.popover{
        background-color: rgb(30, 30, 30)!important;

}

.p-control {
    box-shadow: 0 1px 5px 0 rgb(100, 100, 100)!important;
    background-color: rgb(80, 80, 80)!important;
    border: 1px solid rgb(90, 90, 90)!important;
}
.screenshot:hover,
.screenshot {
    background: url(http://res.readmanga.me/static/picture-aada5a4ad6cb09a3788f8a8ede527848.png) transparent no-repeat scroll right 50%!important;
}

.spoiler{
    border: 4px dashed #141414!important;
    background-color: rgb(60, 60, 60)!important;
}

table.calendar tr td:hover {
    background-color: rgb(0, 0, 0)!important;
    transition: 300ms;
}

table.calendar td.weekend {
    background-color: rgb(230, 197, 115)!important;
    transition: 300ms;
}

table.calendar th,
table.calendar td {
    background-color: rgb(30, 30, 30)!important;
    transition: 300ms;
}
table.calendar td,
table.calendar th,
table.calendar {
    border: 1px dotted black!important;
}
::-webkit-scrollbar {
    width: 9px;
}
::-webkit-scrollbar-thumb {
    border-radius: 10px;
    background-color: #555;
}
::-webkit-scrollbar-thumb:hover {
    background-color: #666;
}
::-webkit-scrollbar-track {
    border: none;
    background-color: rgb(50, 50, 50);
}
}
