css:
@-moz-document domain(jeuxvideo.com) {
#prospect,
#prospect.show-pub,
#prospect #table-prospect,
.part-boutique, 
.bonus-track, 
.bonus-sponso,
.mdd-card-suggest, 
#jv-feedback .btn-feedback,
#prospect-square.show-pub, 
#prospect-square2.show-pub,
#prospect-square3.show-pub {
display:none !important;
}/* Supprime les pubs */
}

@-moz-document regexp('http://www.jeuxvideo.com/(?!profil)([^"]*)'), regexp('https://www.jeuxvideo.com/(?!profil)([^"]*)') {
#content { 
padding-top: 20px !important; 
}/* Ajoute un espace entre la barre du menu et la page */
}

@-moz-document url-prefix("http://www.jeuxvideo.com/forums/0-3002464")
{
/* LOGOS NFS */

.titre-head-bloc .titre-bloc 
	{
		background: url("http://image.noelshack.com/fichiers/2015/44/1446110210-ico-titre.png") no-repeat scroll left 4px transparent !important;
		color: #009ad3  !important;
	}/* Modifie la couleur du titre du forum et ajoute le logo NFS à côté du titre */

.titre-head-bloc-forum .titre-bloc-forum 
	{
	background: url("http://image.noelshack.com/fichiers/2015/44/1446110210-ico-titre.png") no-repeat scroll left 4px transparent !important;
	}/* Modifie la flèche à côté du titre du sujet */
}



@-moz-document url-prefix("http://www.jeuxvideo.com")
{
/* Background du site */

#content 
	{
		background-color: #000000 !important;
		background-image: url("http://image.noelshack.com/fichiers/2015/44/1446046020-background.png") !important;
	}/* Modifie le background du site */

#prospect-interstice 
	{
		display:none !important;  
	} /* Supprime la page "Accéder au site" */


/*HEADER*/


#header-logo .a-logo 
	{
		background: url("http://image.noelshack.com/fichiers/2015/44/1446050230-logo.png") no-repeat scroll left top transparent !important;
	} /* Modifie le logo du site */

#header-top 
	{
		background: none repeat scroll 0 0 #122136 !important;
	}/* Modifie la couleur du haut du header */

#header-bottom, #header-bottom .bloc-sticker 
	{
		background: none repeat scroll 0 0 #33405b !important;
	}/* Modifie la couleur de la barre du menu */

#navbar-jv .liste-rubrique>li.open 
	{
		background: #3fd7fa !important;
	}/* Modifie la couleur du menu sélectionné */

#navbar-jv .content-menu-jv 
	{
		background: #33405b !important;
		border-color: #3fd7fa!important;
	}/* Modifie la couleur du background et des bordures du menu */

.mdd-liste-one li .big-one 
	{
		color: #3fd7fa !important;
	}/* Modifie la couleur des titres cliquables du menu déroulant */

.mdd-liste-one li .big-one:hover 
	{
		color: #c5c5c5 !important;
	}/* Modifie la couleur des titres cliquables du menu déroulant au passage de la souris */



/* CONTENU */


.mdd-liste-card-l .mtag, .mdd-card-xl .mtag 
	{
		background: #3fd7fa !important;
	}/* Modifie la couleur des tags dans le menu */


.mdd-liste-card-s li:hover .titre-slice-playlist 
	{
		color: #3fd7fa !important;
	}/* Modifie la couleur des liens dans le menu au passage de la souris */


.mdd-recherche input[type="text"] 
	{
		background: none repeat scroll 0 0 #122136 !important;
		color: #3fd7fa !important;
	}/* Modifie la couleur du background de la recherche du menu déroulant Forums */

.mdd-recherche .icon-search 
	{
		color: #3fd7fa !important;
	}/* Modifie la couleur de l'icone de la recherche du menu déroulant Forums */

.mdd-recherche button 
	{
		border-left: 1px solid #3fd7fa !important;
		color: #3fd7fa !important;
		background: #122136 !important;
	}/* Modifie la couleur du bouton OK de la recherche du menu déroulant Forums */


#header-user .infos-pseudo-avatar .tab-head-avatar 
	{
		background: #009ad3 !important;
	} /* Modifie la couleur du cadre de l'avatar dans le header */ 

.sup 
	{
		background: none repeat scroll 0 0 #009ad3  !important;
	}/* Modifie la couleur de l'icone MP quand il y a des mps */

#header-store-jv 
	{
		display:none !important; 
	}

   /* FORUMS */

/* Sidebar */

.menu-user-forum .interrupteur-inline.actif 
	{
		background: none repeat scroll 0 0 #009ad3 !important;
		border: 1px solid #009ad3 !important;
	}/* Modifie la couleur des options sélectionnées dans les paramètres */



#bloc-meta-titre-jeu .poutre-machine 
	{
		background: none repeat scroll 0 0 #009ad3 !important;
	}/* Modifie la couleur de la barre verticale dans le bloc de la fiche du jeu */

.poutre-machine .label-pc, .poutre-machine .hover-pc:hover, .poutre-machine .hover-pc:focus,
.poutre-machine .label-ps4, .poutre-machine .hover-ps4:hover, .poutre-machine .hover-ps4:focus,
.poutre-machine .label-one, .poutre-machine .hover-one:hover, .poutre-machine .hover-one:focus,
.poutre-machine .label-wiiu, .poutre-machine .hover-wiiu:hover, .poutre-machine .hover-wiiu:focus,
.poutre-machine .label-ps3, .poutre-machine .hover-ps3:hover, .poutre-machine .hover-ps3:focus,
.poutre-machine .label-360, .poutre-machine .hover-360:hover, .poutre-machine .hover-360:focus,
.poutre-machine .label-3ds, .poutre-machine .hover-3ds:hover, .poutre-machine .hover-3ds:focus,
.poutre-machine .label-vita, .poutre-machine .hover-vita:hover, .poutre-machine .hover-vita:focus,
.poutre-machine .label-ds, .poutre-machine .hover-ds:hover, .poutre-machine .hover-ds:focus,
.poutre-machine .label-wii, .poutre-machine .hover-wii:hover, .poutre-machine .hover-wii:focus,
.poutre-machine .label-ios, .poutre-machine .hover-ios:hover, .poutre-machine .hover-ios:focus,
.poutre-machine .label-android, .poutre-machine .hover-android:hover, .poutre-machine .hover-android:focus,
.poutre-machine .label-mac, .poutre-machine .hover-mac:hover, .poutre-machine .hover-mac:focus,
.poutre-machine .label-web, .poutre-machine .hover-web:hover, .poutre-machine .hover-web:focus
	{
		background-color: #009ad3  !important;
	}/* Modifie la couleur de la barre verticale dans le bloc de la fiche de jeu */

#bloc-meta-titre-jeu 
	{
		background:#112136  !important;
	}/* Supprime le fond du bloc de la fiche de jeu */

#bloc-meta-titre-jeu .bloc-onglet-fiche, #bloc-meta-titre-jeu .onglet-fiche 
	{
		background:#33405b !important;
	}/* Modifie la couleur du bloc inférieure de la fiche de jeu */

.label-support.active 
	{
		background:#009ad3 !important;
	}/* Modifie la couleur du fond de "Tous supports" */

#bloc-meta-titre-jeu .onglet-fiche.active 
	{
		background: none repeat scroll 0 0 #F5F5F5  !important;
		color: #009ad3  !important;
	}/* Modifie la couleur du texte de l'onglet actif dans le bloc de la fiche de jeu */

#bloc-meta-titre-jeu .active .titre-onglet 
	{
		border-bottom: 2px solid #009ad3  !important;
	}/* Modifie la couleur de la bordure de l'onglet actif dans le bloc de la fiche de jeu */

#bloc-meta-titre-jeu .onglet-fiche:hover, #bloc-meta-titre-jeu .onglet-fiche:hover .info-plus 
	{
		background: none repeat scroll 0 0 #F5F5F5  !important;
		color: #009ad3 !important;
	}/* Modifie la couleur du texte des onglets dans le bloc de la fiche de jeu au passage de la souris */

.label-support 
	{
		background-color: #33405b  !important;
	}/* Modifie la couleur de fond des supports dans le bloc de la fiche de jeu */

#content { 
padding-top: 20px !important; 
}/* Ajoute un espace entre la barre du menu et la page */

/* TABLEAU */

#head-sujet
	{
		color: #009ad3  !important;
	}/* Modifie la couleur de la barre de la liste des topics */
#head-auteur
	{
		color: #009ad3  !important;
	}/* Modifie la couleur de la barre de la liste des topics */
#head-reponse
	{
		color: #009ad3  !important;
	}/* Modifie la couleur de la barre de la liste des topics */
#head-dernier-msg
	{
		color: #009ad3  !important;
	}/* Modifie la couleur de la barre de la liste des topics */
#head-admin
	{
		color: #009ad3  !important;
	}/* Modifie la couleur de la barre de la liste des topics */

#table-liste-topic-forum thead 
	{
		background: none repeat scroll 0 0 #334058 !important;
		color: #334058 !important;
	}/* Modifie la couleur de la barre de la liste des topics */

#table-liste-topic-forum .sujet-topic .titre-topic a 
	{
		color: #009ad3 !important;
	}/* Modifie la couleur des liens sur la liste des topics */

#table-liste-topic-forum .sujet-topic .titre-topic a:hover 
	{
		color: #3fd7fa !important;
	}/* Modifie la couleur des liens sur la liste des topics au passage de la souris */

#table-liste-topic-forum .sujet-topic .titre-topic a:visited 
	{
		color: #cacaca !important;
	}/* Modifie la couleur des liens visités sur la liste des topics */

#table-liste-topic-forum .sujet-topic .titre-topic a:hover:visited 
	{
		color: #FFFFFF !important;
	}/* Modifie la couleur des liens visités sur la liste des topics au passage de la souris */

.container-content 
	{
		background-color: #000000 !important;
		background-image: url("http://image.noelshack.com/fichiers/2015/44/1446109192-bg-forums.jpg") !important;
	}/* Change le background des forums */

.titre-bloc-forum span 
	{
    	color: #000000!important;
	}/* Modifie la couleur du titre du sujet quand on est à l'intérieur */

#table-liste-topic-forum 
	{
    	background-color: #122136;
    	border: 1px solid #122136;
	}/* Modifie la couleur et le fond de la grille des topics */

#table-liste-topic-forum tr, #table-liste-topic-forum tr a, #table-liste-topic-forum tr a:visited 
	{
	    color: #009ad3 !important;
	}/* Modifie la couleur du texte des colonnes auteur, nb et dernier msg */




#table-liste-topic-forum tbody tr, .bloc-message-forum:nth-child(even) 
	{
 	   background-color: #122136;
	}/* Modifie la couleur d'un message sur deux */

#table-liste-topic-forum tbody tr:nth-child(2n+1), .bloc-message-forum:nth-child(odd) 
	{
  	  background-color: #122136;
	}/* IDEM */
    
#table-liste-topic-forum tbody tr:hover 
	{
 	   background-color: #33405b;
	}/* Modifie les couleurs des sujets de la liste au passage de la souris */

.bloc-message-forum 
	{
	    color: #c5c5c5;
    	border: 3px #33405b;
	}/* Modifie la couleur de l'encadré des messages et de son texte */

.bloc-message-forum .bloc-header 
	{
    border-bottom: 1px dotted #009ad3;
	}/* Modifie la couleur de la séparation pseudo, date, heure / message des post */

.bloc-message-forum .bloc-pseudo-msg 
	{
    color: #009ad3;
	}/* Modifie la couleur des pseudo dans le post */

/* Page principale */



.panel-jv-forum > .panel-heading 
	{
		background-color: #33405b  !important;
	}/* Modifie la couleur du haut des blocs infos et forums préférés */

.panel-jv-forum .titre-info-fofo 
	{
		color: #009ad3 !important;
	}/* Modifie la couleurs des titres des blocs infos et forums préférés */

#ascrail2002 > div:nth-child(1), #ascrail2003 > div:nth-child(1) 
	{
		background-color: #009ad3  !important;
	}/* Modifie la couleur de la scrollbar dans le bloc forums préférés */

.btn-actu-new-list-forum 
{
		background: none repeat scroll 0 0 #33405b  !important;
		border: 1px solid #33405b !important;
	    text-shadow: none;
		color: #009ad3;
	}/* Modifie la couleur des boutons nouveau sujet et actualiser */

.btn-actu-new-list-forum:hover 
	{
		background: none repeat scroll 0 0 #009ad3 !important;
		border: 1px solid #009ad3 !important;
		color: #33405b;
	}/* Modifie la couleur des boutons nouveau sujet et actualiser au passage de la souris */

.btn-topic-resolu
	{
		background: none repeat scroll 0 0 #33405b  !important;
		border: 1px solid #33405b !important;
	    text-shadow: none;
		color: #009ab3;
	}/* Modifie la couleur du topic résolu */

.btn-topic-resolu:hover
	{
		background: none repeat scroll 0 0 rgb(127,186,3) !important;
		border: 1px solid rgb(127,186,3) !important;
		color: #FFFFFF;
	}/* Modifie la couleur du topic résolu au passage de la souris */

.bloc-rech-forum .btn-lancer-rech 
	{
		background: none repeat scroll 0 0 #33405b  !important;
		border: 1px solid #33405b !important;
		color: #009ab3
	}/* Modifie la couleur du bouton rechercher */

.bloc-rech-forum .btn-lancer-rech:hover 
	{
		background: none repeat scroll 0 0 #009ab3  !important;
		border: 1px solid #009ab3 !important;
	  	color: #33405b !important;
	}/* Modifie la couleur du bouton rechercher au passage de la souris */

.bloc-liste-num-page > span > span 
	{
		color: #009ab3 !important;
	}/* Modifie la couleur du résultat de la recherche sur un forum */

.bloc-header-form 
	{
		background: url("http://image.noelshack.com/fichiers/2015/44/1446110210-ico-titre.png") no-repeat scroll left 2px transparent !important;
		color: #009ab3  !important;
	}/* Modifie la couleur de Nouveau sujet */

.form-post-topic .form-control 
	{
		border-color: #009ab3  !important;
	}/* Modifie la couleur de la case "Saisir le titre du sujet" */

.btn-ajouter-sondage 
	{
		background: none repeat scroll 0 0 #33405b !important;
		border: 1px solid #33405b  !important;
	}/* Modifie la couleur du bouton ajouter un sondage */

.btn-ajouter-sondage:hover 
	{
		background: none repeat scroll 0 0 #009ab3  !important;
		border: 1px solid #009ab3  !important;
	}/* Modifie la couleur du bouton ajouter un sondage au passage de la souris */

input[type=text], select, .btn-ajouter-sondage {
  	background-color: #33405b !important;
  	color: #009ab3 !important;
  	border: 1px solid #33405b !important;
} /* Fonce les cases rechercher dans le forum, saisir le titre du sujet et le bouton sujet/auteur */

/* EN COURS */

.bloc-pagi-default .pagi-debut-actif  
	{
		background: url("http://image.noelshack.com/fichiers/2015/44/1446114133-navbar.png") no-repeat scroll 0 0 transparent !important;
	}/* Modifie la couleur du bouton revenir à la première page */

.bloc-pagi-default .pagi-precedent-actif  
	{
		background: url("http://image.noelshack.com/fichiers/2015/44/1446114133-navbar.png") no-repeat scroll -27px 0 transparent !important;
	}/* Modifie la couleur du bouton revenir à la page précédente */

.bloc-pagi-default .pagi-suivant-actif  
	{
		background: url("http://image.noelshack.com/fichiers/2015/44/1446114133-navbar.png") no-repeat scroll -54px 0 transparent !important;
	}/* Modifie la couleur du bouton aller à la page suivante */

.bloc-pagi-default .pagi-fin-actif  
	{
		background: url("http://image.noelshack.com/fichiers/2015/44/1446114133-navbar.png") no-repeat scroll -81px 0 transparent !important;
	}/* Modifie la couleur du bouton aller à la dernière page */

.btn-poster-msg 
	{
		background: none repeat scroll 0 0 #009ad3  !important;
		color: #33405b  !important;
	}/* Modifie la couleur du bouton poster */


.titre-head-bloc-forum 
	{
	border-top: 2px solid #009ad3  !important;
	}/* Modifie la couleur de la bordure en haut de la page dans les topic */


.btn-poster-msg:hover 
	{
		background: none repeat scroll 0 0 #33405b  !important;
		color: #009ab3  !important;
	}/* Modifie la couleur du bouton poster au passage de la souris */

.titre-head-bloc-forum 
	{
	border-top: 2px solid #009ad3  !important;
	}/* Modifie la couleur de la bordure en haut de la page dans les topic */


/* Bloc RÉPONDRE */

.jv-editor .conteneur-editor 
	{
    background-color: #33405b;
    border: 1px solid #33405b;
	}/* Modifie la couleur du bloc répondre */

.jv-editor .btn-group 
	{
    background-color: #122136;
    border: 1px solid #122136;
	}/* Modifie la barre des options du post (gras, italique etc) */

.jv-editor .btn-jv-editor-toolbar 
	{
    color: #009ad3;
	}/* Modifie la couleur des icones des options du post */

.jv-editor .btn-jv-editor-toolbar:hover 
	{
	color: #3fd7fa;
	}/* Modifie la couleur des icones des options du post au passage de la souris */

.jv-editor .btn-jv-editor-previsu 
	{
    background-color: #009ad3;
    border: 1px solid #009ad3;
    color: #009ad3
	}/* Modifie la couleur de "prévisu" off */

.jv-editor .btn-jv-editor-previsu.active 
	{
    background-color: #009ad3;
    border: 1px solid #009ad3;
    color: #000000
	}/* Modifie la couleur de "prévisu" on */

.jv-editor .area-editor 
	{
    background-color: #33405b;
    color: #009ad3;
	}/* Modifie la zone de texte */

.jv-editor .text-editor 
	{
    background-color: #33405b;
  	border-top: 1px solid #009ad3;
	}/* Modifie la couleur du bas de la zone de texte */

.jv-editor .previsu-editor 
	{
  	color: #009ad3;
    border: 1px solid #33405b;
	}/* Modifie la couleur de la bordure de la prévisu */

.jv-editor .label-previsu 
	{
    color: #009ad3;
	}/* Modifie la couleur de "prévisu" */

.titre-head-bloc-forum .titre-bloc-forum span:first-child 
	{
	color: #009ad3  !important;
	}/* Modifie la couleur de "Sujet" à côté du titre du topic */




.titre-bloc-forum span 
	{
    color: #009ad3 !important;
	}/* Modifie la couleur du titre du sujet */

.bloc-message-forum .bloc-date-msg a 
	{
	color: #009ad3 !important;
	font-size: 11px  !important;   
	font-weight: 500  !important;
	}/* Modifie la couleur et la taille de la date et l'heure des messages */

.bloc-message-forum .bloc-date-msg a:hover 
	{
	color: #909090  !important;
	}/* Modifie la couleur et la taille de la date et l'heure des messages au passage de la souris */



/* Navigation dans les topics */


.bloc-liste-num-page a 
	{
	color: #009ad3  !important;
	}/* Modifie la couleur du numéro de page */

.bloc-liste-num-page a:hover 
	{
	color: #3fd7fa  !important;
	}/* Modifie la couleur du numéro de page au passage de la souris */

.bloc-liste-num-page a:visited 
	{
	color: #c5c5c5 !important;
	}/* Modifie la couleur du numéro de page déjà visité */

.bloc-liste-num-page a:hover:visited 
	{
	color: #9c9c9c !important;
	}/* Modifie la couleur du numéro de page déjà visité au passage de la souris */

.bloc-liste-num-page .page-active 
	{
	background: none repeat scroll 0 0 #009ad3  !important;
	color: #33405b
	}/* Modifie la couleur du numéro de la page active */

.titre-head-bloc .titre-bloc 
	{
		color: #009ad3  !important;
	}/* Modifie la couleur du titre du forum et ajoute le logo NFS à côté du titre */
