css:
@-moz-document regexp("^https\\:\\/\\/progtest\\.fit\\.cvut\\.cz\\/index\\.php\\?.*X\\=Task.*") {
td#countdown {
    display: block;
    position: absolute;
    top: 50%;
    left: 0;
    right: 0;
    text-align: center;
    background: inherit;
    border: none;
    color: #FFF;
    font-size: 110px;
    font-family: monospace;
    height: 130px;
    margin: -65px 0 0 0;
    cursor: none;
}

td {
    display: none;
}

table {
    border: 0!important;
}

body .outBox {
    border: none;
}

body {
    background: #000;
    height: 100%;
}

html {
    height: 100%;
    cursor: none;
}

center div.topLayout:nth-of-type(1) .outBox table td.tCell {
    display: block;
    padding: 0;
    border: none!important;
    cursor: none;
}

center div.topLayout:nth-of-type(1) .outBox table td.tCell b {
    display: none;
}

center div.topLayout:nth-of-type(1) .outBox table td.tCell:after {
    content: "DEADLINE";
    color: #f00;
    font-size: 66px;
    position: absolute;
    top: 50%;
    left: 0;
    right: 0;
    text-align: center;
    margin: -120px 0 0 0;
    font-family: monospace;
    font-weight: bold;
}

body > table:first-of-type td {
    display: block;
    background: inherit;
	cursor: auto;
}

body > table:first-of-type td span.navLink a.navLink {
    display: none;
}

body > table:first-of-type td span.navLink a.navLink:nth-of-type(2) {
    display: block;
    width: 0px;
    overflow: hidden;
}

body > table:first-of-type td span.navLink a.navLink:nth-of-type(2):after {
	content: "⬑BACK";
    position: absolute;
    top: 0;
    margin: 30px;
    font-family: monospace;
    font-size: 26px;
    color: #A74141;
}
}
