css:
@-moz-document domain("habitica.com"), domain("habitrpg.com") {

/*------------------------------ SITE HEADER (GEMS & COINS) ------------------------------ */
  /* as we reduce the height of the toolbar, the icons are now too big, so we reduce them & adjust their position */
  /* each icon comes from a gigantic img, of which only the relevant part is displayed */
  /* we redim each icon at 2/3rd of its initial ratio, by resizing the whole img then updating the size & pos accordingly */
  
    /* gem */
    .Pet_Currency_Gem2x {
      background-image: url("http://i.imgur.com/ktKGeHt.png");
      background-size: 66px 20px;
      background-position: -2px -2px; /* default: -1655px -1066px */
      width: 21px;   /* default: 30px */
      height: 19px;  /* default: 26px */
      margin-top: 5px !important; /* to center the gem properly */
    }
  
    /* gold coin */
    .shop_gold {
      background-image: url("http://i.imgur.com/ktKGeHt.png");
      background-size: 66px 20px;
      background-position: -26px -4px;
      width: 18px;   /* default: 32px */
      height: 14px;  /* default: 22px */
      margin-top: 4px !important;   /* to center the coin properly */
      margin-right: 3px !important; /* to center the coin properly */
    }

  	/* we align the reduced gold coin with the cost */
    .rewards .btn-buy span.shop_gold {
      margin-top: 3px !important;
    }
  
    .shop_silver {
      background-image: url("http://i.imgur.com/ktKGeHt.png");
      background-size: 66px 20px;
      background-position: -46px -4px;
      width: 18px;   /* default: 32px */
      height: 14px;  /* default: 22px */
      margin-top: 4px !important;   /* to center the coin properly */
      margin-right: 3px !important; /* to center the coin properly */
    }
  
  
  /*------------------------------ SHOW/HIDE ITEMS ------------------------------ */
  
    /* hide footer */
    .footer { display: none !important; }
    
    /* hide tasks 'move to top' icon */
    .glyphicon-open { display: none !important; }
    
    /* hide tasks 'task notes' icon */
    .task-notes { display: none !important; }
  
    /* hide task filters */
  	
    
    
    /* hide tags */
 	
    
/* we hide the 'score' legend of all graphs */
    svg > g:nth-of-type(2) { display: none !important; }
  
/* we hide the help menu question mark */
    .toolbar-nav .toolbar-button-dropdown.highlight > a span.glyphicon {
       display: none !important;
                                        }
    
    
  
  /*------------------------------ SITE HEADER (MENUS & AVATAR) ------------------------------ */
  
    /* toolbar fix for firefox users using the vertical scrolling bar on the left of the screen */
    .toolbar {
      right: unset !important;
      border-bottom: 1px solid #9ea9ab;
    }
    #main .col-md-12 {
      padding-left: 0;
    }
    
    /* we add the logo (original height="61" width="240") */
    .toolbar-container::before {
      float: left;
      content: "";
      background-color: #C0D4F7 !important;
      background: url('http://vignette4.wikia.nocookie.net/habitrpg/images/8/89/Wiki-wordmark.png/revision/latest?cb=20150731082810') no-repeat;
      background-size: auto 100%;
      background-position: 5px;
      width: 125px;
      height: 27px;
      padding: 0 3em 0 0.3em !important;
    }
  
    /* we reduce the height of the toolbar */
    .toolbar-nav, .toolbar-bailey, .toolbar-options, .toolbar-subscribe, .toolbar-wallet {
      padding: 0;
    }
    
    .toolbar-currency {
      margin-bottom: 0;
    }
      
    /* we remove the buttons border */
    .toolbar-nav .toolbar-button > a, .toolbar-nav .toolbar-button-dropdown {
      border-width: 0 1px 0 0 !important;
    }
  
     .toolbar-options > li > a {
      border-width: 0 !important;
    }
  
    .toolbar-subscribe-button > button {
      border-width: 0 0 0 1px !important;
    }
    
    /* we remove the space between buttons */
    .toolbar-options > li:not(:first-of-type) {
      margin-left: 0;
    }
    .toolbar-nav > li {
      margin-right: 0;
    }
  
    /* the wrapper height has to reflect the toolbar height */
    .header-wrap {
      padding-top: 28px;
      border-bottom: 1px solid #C3D1D6;
    }
    
    /* we remove the background of the avatar level & name */
    .herobox .avatar-level, .herobox .avatar-name {
      border: 0;
      outline: 0;
      background: none !important;
    }
    
    /* we update the background color of the avatar on hover */
    .herobox.noBackgroundImage:hover {
      background-color: #C9DAF8;
    }
   
    /* what follows impacts only the submenus */
  
    /* we remove the light border before the sandwitch icon */
    .toolbar-nav .toolbar-button-dropdown > a:nth-of-type(2) {
      border-left: none !important;
    }
    
    /* we hide the triangle on the top of each submenu */
    .toolbar-audio > div h4::before, .toolbar-audio > div > div:first-child::before, .toolbar-audio > div > ul:first-child::before, .toolbar-mobile > div h4::before, .toolbar-mobile > div > div:first-child::before,
    .toolbar-mobile > div > ul:first-child::before, .toolbar-nav .toolbar-button-dropdown > div h4::before, .toolbar-nav .toolbar-button-dropdown > div > div:first-child::before, .toolbar-nav .toolbar-button-dropdown > div > ul:first-child::before,
    .toolbar-notifs > div h4::before, .toolbar-notifs > div > div:first-child::before, .toolbar-notifs > div > ul:first-child::before, .toolbar-settings > div h4::before, .toolbar-settings > div > div:first-child::before,
    .toolbar-settings > div > ul:first-child::before {
      display: none !important;
    }
    
    /* the triangle being removed, we unset the top so the submenu appears just below its sandwitch button, without gaps in between. the margin trick allows a perfect alignment with the navbar borders */
    .toolbar-audio > div, .toolbar-mobile > div, .toolbar-nav .toolbar-button-dropdown > div, .toolbar-notifs > div, .toolbar-settings > div {
      top: unset !important;
      border-color: #edf2f2;
      margin-left: -1px;
      margin-right: -1px;
    }
  
    /* we reduce the padding for a more condensed look */
    .toolbar-audio > div ul, .toolbar-mobile > div ul, .toolbar-nav .toolbar-button-dropdown > div ul, .toolbar-notifs > div ul, .toolbar-settings > div ul {
      padding: 0;
    }
     .toolbar-audio > div ul a, .toolbar-mobile > div ul a, .toolbar-nav .toolbar-button-dropdown > div ul a, .toolbar-notifs > div ul a, .toolbar-settings > div ul a {
      padding: 0.5em 1.2em 0.5em 0.5em; /* default: 0.618em 1em */
    }

 .toolbar-nav .toolbar-button-dropdown {
      border-color: #C0D4F7 !important;
    }
      
    .toolbar, .toolbar-nav .toolbar-button-dropdown.highlight textarea, .toolbar-nav .toolbar-button-dropdown.highlight > a, .toolbar-nav .toolbar-button-dropdown.highlight > button, .toolbar-nav .toolbar-button-dropdown.highlight > input , .toolbar-nav .toolbar-button textarea, .toolbar-nav .toolbar-button > a, .toolbar-nav .toolbar-button > button, .toolbar-nav .toolbar-button > input, .toolbar-nav .toolbar-button-dropdown textarea,    .toolbar-nav .toolbar-button-dropdown > a, .toolbar-nav .toolbar-button-dropdown > button, .toolbar-nav .toolbar-button-dropdown > input, .toolbar-audio textarea, .toolbar-audio > a, .toolbar-audio > button, .toolbar-audio > input, .toolbar-settings textarea, .toolbar-settings > a, .toolbar-settings > button, .toolbar-settings > input, .toolbar-sync textarea, .toolbar-sync > a, .toolbar-sync > button, .toolbar-sync > input, .toolbar-notifs textarea, .toolbar-notifs > a, .toolbar-notifs > button, .toolbar-notifs > input {
      background-color: #c0d4f7 !important;
      color: #000000 !important; 
    }
    
    .toolbar-notifs .glyphicon, .toolbar-audio .glyphicon, .toolbar-settings .glyphicon, .toolbar-sync .glyphicon {
      color: #edf2f2 !important;
    }

    
    /* we make the active menu more visible */
    .toolbar-nav .toolbar-button-dropdown > a.active{
      background-color: #EDF2F2 !important;
    } 
    .toolbar-nav .toolbar-button-dropdown.highlight > a.active {
      background-color: #C0D4F7 !important;
    }
    
    /* we make the different options of the submenu more visible */
    .toolbar-audio > div ul a:hover, .toolbar-mobile > div ul a:hover, .toolbar-nav .toolbar-button-dropdown > div ul a:hover, .toolbar-notifs > div ul a:hover, .toolbar-settings > div ul a:hover {
      background: #EDF2F2;
      text-decoration: none !important;
    }
    .toolbar-nav .toolbar-button-dropdown.highlight > div ul a:hover {
      background: #EDF2F2;
      text-decoration: none !important;
    }
    
    /* we hide the ugly big 'settings' h4 in the settings */
    /*.toolbar-settings > div > h4 { display: none !important; }*/
    
  
    /* see http://benkaminski.com/2014/08/30/how-to-customize-bootstrap-3-popovers-individually-using-only-css/ for more details about bootstrap popover styling */
    .popover {
      border-radius: 0;
      border-color: #edf2f2;
      -webkit-box-shadow: none !important;
              box-shadow: none !important;
    }
    
    /* we remove the top arrow then remove the top margin so the popover appears like the other submenus */
    .popover.bottom > .arrow, .popover.bottom > .arrow:after { display: none !important; }

    .popover.bottom{
      margin-left: -30px !important; /* blob to display it completely now that the button is fully to the right */
      margin-top: 0; 
    }
      
  
  /*------------------------------ SITE HEADER (STAT BARS) ------------------------------ */
  /* we remove the gap between symbol & bar then tweak the colors & round the edges */
  /* we also replace the grey background by a light blue one */
  
    .header-wrap{
        background: url('http://sel.shizuyue.net/img/mine/rpg-bg.png'); /*backup, if this breaks: http://i.imgur.com/O1APgco.jpg */
        background-size: cover !important;
	}
    .herobox {
        background-color: transparent !important;
    }
	.site-header .main-herobox {
        background: rgba(255, 255, 255, .5);
    }

    .site-header .main-herobox:hover {
        background: rgba(255, 255, 255, .7);
    }
    
    .site-header  div.hero-stats {
        background: rgba(207,219,222, .7);
    }
    
    /* coupled with the margin-bottom: 0 of the last bar (mana), we have a nicely centered stat bars area (works only after level 10 though, when the mana bar appears) */
    .hero-stats {
      padding-top: 0;
      border-right: none !important;
    }

    .hero-stats .meter-label {
      margin-right: 0; /* default: 0.618em*/  
      text-shadow: unset !important;
      border-top-left-radius: 1.3em !important;
      border-bottom-left-radius: 1.3em !important;
      outline: 0;
      height: 2.31em;
      width: 3em;
      padding: 0.5em 0 0.5em 0.4em;
    }
    
    .hero-stats .meter {
      outline: 0;
      height: 1.8em; /* default: 1.618em; */
      margin-bottom: 0.6em;
      border-top-right-radius: 1.3em !important;
      border-bottom-right-radius: 1.3em !important;
    }
  
    .hero-stats .meter-text.value {
      right: 0.75em;
    }
    
    .hero-stats .meter-label[tooltip='Health'], .hero-stats .meter-label[tooltip='Boss Health'] {
      background: #6F1818 !important;
      border-color: #6F1818 !important;
      outline-color: #6F1818 !important;
    }
  
    .hero-stats .meter.health {
      border-color: #6F1818 !important;
    }
  
    .hero-stats .meter-label[tooltip='Experience'] {
      background: #8A6700 !important;
      border-color: #8A6700 !important;
      outline-color: #8A6700 !important;
    }
  
    .hero-stats .meter.experience {
      border-color: #8A6700 !important;
    }
  
    .hero-stats .meter-label[tooltip='Mana'] {
      background: #0F3577 !important;
      border-color: #0F3577 !important;
      outline-color: #0F3577 !important;
    }
  
    .hero-stats .meter.mana {
      border-color: #0F3577 !important;
      margin-bottom: 0;
    }
 
  	/* we put a white background to the general graph located in the xp bar */
  	.col-md-12.exp-chart {
      background: #FFFFFF !important;
      border-bottom: 1px solid #C3D1D6;
      margin-bottom: -30px;
    }
  
   /* we resize the graph so everything is displayed properly and reposition it so it shows below the menus */  
   .col-md-12.exp-chart > div > div:first-of-type { height: 300px !important; }
   .col-md-12.exp-chart > div > div:first-of-type > div:first-of-type { top: 15px !important; }
   .col-md-12.exp-chart > div > div:first-of-type > div:first-of-type > svg { height: 280px !important; width: 350px !important; }
            
  
  /*------------------------------ TASKS FILTER TAGS ------------------------------ */
    
    /* we remove the gap between the header and the filter tags (note: we cannot unset the height because if we do so, the scrolling will snap to the sticky wrapper and yield unwanted effects */
    .sticky-wrapper {
      height: calc(147px + 28px + 1px) !important; /* height of the avatar picture + height of the navbar */
    }
       
  /* we remove padding & margin from the filters class in order to have a smaller tag area, where tags are as high as their container */
    .filters {
      padding: 0;
      margin: 0;
    }
  
    /* we improve the centering of tag labels */
    .filters .filters-controls li:first-of-type, .filters .filters-controls li > a, .filters .filters-tags > a {
      padding-top: 0.3em !important; /* default  0.25em */
    }
    
    /* 30px is the default height of the search box, so we set it up for all other fields for a clean look */
    .filters .filters-controls, .filters .filters-edit, .filters .filters-tags, .filters .filters-search {     
      height: 30px;
      margin-top: -1px;
      margin-right: -1px;
      margin-bottom: 0;
    }  
  
    /*  we remove the tag margins to have a more condensed look (tags are now touching each other) */
    .filters > ul, .filters .hrpg-input-group {
      margin-right: -1px;
    }
    
    /* we add a border during edit mode */
    .filters .hrpg-input-group textarea, .filters .hrpg-input-group > a, .filters .hrpg-input-group > button, .filters .hrpg-input-group > input {
      height: 30px;
    }  
  
    /* we remove the border between the tag & the delete button (tag edit mode) */
    .filters .hrpg-input-group > input {
      border-right: 0;
    }
       
  

  /*------------------------------ TASKS COLUMNS LAYOUT ------------------------------ */
    
    .tasks-lists {
      margin-top: -1px;
      border-top: 1px solid #C3D1D6;
    }
      
    .tasks-lists .row .col-md-3 {
      padding: 0;
      margin: 0.618em 0;
    }
    
    /* we use the -1px trick instead of keeping only the left (or right) border in order to have a nice border going all the way down the lists of tasks */
    .task-column {
      padding: 0.5em 1.5em;
      background: none !important;
      border-width: 0 1px;
      margin-left: -1px; /* in order to have a perfectly centered border (overlapping) */
    }
    
    /* we remove the left border of habits & right border of reward */
    .rewards { border-right: 0; }
    .habits { border-left: 0; }
    
    /* we hide the always empty items rewards ul of all columns except for rewards */
    .task-column:not(.rewards) .items.rewards, .task-column.rewards .items.rewards:not(.ng-scope) {
      display: none !important;
    }
    
    /* we resize the tasks columns only when the media is a desktop (md in bootstrap starts at 992px), in order not to mess with the responsiveness */
    @media screen and (min-width: 992px) {
      .col-md-3:not(.rewards-module) {
        width: 27%;
      }
      .col-md-3.rewards-module {
        width: 19%;
      }
    }
    
    /* we update the title margin for a more compact look */
    .task-column .task-column_title {
      margin: 0;
    }
    
    /* we tweak the option box, which are the icons on the right of the column title */
    .option-box {
      margin: 0.75em 0.1em 0 0;
    }
    
    /* we bring the add box closer to the title */
    .task-add {
      margin-top: 1em;  /* default: 1.5em */
    }
      
    /* we bring the task filter closer to the add box */
    .task-filter {
      margin-top: 0;    /* default 1.5 em */
      margin-bottom: 0; /* default: 1em; we use .main-list instead */
    }
  
    /* using main list margin, so it still looks good when filters are not displayed */
    .main-list {
      margin-top: 1em;
    }
  
  
  
  /*------------------------------ TASKS COLUMNS - HEADERS ------------------------------ */
  
    /* default for input bars is 2.618em, so we use that to square the '+' button */
    .task-add input, .task-add textarea { width: calc(100% - 2.618em); }
    .task-add button { width: 2.618em; }
    
    /* we adjust the button padding so it looks properly centered */
    .task-add button {
      padding-top: 0.45em !important;
    }
    
    /* we bring the 'add multipl' button closer to the input bar */
    .help-block.btn-link {
      margin-top: 3px; /* default: 5px */
    }
    
  
  
  /*------------------------------ TASKS COLUMNS - TASKS ------------------------------ */
  /* we reduce the size of the colored band of each task, then update text margins accordingly */
    
    /* we bring tasks closer from one another */
    .task {
      margin-bottom: 4px;  /* default: 0.75em */
    }
  
    /* we use shadow to create a seamless border */
    .task, .task-options {
      border: none !important;
      box-shadow: inset 0 0 0 1px rgba(0,0,0,0.13) !important;
    }
  
    /* the reward column min height is left untouched because otherwise we would have to resize all images which is a real pain */
    .task-column:not(.rewards) .task { 
      min-height: 2.5em !important;  /*default: 3em */
    }
    
    /* we reduce the width of the lateral band of each task, to match the modified min-height */
    .habits .task-actions, .task-checker label {
      width: 2.5em; /*default: 3em */
    }
    
    /* we update the checkbox position */
    .action-yesno label::after { margin: 13px auto; }

    /* we resize the dailies & todos success check to match the resized box */
    .action-yesno label::after, .action-yesno label::before {
      font-size: 1.38em;
    }
    
    /* we */
    .task-column:not(.rewards) .task-checklist {
      margin-right: 0;
    }
  
    /* we update the text position, except for rewards which are left untouched */
    .task-column:not(.rewards) .task-text {
      padding: 0.5em 0.7em 0.5em 3.2em; /* default: 0.75em 0px 0.75em 3.75em */
    }
    
    /* we update the controls position, except for rewards which are left untouched (note that they appear only for custom rewards) */
    .task-column:not(.rewards) .task-meta-controls {
      margin: 0.55em 0.5em 0px;
    }
    
    /* we reduce the space between rewards & spells */
    .rewards.main-list, .items.rewards.ng-scope {
      padding-bottom: 0;
      border-bottom: 0;
    }

    /* we slim the rewards buttons to match the resized gold coin */
    .btn-buy {
      width: 4em;
      padding: 0.8em 0px 0px 0.5em; /* default: 0.75em 0px 0px 0.5em */
    }
    
    /* we bring the rewards text closer to the left, as the buttons are now smaller */
    .rewards .task-text {
      padding-left: 4.75em; /* default: 5em */
    }
    .rewards .reward-img {
      margin-left: 4.5em; /* default: 5em */
    }
  
    /* we adjust the rewards text positioning */
    .rewards .btn-buy span {
      left: 3px;
    }
    
    /* we adjust the text positioning for the spells */
    .rewards .btn-buy span:only-child {
      padding-left: 0.4em; /* default: 0.75em */
    }
  
  
    /*------------------------------ TASKS COLUMNS - TASKS DETAILS ------------------------------ */
  
    /* we let the last child breathe a little */
    .checkbox:last-child {
      margin-bottom: 2px /* default: 0 */
    }
    
    /* the following is only when editing tasks */
    
    /* we remove the specific styling of hints titles */
    .hint { 
      color: unset !important;
      border-bottom: 0 !important; 
    }
  
    /* we add the '?' glyphicon after all the title elements having a hint/explanations */
    .hint::after {
      font-family: "Glyphicons Halflings" !important;
      font-size: 0.65em;
      content: " \e123";
      color: #6e6e6e;
    }  
  
    /* we remove the hr of dailies, which separate the different advanced options. the gap will be managed with margins */
    hr { display: none !important; }
  
    /* we align all titles to the left */
    [class$="-options"] .option-title {
      text-align: left;
    }
    
    /* we reduce the margins */
    [class$="-options"] .option-group, .form-group {
      margin-bottom: 0.5em; /* default: 1em */
      margin-top: 0.5em;    /* default: 1em */
    }
        
    /* we increase the margin-top of the advanced area, to be more consistent with the other zones */
    [class$="-options"] button.advanced-options-toggle {
      margin-top: 1em;
      margin-bottom: 1em;
    }
    
    /* we remove the margin bottom of the datepicker, to be more consistent with the other zones */
    .option-group .option-content.datepicker {
      margin-bottom: 0; /* default: 1em */
    }
    
    /* we increase the margin-bottom of the advanced options area, to have a save button evenly spaced vertically */
    .option-group.advanced-option {
      margin-bottom: 1em;
    }
  
    /* we remove some extra white space */
    .task-options .repeat-days {
      padding-bottom: 0;
    }
    .task-options .priority-multiplier li, .task-options .repeat-days li, .task-options .repeat-frequency li, .task-options .task-attributes li {
      margin-bottom: 0;
    }
  
    /* we bring sub-tasks closer together */
    .task-checklist-edit > .checklist-form li {
      margin-bottom: 0.35em;  /* default: 1em */
    }
  
    /* we reduce further the margin of the last element so the gap with the next area is not so big */
    .task-checklist-edit > .checklist-form li:last-of-type {
      margin-bottom: 0.25em;
    }
  
    /* we move the checkboxes glyphicons closer to the left edge, so that it is aligned with the text areas below the checklist */
    .task-checklist-edit > .checklist-form .checklist-icon {
      padding-left: 0;
    }
    
    /* we increase the checkbox text area width: 100% minus twice the width of the glyphicon minus the with of the delete button (twice the width so the list appears more symetrical) */
    .task-checklist-edit > .checklist-form input {
      width: calc(100% - 2*1.5em - (2em + 2px));
    }
    
    /* we resize the graphs and adjust their position so they are displayed properly */
    .task > div:first-of-type > div:first-of-type > div:first-of-type, .task > div:first-of-type > div:first-of-type > div:first-of-type svg {
      height: 280px !important;
      width: 320px !important;
    }
    
    .task > div:first-of-type > div:first-of-type > div:first-of-type > div:first-of-type {
      top: -15px !important;
      left: 10px !important;
    }
   
  .task-column.rewards .reward-item,
    .task-column:not(.rewards) .color-best:not(.completed),
    .task-column:not(.rewards) .color-better:not(.completed),
    .task-column:not(.rewards) .color-good:not(.completed),
    .task-column:not(.rewards) .color-neutral:not(.completed),
    .task-column:not(.rewards) .color-bad:not(.completed),
    .task-column:not(.rewards) .color-worse:not(.completed),
    .task-column:not(.rewards) .color-worst:not(.completed) {
      background-color: #fff !important;
      /*background-color: #f7f8f9 !important;*/
      /*background-color: grey !important;*/
    }

  
    /* we update the border color of all task forms, so they are not just a dull grey - we use the colors of the checklists */
    
    /* best */
    .task-column:not(.rewards) .color-best:not(.completed) .form-control, .task-column:not(.rewards) .color-best:not(.completed) .option-content { border-color: #78A2ED !important; color: #0F3271 !important; }
    .task-column:not(.rewards) .color-best:not(.completed) .form-control:hover, .task-column:not(.rewards) .color-best:not(.completed) .option-content:hover { border-color: #3A77E4 !important; }
    .task-column:not(.rewards) .color-best:not(.completed) .form-control:focus, .task-column:not(.rewards) .color-best:not(.completed) .option-content:focus { border-color: #13408F !important; }
    
    /* better */
