css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("polycount.com") {

/*fixes for actual posts */
  
blockquote {
    font-size: 14.5px;
}  
  
.Message {
    font-size: 14px;
    font-weight: 400;
}  

.MessageList .Item-Header, .DataList-Search .Item-Header {
    padding: 15px 20px;
}    
  
.MessageList .Item + .Item {
    margin-top: 5px;
}  

.Item-Aside {
    padding-left: 2px;
}

.Item-Footer {
    padding: 7px 15px;
}   
    
.Item-Aside ul {
    margin: 0px 0px 12px;
}  

.Item .Author-Card {
    width: 60%;
}   
    
.PagerWrap .PagerWrap, .PageControls .PagerWrap, .BeforeCommentHeading .PagerWrap, .PagerWrap .NumberedPager, .PageControls .NumberedPager, .BeforeCommentHeading .NumberedPager {
    float: right !important;
    margin-right: 9.75%;
}      

.banner {
    padding-top: 50px;
}

.banner-image {
    height: 250px;
}

.adInfo {
    height: 0px;
}
  
/*fixes for forum view */ 
.DataTable > tbody > tr > td {
    padding: 10px;
    border-top: 3px solid #1E1E1E;
}  

.DataTable h3 {
    display: block;
    font-family: "Montserrat",sans-serif;
    font-size: 14px;
    font-weight: 400;
    line-height: 1;
    color: #298041;
}    

.pagination > li > a, .pagination > li > span, .NumberedPager > a, .NumberedPager > span, .NumberedPager > div {
    padding: 8px 15px;   
}
    
.PagerWrap, .PageControls, .BeforeCommentHeading {
    margin: 5px 0px;
}

h1.H, .HomepageTitle {
    padding-bottom: 0px;
}    
    
}
