css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("livejournal.com")

{

  h1
    {padding: 20px 0 0 20px !important;
    color: #114577 !important;}

  #profile_page
    {font-family: Verdana !important;
    margin: 0 20px 0 20px !important;}

  #profile_page a
    {color: #0000cc !important;}

  #profile_page a:visited
    {color: #670391 !important;}

  #profile_top
    {padding-top: 10px !important;}

  .userpicdiv
    {float: right !important;
    padding-top: 15px !important;}

  .user_pic_caption
    {display: none !important;}

  .username
    {border-bottom: 0 !important;
    background-color: #ffffff !important;}

  .actions ul
    {padding-top: 10px !important;}

  .actions li
    {padding: 0 !important;
    list-style: none;
    border-bottom: 0 !important;
    background-color: #ffffff !important;}

  .actions a
    {font-size: 0 !important;}

  .actions img
    {padding-top: 5px !important;}

  .actions span
    {display: inline !important;}

  .profile_join_disabled, .profile_postentry_disabled, .profile_sendvgift_disabled, .profile_sendmessage_disabled, .profile_watch_disabled, .profile_addfeed_disabled, .profile_addfriend_disabled, .profile_trackuser_disabled
    {font-size: 0 !important;}

  .user_details
    {width: 85% !important;}

  .user_details_inner
    {margin: 0 !important;}

  .details_journal .journal_title
    {font-size: 1.3em !important;
    padding-bottom: 4px !important;}

  .details_journal .journal_subtitle
    {font-size: 1.1em !important;
    padding-bottom: 4px !important;}

  .details_stats p
    {font-size: 0.8em !important;
    color: #000000 !important;}

  .details_stats .account_level
    {padding-top: 5px !important;}

  .details_stats .journal_adult_warning, .details_stats .statusvis_msg
    {padding-bottom: 5px !important;}

  .details_links
    {padding: 5px 0 5px 0 !important;}

  .details_links p
    {font-size: 0.85em !important;}

  .section
    {border-bottom: 0 !important;
    background-color: #ffffff !important;
    color: #114577 !important;}

  .section_body
    {padding: 0 70px 15px 70px !important;}

  .section_body .inner_section_header
    {color: #114577 !important;}

  .inner_section_body a
    {text-decoration: underline !important;}

}
