css:
@-moz-document domain("www.trictrac.net") {
/* Pubs */

.flags + div.row,
.flags,
.flag {
    display: none;
}
body.withAd {
    background: #DDD!important;
}
.flex-obj {
    display: none;
}
.flex-obj>iframe {
    display: none;
}
/* Taille */

body.withAd .contain-to-grid,
body .top-bar,
.row,
body.withAd #content,
body.withAd .asRowWithAd {
    max-width: 1500px !important;
}
/* Textes */

.breadcrumbs>* {
    font-size: 14px;
}
.breadcrumbs>* a {
    color: #000;
}
/* Forum */

.forums header {
    padding: 1em 0 0 0;
}
.forums header.asRowWithAd {
    padding: 1em 0;
}
.forums header .columns.text-right {
    top: 40px;
    position: absolute;
}
.forums header p {
    margin-right: 100px;
}
.forums .posts {
    background: none;
    padding: 0;
}
.forums .content {
    background: none;
}
.forums.topic header div.subheader,
.forums.forum header div.subheader {
    font-size: 14px;
}
ul.pagination li {
    font-size: 14px;
}
a,
.forums .posts .list ul li.post .footer .message-actions a {
    color: #000;
}
.forums .posts .list ul li.post .footer .message-actions a i {
    margin-left: 20px;
    top: 4px;
}
.side-nav li {
    font-size: 14px;
}
.forum .clearfix {
    padding: 10px 0 !important;
}
#content.forums {
    background: #EEE!important;
}
.forums .posts .list ul li.post .message {
    background: #FFF;
}
.cke-content p,
.cke-content-forum p {
    border-bottom: 1px solid #DDD;
    font-weight: bold;
}
ul.pagination li a,
ul.pagination li button {
    color: #000;
}
.forums .list ul .head {
    background: none;
}
.forums .topics .list ul li.topic,
.forums .topics .list ul li.forum ul li.forum,
.forums .forums .list ul li.topic,
.forums .forums .list ul li.forum ul li.forum,
.forums .topics .list ul li.topic:hover,
.forums .topics .list ul li.forum ul li.forum:hover,
.forums .forums .list ul li.topic:hover,
.forums .forums .list ul li.forum ul li.forum:hover {
    background: none;
    border-bottom: none;
}
/* Navigation */

.top-decoration {
    position: fixed;
    width: 100%;
    z-index: 1000;
}
.top-bar {
    position: fixed;
    top: 0;
    width: 100%;
}
#container {
    margin-top: 39px;
}
header .action-bar {
    right:0;
    width:220px;
}
.action-bar {
    background: none;
    position: absolute;
    top: 0;
}
.action-bar.fixed {
    background: none;
    margin-top: 39px;
    padding-bottom: 0;
}
.action-bar.fixed .row {
    background: #FFF;
    border-bottom: 1px solid #DDD;
    padding-top: 10px;
}
.action-bar.fixed .row h3 {
    padding-top: 15px;
}
.sub-nav dt,
.sub-nav dd,
.sub-nav li {
    font-size: 16px;
}
/* Divers */

* {
    outline: none !important;
}
#notifications {
    border: none !important;
    top: 43px !important;
}
button,
.button {
    font-size: 18px!important;
}
header .slider .cover,
#container .news .box .image {
    background-position: center top;
}
header .slider .cover .row {
    margin: 0 !important;
}
#shortcut {
    bottom: 1%;
}
}
