css:
@-moz-document url-prefix('https://ya.ru/') {
	
body {cursor: url('http://i63.tinypic.com/1z545yv.jpg'),default !important;} 
	 
	.input_size_xl {background: transparent!important; background-color: transparent!important; border: 0!important; box-shadow: none!important;}
	
	.input_size_xl::before {
    border-width: 0;
	background: transparent; box-shadow: none!important;}
	
	.popup__getposoffix {top: -100px!important;}
	
	::-moz-selection{background: #E9F58A}
	
	:link,.button_theme_websearch {cursor: url('http://i68.tinypic.com/168cx94.jpg'),pointer !important;}

 .b-page {background: transparent url("http://i62.tinypic.com/nxngpt.jpg") no-repeat scroll  center;background-size: 100%}
	
	.link_black_novisit, .link_black_novisit:link, .link_black_novisit:visited {padding: 0px 0px;}
	
	.b-sethome__link:link, .b-sethome__link:visited {color: #FED940!important;;
text-shadow: 1px 0 0 #FED940,0 1px 2px #B3034B, 0 -1px 2px #B3034B, 0 2px 2px #FF8375,0 -2px 2px #FF8375, 0 3px 2px #FF4A73,0 -3px 2px #FF4A73;font-family: Segoe print; transform:perspective(100px) rotatey(25deg) !important;; position:  absolute; margin-left: -95% !important;transition: .3s; }
	
	.b-sethome__link:before {content: ''; background: url('http://i57.tinypic.com/ddz12e.jpg') no-repeat; height: 100px!important; width: 100px!important;; position: absolute; background-size: 80%; margin-left: 20px!important; margin-top: 17px!important;}
	
	.b-sethome__link:hover:before {background: url('http://i59.tinypic.com/2zp1b86.jpg')no-repeat;background-size: 80%}
	
	.b-sethome__link:link:hover, .b-sethome__link:visited:hover {color: #FED940!important;;
text-shadow: 1px 0 0 #FED940,0 1px 2px #B3034B, 0 -1px 2px #B3034B, 0 2px 2px #FF8375,0 -2px 2px #FF8375, 0 3px 2px #FF4A73,0 -3px 2px #FF4A73, 0 -4px 4px #FFD4B7, 0 4px 4px #FFD4B7, 0 5px 3px #E6FFD5, 0 -5px 3px #E6FFD5; }
	
.personal__exit {color: rgb(79, 236, 215)!important;;
text-shadow: 1px 0px 0px white, 2px 0px 4px rgb(108, 217, 198), -2px 0px 4px rgb(108, 217, 198), 0px -7px 4px rgb(39, 139, 130), 0px 7px 4px rgb(50, 105, 112);
font-family: Segoe print;transform:perspective(100px) rotatey(-35deg) !important;; position:  absolute; margin-left: -73px;margin-top: 138px} 
	
	.personal__exit:before { content: ''; background: url('http://i60.tinypic.com/2l94gex.jpg') no-repeat; height: 100px!important; width: 100px!important;; position: absolute; background-size: 80%; margin-left: -29px!important; margin-top: 17px!important; }
	
	.personal__exit:hover:before {background: url('http://i61.tinypic.com/2uggxw9.jpg')no-repeat;background-size: 80%;}
	
	.personal__exit:hover, .personal__exit:link:hover {color: rgb(79, 236, 215)!important;;
text-shadow: 1px 0px 0px white, 2px 0px 4px rgb(108, 217, 198), -2px 0px 4px rgb(108, 217, 198), 0px -7px 4px rgb(39, 139, 130), 0px 7px 4px rgb(50, 105, 112);
font-family: Segoe print;}
	
	.personal__mail {color: #53EFFF!important; text-shadow: 1px 0 0 #6AB6D0, 0 1px 2px #316491,0 -1px 2px #316491, 0 2px 2px #87A5E1,0 -2px 2px #87A5E1, 0 3px 1px #86A3DD, 0 -3px 1px #86A3DD, 0 4px 2px #53EFFF, 0 -4px 2px #53EFFF!important;;font-family: Segoe print;transform:perspective(100px) rotatey(-25deg) !important;; position:  absolute; margin-left: -73px; margin-top: 35px}
	
	.personal__mail:before {content: ''; background: url('http://i62.tinypic.com/e6yq00.jpg') no-repeat; height: 100px!important; width: 100px!important;; position: absolute; background-size: 80%; margin-left: -15px!important; margin-top: 17px!important;}
	
	.personal__mail:hover:before {background: url('http://i59.tinypic.com/2n1ub6o.jpg')no-repeat;background-size: 80%}
	
	.personal__mail:hover,.personal__mail:visited:hover { color: #53EFFF!important; text-shadow: 1px 0 0 #6AB6D0, 0 1px 2px #316491,0 -1px 2px #316491, 0 2px 2px #87A5E1,0 -2px 2px #87A5E1, 0 3px 1px #86A3DD, 0 -3px 1px #86A3DD, 0 4px 2px #53EFFF, 0 -4px 2px #53EFFF!important;;font-family: Segoe print;}
  
  .personal__name {color: white; text-shadow: 1px 0 0 white, 0 -1px 3px #DAAFDD,0 1px 3px #DAAFDD, 0 2px 3px #C965E1,0 -2px 3px #C965E1,0 -3px 3px #AB1ECF, 0 3px 3px #AB1ECF; }
	
	 .personal__name:hover,  .personal__name:visited:hover {color: white; text-shadow: 1px 0 0 white, 0 -1px 3px #DAAFDD,0 1px 3px #DAAFDD, 0 2px 3px #C965E1,0 -2px 3px #C965E1,0 -3px 3px #AB1ECF, 0 3px 3px #AB1ECF, 0 4px 3px #9C328A, 0 -4px 3px #9C328A;}
	
  .input_size_xl .input__control{ 
		
		background: transparent;
		
		text-shadow:0 -2px 1px#F2F7FA,0 -3px 0 #2B1F37,0 -2px 2px #DCD9C8, -1px 0 1px #6C5C69,0px 9px 4px rgba(0,0,0,.2), -3px 12px 4px rgba(194,194,194,0.5), 0 3px 3px #A8268A,0 -3px 3px #4E6FF3; 
		font-weight: bold; 
		font-size: 23pt; 
		
		height: 60px!important; 
		
		position: relative;
		margin-top: 95px; 
		
		cursor: url('http://i67.tinypic.com/b84aol.jpg'),text !important;}
	
	.input_size_xl .input__box {cursor: url('http://i63.tinypic.com/1z545yv.jpg'),default !important; }
	
	.input_theme_websearch .input__box {background: transparent;margin-bottom: -5px!important;; }
	
.input_size_xl .input__ahead-filler {background: transparent;}
	
	
	/* Меню вариантов поиска */
	
.suggest2.popup {
	
	background:   -moz-linear-gradient(top, rgba(164,244,215,1) 0%, rgba(239,244,186,0.5) 78%, rgba(255,255,255,0.7) 120%)!important; 
	
	margin-top: -3px; 
	
	font-family: Segoe Script; 
	font-style: italic;  
	text-shadow: 0px -4px 3px #563278, 0 -2px 4px #994CC0, 0 2px 4px #2B48E4, 0 4px 3px #342F57;  
	text-align: center; 
	
	transform:perspective(300px) rotateX(30deg); 
	
	box-shadow: 0 0px 14px   -7px rgba(87,242,236,.7),0 4px 14px  -2px rgba(239,244,186,0.8), inset 0 3px 1px #6FA299 !important; 
	
	padding-bottom: 2px;
	width: 670px!important;	
	
	cursor: url('http://i63.tinypic.com/dwdxyr.jpg'),pointer !important;
	
-moz-animation-duration: .2s; -moz-animation-name: slide; -moz-animation-iteration-count:  1; -moz-animation-direction:  alternate;  }
  

   
  @-moz-keyframes slide {
  0% {
   
   height: 0; transform:perspective(300px) rotateX(35deg); margin-top: 15px; 
  }
    
  50% {
   
   height: 50%;transform:perspective(300px) rotateX(35deg); margin-top: 15px; 
  }
    
   100% {
   
    height: 100%;transform:perspective(300px) rotateX(30deg); margin-top: -3px; 
   }}
	
.suggest2-item {cursor: url('http://i63.tinypic.com/dwdxyr.jpg'),default!important;}
	
.suggest2-item:hover {background:   -moz-linear-gradient(top, rgba(246,255,130,1) 0%, rgba(255,255,255,.2) 100%); }
	
	.suggest2-item__text {color: white!important;}
	
	.input_size_xl .input__control {color: #3B2740; }
  
  .input_size_xl {border: 0; margin-bottom: 20px;box-shadow: 0 0 4px white;line-height: 0px; }
  
	
	/* Строка (Dock) поиска */
	
  .suggest2-form__node {background: url(http://i65.tinypic.com/2zyy0aw.jpg) 50% 100% repeat-x;
		position: relative;
		display: inline-block;
		list-style-type: none; color: #AFC0BA!important;;
		padding: 0px 20px;
		margin: 0px auto; width: 800px; margin-top: 150px;}
  
  .suggest2-form__node:before {content: '';
			background: url(http://i66.tinypic.com/2i73g3o.jpg) 50% 100% no-repeat;
			position: absolute;
			top: 0px;
			bottom: 0px; 
			left: -29px;
			width: 29px;}
  
   .suggest2-form__node:after {
			content: '';
			background: url(http://i63.tinypic.com/20qkj9t.jpg) 50% 100% no-repeat;
			position: absolute;
			top: 0px;
			bottom: 0px;
			right: -29px;
			width: 29px;
		}
	
	
	.tooltip {

	  border-bottom: 1px dotted #000000;

	  color: #000000; outline: none;

	  cursor: help; text-decoration: none;

	  position: relative;

	}
	


.layout__footer-logo{background: #FFF url("http://i63.tinypic.com/25aqcrk.jpg") no-repeat -5px 16px;
overflow: hidden; 
background-size: 100%;
background-color: transparent!important;;
width: 400px;
height: 140px; 
transform: perspective(101px) rotatex(20deg) scale(0.65); 
margin-bottom: -20px
 }
	
	.layout__footer-logo:hover {background: url('http://i67.tinypic.com/15wktxf.jpg') no-repeat -5px 18.5px !important; background-size: 100%!important;}
	
	.layout__footer-logo:before {display: none!important;}
	
	/* Кнопка "Найти" */
	
	.button_theme_websearch.button_pressed_yes .button__text,.button_theme_websearch .button__text {border: 0;background: transparent; border-color: transparent; height: 170px; width: 100px; color: transparent; margin-top: 0px!important;  color: transparent;transition: 0.2s}

	.button_theme_websearch:hover .button__text {color: white!important;;text-shadow: 1px 0 0 white, 0 -1px 3px #61EFFF, 0 1px 3px #67F1FF, 0 -2px 3px #3E96A0,0 -3px 3px #088FC4;font-size: 12pt!important; font-family: tahoma!important; transition: 0.3s;background: transparent}

	.button_theme_websearch .button__text:before {
	
	position:absolute;
		
	margin-top: 50px; 
	margin-right: -20px!important;;
	width:150px;
	height:150px;
	content:'';
	border-style:none;
	border-color:transparent;
		
	background: url('http://i65.tinypic.com/1ywmzm.jpg')no-repeat; background-size: 90%}
	
	
	.input_size_xl .input__clear,.input__clear {opacity: 1!important;; background: url('http://i63.tinypic.com/2cxdtp4.jpg') no-repeat center; background-size: 80%; height: 40px; margin-top: 117px; cursor: url('http://i67.tinypic.com/33kgphl.jpg'),default !important;  }
	
	.input__clear:active {background: url('http://i68.tinypic.com/102mbv8.jpg') no-repeat center; background-size: 80%!important;margin-top: 117px;cursor: url('http://i65.tinypic.com/15s36z4.jpg'),default !important; }

	.input__clear_visibility_visible {opacity: 1!important;}
	
	
	/* Новое Письмо */
	
	.personal__count {
    font-size: 11px;
    color: red !important;
    text-shadow: 1px 0 0 red, 0 -1px 3px #A50008, 0 1px 33px #A50008, 0 3px 3px #A83E43, 0 -3px 3px #A83E43;
    box-shadow: 5px 0 4px -2px #FFDC6B;
    border-radius: 50px;
}

}
