css:
@-moz-document domain("salesforce.com"), domain("force.com") {
/* ////////////////////////////////////////////////////////

  S A L E S F O R C E     C L E A N     R E D E S I G N
  
  by Sébastien Lopez - Twitter @slintl

  Note: shoud work on a vanilla 'pre-winter16' version

//////////////////////////////////////////////////////// */

/* Uses the font Source Sans Pro you can get here: https://github.com/adobe-fonts/source-sans-pro and install in c:\windows\fonts */
  
/* Main links color change */
body { font-family: "Source Sans Pro", Calibri, Helvetica Neue, Helvetica, Arial, sans-serif !important; } 
body a{ text-decoration:none; color: #019cdb; }
body a:hover{ color: #0888BD; text-decoration:none;}

/* Body: increase readability by limiting width, clear background */
#contentWrapper{ max-width: 1100px; margin: auto;}
body.sfdcBody { background: none; }

/* Global header modification */
#globalHeaderBar { 
border-bottom-right-radius: 5px;
border-bottom-left-radius: 5px;
margin:auto;
margin: 0 -3px 15px -3px;
background: white;
}
.globalHeaderCommunityMenuContainer, .globalHeaderVerticalLineInner, .globalHeaderVerticalLineOuter { border-right: 0px !important; }
.globalHeaderNameMenuContainer, .globalHeaderVerticalLineInner, .globalHeaderVerticalLineOuter { border-left: 0px !important; }
#globalHeaderNameMink:hover { color: #019cdb; }
#globalHeaderNameMink .globalHeaderProfilePhoto img{ display:none; }

/* Header : logo, search bar */
#phHeader{ margin-top: 5px; } 
.phHeaderCustomLogo #tsidButton { background-color: #019cdb;}
#tsidButton, #helpNavButton, #notificationsNavButton, #userNavButton { background: #019cdb; }

/* Main menu : centered, flat background */
#AppBodyHeader { font-family: "Source Sans Pro", Calibri, Helvetica Neue, Helvetica, Arial, sans-serif !important; font-size: 13px; }
#AppBodyHeader nav ul li { padding: 0 13px 0 13px !important; }
#tabContainer{ border-top-color: #019cdb !important; background: #FFF !important; }
.brdPalette {border-top-color: #019cdb !important; }
#tabContainer li a:hover{ color: #019cdb !important; }
#tabContainer li.zen-active{
border-top-left-radius: 0px !important;
border-top-right-radius: 0px !important;
background-image: none !important;
background: /*#636363*/ #019cdb !important;
}
#tabContainer li.zen-active a:hover{ color: #fff !important; }
nav ul#tabBar { text-align:center; }

/* Spécifique SLO */
li#Chatter_Tab, li#File_Tab, li#Product2_Tab{ display: none;}


/* Sidebar: do not display -> not so useful */
#sidebarCell{ display: none; }


/* Body Borders : blue & yellow for opportunities */
#ilsBanner{ border-color: #019cdb !important; background: #ECF6FF !important;}

.accountTab .listViewport .subNav .linkBar, .accountTab .mComponent .cHeader, .accountTab .genericTable, .accountTab .bSubBlock, .accountTab .bPageBlock
{border-top: 3px solid #019cdb}

.contactTab .tertiaryPalette, .individualPalette .contactBlock .tertiaryPalette, .layoutEdit .individualPalette .contactBlock .tertiaryPalette {border-color: #019cdb}

.contactTab .listViewport .subNav .linkBar, .contactTab .mComponent .cHeader, .contactTab .genericTable, .contactTab .bSubBlock, .contactTab .bPageBlock {border-top: 3px solid #019cdb}

.contactTab .secondaryPalette, .individualPalette .contactBlock .secondaryPalette {border-color: #019cdb}

.opportunityTab .listViewport .subNav .linkBar, .opportunityTab .mComponent .cHeader, .opportunityTab .genericTable, .opportunityTab .bSubBlock, .opportunityTab .bPageBlock {border-top: 3px solid #F7B10F}
 
.opportunityTab .secondaryPalette, .individualPalette .opportunityBlock .secondaryPalette {border-color: #F7B10F}
 
.leadTab .listViewport .subNav .linkBar, .leadTab .mComponent .cHeader, .leadTab .genericTable, .leadTab .bSubBlock, .leadTab .bPageBlock {border-top: 3px solid #019cdb}

.leadTab .secondaryPalette, .individualPalette .leadBlock .secondaryPalette {border-color: #019cdb}

/* Page titles */
h1.pageType { color: #019cdb !important; line-height: 1.5em !important; text-transform: uppercase;}
h2.pageDescription{ font-weight: bold !important; }

/* Increase readability of main info (contacts, accounts, opps) */
div.listHoverLinks { display:none; }
div.pbSubsection:nth-child(2) table.detailList{ background: #F1F1F1 ; padding: 8px; border: 1px solid #EAEAEA;}
div.pbSubsection:nth-child(2) table.detailList td{ 
    border-bottom: 1px solid /*#ccc*/ #EAEAEA; 
	border-collapse: collapse; 
}
div.pbSubsection:nth-child(2) table.detailList tr:last-child td{ border-bottom:none !important; }

/* Divers */
#summary{ font-family: "Source Sans Pro", Calibri, Helvetica Neue, Helvetica, Arial, sans-serif !important; }

/*reports */
tr.odd, tr.even { font-family: "Source Sans Pro", Calibri, Helvetica Neue, Helvetica, Arial, sans-serif !important; font-size: 0.9em; }

}
