css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("www.bing.com") {

  body {

    overflow-x: hidden !important;

  }

      /*search*/

          #sb_form_q { /*search Input*/

            background-color: rgba(255,255,255, 0.9) !important;

            margin: 0 !important;

            transition: background-color 0.25s !important;;

            padding: 3.5px 5px !important;

            max-width: calc(100% - 40px) !important;

            text-align: left !important;

          }

          #sb_form_q[value=""] {

            background-color: rgba(255,255,255, 0.25) !important;

          }

          #sb_form_q:focus { /*search Input*/

            background-color: rgba(255,255,255, 0.9) !important;

          }



          #sa_ghostbox {

            margin: 0 !important;

            display: none !important;

          }





          #sb_form_go {

            margin: 0 !important;

          }



          #sw_as {

            top: -1px !important;

            left: 1px !important;

            max-width: 100% !important;

            /*width: 523px !important;*/

          }

          .sa_s {

            text-align: left !important;

          }



          .b_searchboxForm { /*search bar*/ 

            background-color: transparent !important;

            border-color: transparent !important;

            padding: 0 !important;

            max-width: 100% !important;

          }

          .sa_as {

            background-color: rgba(255,255,255, 0.9) !important;

            border-color: transparent !important;

          }



          .b_searchboxForm {

            border-color: transparent !important;

          }

          .sa_sg {

            margin: 0 !important;

          }

          #sbox {

            margin: 26px 0 !important;

          }

          #b_searchboxForm {

            margin: 0 !important;

            padding: 0 !important;

            border-width: 0 !important;

          }

  

  

    /*remove_bottom*/

          #musCardContent {

            background-image: none;

            background-color: rgba(0, 0, 0, 0.55) !important;

          }

          #sb_foot {

            background-image: none;

            background-color: rgba(0, 0, 0, 0.7) !important;

          }

          #sw_footL a, #sw_footL span{

            color: rgba(255,255,255,0.5) !important;

          }

          /*#sb_foot {

            height: 5px;

            transition: height 0.25s;

          }

          #sb_foot:hover {

            height: 30px;

          }*/

          #hp_bottomCell {

            bottom: -38px;

            transition: bottom 0.5s cubic-bezier(0.165, 0.84, 0.44, 1);



          }

          #hp_bottomCell:hover {

            bottom: 0;

          }

    /*Menu*/

          #sc_hdu > li {

            /*background-color: #333;

            padding: 5px 0;*/

            border-radius: 5px;

            margin: 0px;

          }

          #hp_sw_hdr {

            position: fixed;

            left: 0;

            min-width: 1000px !important;



            background-color: rgba(0,0,0,0.5);

          }

          #sb_foot {

            background-color: rgba(0,0,0,0.8);

          }



          .sw_pref {

            border-width: 0px;

            position: relative;

            top: -5px;

          }

    /*Animation*/

        .sa_as {

          display: block !important;

          height: 0;

          transition: height 0.25s, opacity 0.25s;

          opacity: 0 !important;

        }

        .sa_as[style="display: block;"] {

          height: 283px;

          opacity: 1 !important;

        }



    /*Fixing Bing Size*/

        * {

          min-width: 0 !important;

        }



        body {

          min-width: 100% !important;

        }

}









@-moz-document url-prefix("http://www.bing.com/search"), url-prefix("http://www.bing.com/images/search"), url-prefix("http://www.bing.com/videos/search"), url-prefix("http://www.bing.com/news/search"), url-prefix("http://www.bing.com/explore") {

    /*orange header*/

          #b_header {

            /*background-color: #ffd054 !important;*/ /*rgba(255, 185, 0, 0.67)*/

            /*background-color: rgba(0, 0, 0, 0.1) !important;*/

          }

          .b_scopebar, #sb_form {

              background-color: transparent !important;

          }

          /*#b_header .b_scopebar a {

            border-color: rgba(255,255,255,0.25) !important;

          }*/

          #sb_form_q, #sb_form_q[value=""], #sb_form_q:focus {

            /*background-color: rgba(0,0,0, 0.1) !important;*/

            border: 1px solid #ccc !important;

            border-width: 1px 0 1px 1px !important;

          }

  

  

    /*fixing search button*/

          #sb_form_go {

            height: 37px !important;

            width: 37px !important;

            border: 5px solid #FFB900 !important;

            background-color: #FFB900 !important;

            background-position: -120px 3px !important;

            vertical-align: top !important;

            margin: 0px 0px 3px !important;

          }



          #b_header .b_scopebar .b_active a {

            border-top-color: #F84E29 !important;;

          }

          /*#id_sc {

            border-color: #ffd054 !important;

          }*/

    /*fixing Minibar on searchresult (scroll down and then a bit up)*/

        #miniheader .b_scopebar {

          background-color: #111 !important;;

      }

}
