css:
@-moz-document domain("forums.digitalspy.co.uk") {
/* === GENERAL STUFF === */

header.main {
    color: #000 !important;
    background-color: #eeeeee !important;
}

div.main {
    color: #000 !important;
    background-color: #eeeeee !important;
}

/* === RESTORE ALL THE ORIGINAL (READABLE!) FONTS === */

body {
    font-family:horseferry,sans-serif !important;
	font-size:13px !important;
	line-height:1.25em !important;
	margin:0 !important;
	padding:0 !important;
}

.para{font-family:horseferry,sans-serif !important;}

.para.semi, .button.generic, label, header.main .container nav.main ul li a, #sub_nav .sub_menu_content.active .container ul.sub_menu_channels li a, #sub_nav .sub_menu_content.active .container .channel_content ul.channel_posts li
.component {font-family:horseferry,sans-serif !important;}

.para.bold, .content_area header.gen_head .bold.icon.info, .content_area .list.small .component .content .headline, .content_area .list .component .info .heading, .module.sg_round .content .featured .content .bold.strapline, .module.sg_round .content ul.list li, .module.inline .component .content .bold.category, .module.fw.vert .content .bold.strapline, .module.qw .components .component .content.large .bold.strapline, .module.list div.content ul li a span.content .bold.strapline, .module.list div.content ul li a span.content .bold.date, #fb_comments #comments li
.bold.comment{font-family:horseferry,sans-serif !important;}

.heading.small{	font-family:horseferry,sans-serif !important;}
.heading.medium, article.main header.gen_head h3, #overlay .search .relative_container input[type="text"]{	font-family:horseferry,sans-serif !important;}
.heading.large{	font-family:horseferry,sans-serif !important;}
.heading.large.light{font-family:horseferry,sans-serif !important;}
strong{	font-family:horseferry,sans-serif !important;}
#sub_nav .sub_menu_content.active .container .channel_content .channel_header h2 {font-family:horseferry,sans-serif !important;}
#breadcrumb_trail p, #breadcrumb_trail p a {font-family:horseferry,sans-serif !important;}
footer.main .container .module {background:#282828 !important;}
footer.main .container .module #footer_links ul li a {font-family:horseferry,sans-serif !important;}
footer.main .container .module #about_links .component .title {	font-family:horseferry,sans-serif !important;}
footer.main .container .module #about_links ul.component ul a, footer.main .container .module #about_links ul.component ul li a {font-family:horseferry,sans-serif !important;}
#overlay .search .relative_container .close {font-family:horseferry,sans-serif !important;}
.forum_head a{font-family:horseferry,sans-serif !important;}
.forum_head a:hover {font-family:horseferry,sans-serif !important;}
.forum_head ul.forum_nav {font-family:horseferry,sans-serif !important;}
.forum_head ul.forum_nav li {font-family:horseferry,sans-serif !important;}
.forum_head ul.forum_nav li:last-child {font-family:horseferry,sans-serif !important;}
.forum_head ul.forum_nav li a {font-family:horseferry,sans-serif !important;}
.forum_head ul.forum_nav li a {font-family:horseferry,sans-serif !important;}
.boximportant h2 {font-family:horseferry,sans-serif !important;}

.boldlink {
	font-family:horseferry,sans-serif !important;
    font-weight: bold !important;
}

.boldlink.navytext {font-weight: bold !important;}
a.boldlink {font-weight: normal !important;}
.thread_header h1 {	font-family:horseferry,sans-serif !important;}

.button {
	font-family: horseferry,sans-serif !important;
	background-color: #670e71 !important;
	color: #ffffff !important;
	font-weight: bold !important;
    padding-top: 2px !important;
    padding-right: 2px !important;
    padding-bottom: 2px !important;
    padding-left: 2px !important;
}

.vbmenu_control a:link,.vbmenu_control_alink {font-family: horseferry,sans-serif !important;}
.bigusername {font-family: horseferry,sans-serif !important;}

/* --- Set the default font colours --- */

.page a:link {color: #1515AA !important;}
.page a:visited {color: #1515AA !important;}
.page a:hover, .bluetext {color: #1515AA !important;}
.vbmenu_control {
    color:#FFFFFF !important;
    background: #334489 !important;
}
.vbmenu_control a:link,.vbmenu_control_alink {color:#FFFFFF !important;}
.vbmenu_control a:visited,.vbmenu_control_avisited {color:#FFFFFF !important;}
.vbmenu_control a:hover,.vbmenu_control a:active,.vbmenu_control_ahover {color:#FFFFFF !important;}

.alt1 .bluetext {
    color: #000000 !important;
    font-weight: normal !important;
}

.bllink a:link,.bllink a:visited,.bllink a:hover{
	color:#020727 !important;
}

/* === PAGE HEADER AREA === */

#adposition1_container {
    background-color: #334 !important;
    height: 5px !important;
    padding: 0 !important;
}

#breadcrumb_bar {
    position: absolute !important;
    top: 75px !important;
    display: table !important;
}

#breadcrumb_trail p {color: #000 !important;}
#breadcrumb_trail .mb {color: #448 !important;}

/* Space above "DS Forums" */
table.forum_head > TBODY > TR:first-child > TD:first-child > TABLE:first-child > TBODY > TR:first-child {
    visibility: collapse !important;
}

/* "DS Forums" */
table.forum_head > TBODY > TR:first-child > TD:first-child > TABLE:first-child > TBODY > TR:nth-child(2) .large.light.heading {
    color: #666 !important;
}

.nav_buttons {
    color: #5583ed !important;
    background-color: #eff0f7 !important;
    display: table !important;
}

.forum_nav {padding: 4px !important;}
.forum_nav li a {padding: 4px  !important;}

.forum_nav li a:hover {
    color: #eff0f7 !important;
    background-color: #5583ed !important;
}
 
th.login_box {
    color: #fff !important;
    background-color: #3b4787 !important;
    padding-top: 4px !important;
    padding-bottom: 4px !important;
}

td.login_box {
    color: #000 !important;
    background-color: #f0f0f0 !important;
    padding-top: 4px !important;
    padding-bottom: 4px !important;
}

/* === PAGE NAVIGATION BAR FOR LISTS === */

/* Remove padding above/below page navigation bar */
#inlinemodform > table:first-of-type [valign=bottom] > td {padding: 0 !important;}

.pagenav td {padding: 1px 4px !important;} 
.pagnavthread {padding: 2px 0px !important;}

.pagenav .bluetext {
    background-color: #e3e6f7 !important;
    color: #000000 !important;
}

.pagenav .bllink {background-color: #f5f6fb !important;}

/* === GENERAL LISTS (FORUMS, THREADS EWTC.) === */

.thead, .posthead {
    font-family:horseferry,sans-serif !important;
    font-weight: bold !important;
    font-size: 13px !important;
    color: #ffffff !important;
    background: #5566ab !important;
    padding: 4px !important;
    border: none !important;
}
.thead a:link,.thead_alink {color: #ffffff !important;}
.thead a:visited,.thead_avisited {color: #ffffff !important;}
.thead a:hover,.thead a:active,.thead_ahover {color: #ffffff !important;}
.posthead a:link,.posthead_alink {color: #ffffff !important;}
.posthead a:visited,.posthead_avisited {color: #ffffff !important;}
.posthead a:hover,.posthead a:active,.posthead_ahover {color: #ffffff !important;}

.tcat {
    font-family:horseferry,sans-serif !important;
    font-weight: bold !important;   
    font-size: 13px !important;
    color: #fff !important;
    background: #334489 !important;
    padding: 4px !important;
    border: none !important;
}
.tcat a:link,.tcat_alink {color:#fff !important;}
.tcat a:visited,.tcat_avisited {color:#fff !important;}
/* .tcat a:hover,.tcat a:active,.tcat_ahover {color:#0a1240 !important;} */
        
.tborder {
    border:1px solid #0B198C !important;
    background-color: #e4e7f5 !important;
}

.tfoot {
    background-color: #670e71 !important;
    padding-top: 4px !important;
    padding-bottom: 4px !important;
    font-size:1em !important;
}    

.pagenav .pagenums {
    font-family:horseferry,sans-serif !important;
    font-weight: bold !important;
    font-size: 13px !important;
    color: #ffffff !important;
    background: #334489 !important;
    padding-right: 4px !important;
    border: none !important;
}

/* thread title */
#wrapper > DIV > DIV > DIV > DIV > DIV > TABLE > TBODY > TR:last-child > TD > H1.large.light.heading {
    color: #2b3464 !important;
    margin-top: 10px !important;
}

/* alt1 is used for general table columns, such as in forum and thread lists */
.alt1 {padding: 4px !important;}

.forumrow td {border-bottom:0 !important;}

/* contentcontain is the background of all content below thread title */
.contentcontain, table.pagnavthread {background-color: #fff !important;}
#wrapper div.main .container {background: #fff !important;}

/* thread head */
.pagnavthread + table.tborder td {
    color: #fff !important;
    background-color: #3b4787 !important;
}

/* The original style had alternating dark and light columns in lists */
/* The new one doesn't have enough unique identifiers to reproduce the effect entirely */
/* This is as near as we can get */
.alt1 {background:#f5f6fb !important;}
.forumrow > .alt1:nth-last-child(3) {background: #e3e6f7 !important;}
.forumrow > .alt1:last-child {background: #e3e6f7 !important;}

/* === POSTS PAGES === */

/* Restore the little gap between posts in the list */
#posts table {margin-bottom: 6px !important;}

/* --- Left column of posts --- */

.alt2 {background-color: #e3e6f7 !important;}
.tborder tr .alt2 {padding: 10px !important;}

.bigusername {
    color: #1515c4 !important;
    font-weight: bold !important;
    font-size: 1em !important;
}

/* member details in left column of posts */
div[id^="postmenu"]~div.smallfont {
    color: #000 !important;
    font-size: 13px !important;
}

div[id^="postmenu"]~div.smallfont~div.smallfont DIV:nth-child(2) {color: #000 !important;}
div[id^="postmenu"]~div.smallfont~div.smallfont DIV:nth-child(3) {color: #000 !important;}
div[id^="postmenu"]~div.smallfont~div.smallfont DIV:nth-last-child(2) {color: #000 !important;}

/* --- Replies --- */

/*  Reply editor box */
vBulletin_editor ,textarea {
    font-family:horseferry,sans-serif !important;
    font-weight: normal !important;
    font-size: 13px !important;
    color: #000000 !important;
    padding: 4px !important;
}

.quotebox {
    font-style: italic !important;
    color: #000000 !important;
    background-color: #e3e6f7 !important;
    border-style: solid !important;
    border-width: 1pt !important;
    border-color: #9baaf4 !important;
    padding: 5px !important;
    margin: 2px 16px 16px 16px !important;
}

/* quote "posted by" (The original was actually black, but as it's a link...) */
.quotebox a {
    color: #1515c4 !important;
    font-weight: bold !important;
}

/* Un-bold and indent the word "Quote:" above a quote box */
#posts td div div div.boldlink, #collapseobj_threadreview table tr td div div.boldlink {
    font-weight: normal !important;
    margin: 2px 16px !important;
}

/* --- Post contents (right-hand column) --- */

/* Override the default alt1 definition for post contents */
#posts .alt1 {
	background:#f5f6fb !important;
	color:#020727 !important;
    padding: 10px 10px 5px 15px !important;
}

/* --- Reply form --- */

DIV:last-child > TABLE > TBODY > TR > TD.theadp, FORM > TABLE TD.theadp {
    color: #fff !important;
    background-color: #3b4787 !important;
}

/* === RESTORE ORIGINAL ICONS AND BUTTONS === */

