/* === FAQ, SEARCH ETC. PAGES === */

.panelsurround {background-color: #d1d4e0 !important;}

.panel {
    background: #e4e7f5 !important;
    padding: 10px !important;
	border: 2px !important;
    border-style: outset !important;
}

.fieldset {
    background-color: #e4e7f5 !important;
    color: #000000 !important;
    border: 2px !important;
    border-style: outset !important;
}

legend{
	color:#348;
	font-family:horseferry,sans-serif;
	font-weight:700;
	letter-spacing:1px;
	font-variant:small-caps;
